static void view7() {
struct vi7 poems[] = {
	{1, "1 Khi vách thành đã xây xong, và tôi đã tra các cánh cửa, lập những người giữ cửa, kẻ ca hát, và người Lê-vi trong chức việc của họ,"},
	{2, "2 thì tôi giao phó việc cai trị Giê-ru-sa-lem cho Ha-na-ni, em tôi, và cho Ha-na-nia, quan tể cung; vì là một người trung tín và kính sợ Ðức Chúa Trời hơn nhiều kẻ khác."},
	{3, "3 Tôi dặn chúng rằng: Chớ mở các cửa Giê-ru-sa-lem trước khi mặt trời nắng nóng; người ta phải đóng cánh cửa, và gài then lại trong lúc kẻ giữ cửa còn ở tại đó; lại phải đặt người trong dân Giê-ru-sa-lem theo phiên mà canh giữ, mỗi người đứng canh nơi đối ngang nhà mình."},
	{4, "4 Vả, thành thì rộng rãi lớn lao, còn dân sự ở trong nó lại ít, và nhà mới thì chưa có cất."},
	{5, "5 Ðức Chúa Trời tôi có cảm động lòng tôi chiêu tập những người tước vị, các quan trưởng, và dân sự, đặng đem chúng vào sổ từng gia phổ. Tôi có tìm đặng một cuốn bộ gia phổ của những người đã trở lên lần đầu, và trong đó tôi thấy có ghi rằng:"},
	{6, "6 Này là các người tỉnh Giu-đa từ nơi làm phu tù được trở lên, tức những kẻ thuộc trong bọn bị Nê-bu-ca-nết-sa bắt dẫn từ; chúng trở lại Giê-ru-sa-lem và xứ Giu-đa, ai nấy đều về bổn thành mình;"},
	{7, "7 chúng trở về với Xô-rô-ba-bên, Giê-sua, Nê-hê-mi, A-xa-ria, Ra-a-mia, Na-ha-ma-ni, Mạc-đô-chê, Binh-san, Mích-bê-rết, Biết-vai, Bê-hum, và Ba-a-na. Nầy là số tu bộ những người nam của dân Y-sơ-ra-ên;"},
	{8, "8 Về con cháu Pha-rốt, hai ngàn một trăm bảy mươi hai người;"},
	{9, "9 về con cháu Sê-pha-tia, ba trăm bảy mươi hai người."},
	{10, "10 Về con cháu A-ra, sáu trăm năm mươi hai người."},
	{11, "11 Về co cháu Pha-hát-Mô-áp, con cháu của Giê-sua và Giô-áp, có hai ngàn tám trăm mười tám người."},
	{12, "12 Về co cháu Ê-lam, một ngàn hai trăm năm mươi bốn người."},
	{13, "13 Về con cháu Xát-tu, tám trăm bốn mươi lăm người."},
	{14, "14 Về con cháu Xác-cai, bảy trăm sáu mươi người."},
	{15, "15 Về con cháu Bin-nui, sáu trăm bốn mươi tám người."},
	{16, "16 Về con cháu Bê-bai, sáu trăm hai mươi tám người."},
	{17, "17 Về con cháu A-gát, hai ngàn ba trăm hai mươi hai người."},
	{18, "18 Về con cháu A-đô-ni-cam, sáu trăm sáu mươi bảy người."},
	{19, "19 Về con cháu Biết-vai, hai ngàn sáu mươi bảy người."},
	{20, "20 Về con cháu A-đin, sáu trăm năm mươi lăm người."},
	{21, "21 Con cháu A-te, về dòng dõi Ê-xê-chia, có chín mươi tám người."},
	{22, "22 Về con cháu Ha-sum, ba trăm hai mươi tám người."},
	{23, "23 Về con cháu Bết-sai, ba trăm hai mươi bốn người."},
	{24, "24 Về con cháu Ha-ríp, một trăm mười hai người."},
	{25, "25 Về con cháu Ga-ba-ôn, chín mươi lăm người."},
	{26, "26 Về những người Bết-lê-hem và Nê-tô-pha, một trăm tám mươi tám người."},
	{27, "27 Về những người A-na-tốt, một trăm hai mươi tám người."},
	{28, "28 Về những người Bết-a-ma-vết, bốn mươi hai người."},
	{29, "29 Về những người Ki-ri-át-Giê-ê-rốt, bảy trăm bốn mươi ba người."},
	{30, "30 Về những người Ra-ma và Ghê-ba, sáu trăm hai mươi mốt người."},
	{31, "31 Về những người Mích-ma, một trăm hai mươi hai người,"},
	{32, "32 Về những người Bê-tên và A-hi, một trăm hai mươi ba người."},
	{33, "33 Về những người Nê-bô khác, năm mươi hai người."},
	{34, "34 Về con cháu Ê-lam khác, một ngàn hai trăm năm mươi bốn người."},
	{35, "35 Về con cháu Ha-rim, ba trăm hai mươi người."},
	{36, "36 Về con cháu Giê-ri-cô, ba trăm bốn mươi lăm người."},
	{37, "37 Về con cháu của Lô-đơ, của Ha-sít, và của Ô-nô, bảy trăm hai mươi mốt người."},
	{38, "38 Về con cháu Sê-na, ba ngàn chín trăm ba mươi người."},
	{39, "39 Những thầy tế lễ: Về con cháu Giê-đa-gia, nhà Giê-sua, chín trăm bảy mươi ba người."},
	{40, "40 Về con cháu Y-mê, một ngàn năm mươi hai người."},
	{41, "41 Về con cháu Pha-ru-sơ, một ngàn hai trăm bốn mươi bảy người."},
	{42, "42 Về con cháu Ha-rim, một ngàn mười bảy người."},
	{43, "43 Người Lê-vi: Con cháu của Giê-sua và của Cát-mi-ên, về dòng dõi Hô-đê-va, bảy mươi bốn người."},
	{44, "44 Người ca hát: Về con cháu A-sáp, một trăm bốn mươi tám người."},
	{45, "45 Kẻ giữ cửa: Về con cháu Sa-lum, con cháu A-te, con cháu Tanh-môn, con cháu A-cúp, con cháu Ha-ti-la, và con cháu Sô-bai, một trăm ba mươi tám người."},
	{46, "46 Người Nê-thi-nim: Con cháu Xi-ha, con cháu Ha-su-pha, con cháu Ta-ba-ốt,"},
	{47, "47 con cháu Kê-rốt, con cháu Sia, con cháu Pha-đôn,"},
	{48, "48 con cháu Lê-ba-na, con cháu Ha-ga-ba, con cháu Sanh-mai,"},
	{49, "49 con cháu Ha-nan, con cháu Ghi-đên, con cháu Ga-ha;"},
	{50, "50 con cháu Rê-a-gia, con cháu Rê-xin, con cháu Nê-cô-đa,"},
	{51, "51 con cháu Ga-xam, con cháu U-xa, con cháu Pha-sê-a,"},
	{52, "52 con cháu Bê-sai, con cháu Mê -u-nim, con cháu Nê-phi-sê-nim,"},
	{53, "53 con cháu Bác-búc, con cháu Ha-cu-pha, con cháu Ha-rua,"},
	{54, "54 con cháu Bát-tít, con cháu Mê-hi-đa, con cháu Hạt-sa,"},
	{55, "55 con cháu Bạt-cô, con cháu Si-sê-ra, con cháu Tha-mác,"},
	{56, "56 con cháu Nê-xia, con cháu Ha-ti-pha."},
	{57, "57 Dòng dõi các đầy tớ của Sa-lô-môn: Con cháu Sô-tai, con cháu Sô-phê-rết, con cháu Phê-ri-đa,"},
	{58, "58 con cháu Gia-a-la, con cháu Ðạt-côn, con cháu Ghi-đên,"},
	{59, "59 con cháu Sê-pha-tia, con cháu Hát-tinh, con cháu Phô-kế-rết Hát-xê-ba-im, con cháu A-mon."},
	{60, "60 Tổng cộng các người Nê-thi-nim và con cháu các đầy tớ của Sa-lô-môn, là ba trăm chín mươi hai người."},
	{61, "61 Nầy là những người ở Tên-Mê-la, Tên-Hạt-sa, Kê-rúp, A-đôn, và từ Y-mê mà đi lên; nhưng chúng nó không thể chỉ ra nhà của tổ phụ mình và gốc gác của họ, hoặc thuộc về Y-sơ-ra-ên hay chăng:"},
	{62, "62 con cháu Ðê-la-gia, con cháu Tô-bi-gia, và con cháu Nê-cô-đa, đều sáu trăm bốn mươi hai người."},
	{63, "63 Về những thầy tế lễ: Con cháu Ha-ba-gia, con cháu Ha-cốt, con cháu Bạt-xi-lai, là người lấy vợ trong vòng con gái của Bạt-xi-lai, người Ga-la-át, và được gọi bằng tên của họ."},
	{64, "64 Các người ấy tìm kiếm gia phổ mình, nhưng không thấy, bèn bị trừ ra chức tế lễ, kể cho là ô uế."},
	{65, "65 Quan tổng trấn cấm chúng ăn những vật chí thánh cho đến chừng có một thầy tế lễ dấy lên dùng U-rim và Thu-nim mà cầu hỏi Ðức Chúa Trời."},
	{66, "66 Cả hội chúng hiệp lại được bốn vạn hai ngàn ba trăm sáu mươi người,"},
	{67, "67 không kể những tôi trai tớ gái của dân, số được bảy ngàn ba trăm ba mơi bảy người; cũng có hai trăm bốn mươi lăm kẻ hát, nam và nữ."},
	{68, "68 Chúng có bảy trăm ba mươi sáu con ngựa, hai trăm bốn mươi lăm con la,"},
	{69, "69 bốn trăm ba mươi lăm con lạc đà, và sáu ngàn bảy trăm hai mươi con lừa."},
	{70, "70 Có mấy trưởng tộc đem của cung tiến đặng giúp làm công việc. Quan tổng trấn dâng cho kho một ngàn đa-riếc vàng, năm mươi cái chậu, và năm trăm ba mươi cái áo thầy tế lễ."},
	{71, "71 Có mấy trưởng tộc dâng vào kho tạo tác hai vạn đa-riếc vàng, và hai ngàn hai trăm min bạc."},
	{72, "72 Còn vật dân sự khác dâng, được hai vạn đa-riếc vàng, hai ngàn min bạc, và sáu mươi bảy cái áo thầy tế lễ."},
	{73, "73 Ấy vậy, những thầy tế lễ, người Lê-vi, kẻ giữ cửa, kẻ ca hát, mấy người trong dân, và người Nê-thi-nim, cùng cả Y-sơ-ra-ên, đều ở trong các thành của mình. Khi tháng bảy đến, thì dân Y-sơ-ra-ên đều ở trong các thành của mình."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view8() {
struct vi8 poems[] = {
	{1, "1 Bấy giờ, dân sự đều nhóm hiệp như thể một người, tại phố ở trước cửa Nước. Chúng nói với E-xơ-ra là thầy thông giáo, xin người đem quyển sách luật pháp của Môi-se mà Ðức Giê-hô-va đã truyền dạy cho Y-sơ-ra-ên."},
	{2, "2 Ngày mồng một tháng bảy, thầy tế lễ E-xơ-ra đem luật pháp đến trước mặt hội chúng, người nam và nữ, cùng những người có thông sáng nghe hiểu được."},
	{3, "3 Người đứng tại phố ở trước cửa Nước, đọc trong quyển ấy từ rạng đông cho đến trưa, có mặt các người nam và nữ, cùng những kẻ có thể hiểu được. Cả dân sự lắng tai nghe đọc sách luật pháp."},
	{4, "4 Thầy thông giáo E-xơ-ra đứng trên một cái sạp bằng gỗ mà người ta làm nhơn dịp ấy; Ma-ti-thia, Sê-ma, A-na-gia, U-ri, Hinh-kia, và Ma-a-xê-gia, đứng gần người ở bên hữu; còn bên tả người thì có Phê-đa-gia, Mi-sa-ên, Manh-ki-gia, Ha-sum, Hách-ba-đa-na, Xa-cha-ri, và Mê-su-lam."},
	{5, "5 E-xơ-ra dở sách ra trước mặt cả dân sự, vì người đứng cao hơn chúng; khi người dở sách ra, thì dân sự đều đứng dậy."},
	{6, "6 E-xơ-ra ngợi khen Giê-hô-va Ðức Chúa Trời cao cả; cả dân sự bèn giơ tay lên, mà đáp lại rằng: A-men, A-men! rồi chúng cúi đầu, sấp mặt xuống đất mà thờ lạy Ðức Giê-hô-va."},
	{7, "7 Giê-sua, Ba-ni, Sê-rê-bia, Giô-xa-bát, Ha-nan, Bê-la-gia, và người Lê-vi, đều giải cho dân sự hiểu luật pháp; và dân sự đứng tại chỗ mình."},
	{8, "8 Họ đọc rõ ràng trong sách luật pháp của Ðức Chúa Trời, rồi giải nghĩa nó ra, làm cho người ta hiểu lời họ đọc."},
	{9, "9 Nê-hê-mi, quan tổng trấn, E-xơ-ra, thầy tế lễ và thầy thông giáo, cùng người Lê-vi mà dạy dỗ dân sự, bèn nói với cả Y-sơ-ra-ên rằng: Ngày nay là thánh cho Giê-hô-va Ðức Chúa Trời của các ngươi; chớ để tang, cũng đừng khóc lóc. Vì cả dân sự đều khóc khi nghe đọc các luật pháp."},
	{10, "10 Nê-hê-mi nói với chúng rằng: Hãy đi ăn vật gì béo, uống đồ gì ngọt, và hãy gởi phần cho những người không có sắm sửa gì hết; vì ngày nay là thánh, biệt riêng ra cho Chúa của chúng ta. Chớ buồn thảm, vì sự vui vẻ của Ðức Giê-hô-va là sức lực của các ngươi."},
	{11, "11 Vậy, người Lê-vi làm cho cả dân sự đều được yên ổn, mà rằng: Khá nín đi, vì ngày nay là thánh, đừng sầu thảm chi."},
	{12, "12 Cả dân sự bèn đi đặng ăn và uống, gởi cho những phần, cùng vui vẻ lắm; vì chúng có hiểu các lời người ta đã truyền dạy cho mình."},
	{13, "13 Qua ngày thứ hai, các trưởng tộc của cả dân sự, những thầy tế lễ, và người Lê-vi, đều nhóm lại bên E-xơ-ra, là người thông giáo, đặng chú ý nghe các lời của luật pháp."},
	{14, "14 Chúng thấy có chép trong luật pháp rằng Ðức Giê-hô-va cậy Môi-se phán rằng dân Y-sơ-ra-ên phải ở trong nhà lều đương lúc lễ tháng bảy;"},
	{15, "15 và chúng phải hô truyền trong các thành và tại Giê-ru-sa-lem nghe, rằng: Hãy đi kiếm trong núi những nhánh ô-li-ve và ô-li-ve rừng, những nhánh cây sim, những tầu lá kè, và những nhánh cây rậm, đặng làm những nhà lều, y như đã chép."},
	{16, "16 Dân sự bèn đi ra đem các lá ấy về, mỗi người đều làm nhà lều trên nóc nhà mình, ngoài sân mình, trong hành lang của đền Ðức Chúa Trời, nơi phố của cửa Nước, và nơi phố của cửa Ép-ra-im."},
	{17, "17 Cả hội chúng của những kẻ bị bắt làm phu tù được trở về bèn cất những nhà lều, và ở trong đó. Từ đời Giô-suê, con trai của Nun, cho đến ngày ấy, dân Y-sơ-ra-ên chẳng có làm gì giống như vậy. Bèn có sự rất vui mừng."},
	{18, "18 Mỗi ngày, từ ngày đầu cho đến ngày chót, người đọc trong sách luật pháp của Ðức Chúa Trời. Chúng ăn lễ bảy ngày; còn qua ngày thứ tám, có một lễ trọng thể, tùy theo luật lệ."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view9() {
struct vi9 poems[] = {
	{1, "1 Ngày hai mươi bốn tháng ấy, dân Y-sơ-ra-ên nhóm lại, cữ ăn, mặc bao và phủ bụi đất."},
	{2, "2 Dòng Y-sơ-ra-ên chia rẽ các người ngoại, đứng dậy xưng tội lỗi mình và sự gian ác của tổ phụ mình."},
	{3, "3 Chúng đứng dậy tại chỗ mình, đọc trong sách luật pháp của Giê-hô-va Ðức Chúa Trời mình, lâu đến phần tư ngày: trong một phần tư khác, chúng xưng tội và thờ lạy Giê-hô-va Ðức Chúa Trời của họ."},
	{4, "4 Bấy giờ, Giê-sua, Ba-ni, Cát-mi-ên, Sê-ba-nia, Bun-ni, Sê-rê-bia, Ba-ni, và Kê-na-ni, đứng dậy tại trên sạp người Lê-vi, và kêu cầu lớn tiếng cùng Giê-hô-va Ðức Chúa Trời mình."},
	{5, "5 Khi ấy những người Lê-vi, là Giê-sua, Cát-mi-ên, Ba-ni, Ha-sáp-nia, Sê-rê-bia, Hô-đia, Sê-ba-nia, và Phê-ta-hia nói rằng: Hãy đứng dậy, ngợi khen Giê-hô-va Ðức Chúa Trời của các ngươi, là Ðấng hằng có đời đời kiếp kiếp! Ðáng ngợi khen danh vinh hiển của Ngài, vẫn trổi cao hơn các sự chúc tụng và các lời khen ngợi."},
	{6, "6 Ôi! chỉ một mình Chúa là Ðức Giê-hô-va có một không hai; Chúa đã dựng nên các từng trời, và trời của các từng trời, cùng toàn cơ binh của nó, trái đất và các vật ở trên nó, biển và muôn vật ở dưới nó Chúa bảo tồn những vật ấy, và cơ binh của các từng trời đều thờ lạy Chúa."},
	{7, "7 Chúa ôi! Chúa là Giê-hô-va Ðức Chúa Trời mà đã chọn Áp-ram, đem người ra khỏi U-sơ của dân Canh-đê, và ban cho người tên Áp-ra-ham."},
	{8, "8 Chúa thấy lòng người trung thành tại trước mặt Chúa, và Chúa lập giao ước với người, đặng ban cho dòng dõi người xứ của dân Ca-na-an, dân Hê-tít, dân A-mô-rít, dân Phê-rê-sít, dân Giê-bu-sít, và dân Ghi-rê-ga-sít; Chúa có làm ứng nghiệm lời của Chúa, vì Chúa là công bình."},
	{9, "9 Chúa có thấy sự khốn khổ của tổ phụ chúng tôi tại Ê-díp-tô, có nghe tiếng kêu la của chúng tại bên Biển đỏ,"},
	{10, "10 làm những dấu kỳ phép lạ nơi Pha-ra-ôn, nơi đầy tớ người và nơi toàn dân sự của nước người; vì Chúa biết rằng họ có đối đãi tổ phụ chúng tôi cách kiêu căng, và Chúa có làm nổi danh Chúa như thể ngày nay."},
	{11, "11 Chúa có phân rẽ biển trước mặt chúng nó, để chúng nó đi ngang qua giữa biển trên đất khô; còn những kẻ đuổi theo, Chúa bèn ném họ dưới vực sâu, như ném đá trong nước lớn."},
	{12, "12 Ban ngày, Chúa dẫn dắt chúng bằng một trụ mây, và ban đêm bằng một trụ lửa, để chiếu sáng cho chúng trong con đường phải đi theo."},
	{13, "13 Chúa cũng giáng lâm tại trên núi Si-nai, phán với chúng từ trên trời, ban cho chúng những luật lệ ngay thẳng, pháp độ chơn thật, và những qui tắc cùng điều răn tốt lành."},
	{14, "14 Chúa khiến cho chúng biết ngày sa-bát thánh của Chúa, cậy Môi-se, tôi tớ của Chúa, truyền cho chúng những điều răn, qui tắc, và luật pháp."},
	{15, "15 Từ các từng trời, Chúa ban cho chúng bánh đặng ăn đói, khiến hòn đá chảy ra nước cho chúng uống khát, phán biểu chúng vào nhận lấy xứ mà Chúa đã thề ban cho chúng."},
	{16, "16 Nhưng tổ phụ chúng tôi cư xử cách kiêu hãnh cứng cổ mình, không nghe các điều răn của Chúa,"},
	{17, "17 chẳng khứng vâng theo và không nhớ đến các phép lạ mà Chúa đã làm giữa chúng; nhưng chúng cứng cổ mình, và trong sự bội nghịch mình cắt một kẻ làm đầu đặng trở về nơi nô lệ của họ. Nhưng Chúa vốn một Ðức Chúa Trời sẵn tha thứ, hay làm ơn, và thương xót, chậm nóng giận, và dư đầy nhân từ, Chúa không có lìa bỏ chúng."},
	{18, "18 Dẫu khi chúng làm một con bò con đực, mà rằng: Nấy là thần đã dẫn các ngươi lên khỏi Ê-díp-tô, và chọc cho Chúa giận nhiều,"},
	{19, "19 thì Chúa vì sự thương xót của Chúa, không lìa bỏ chúng trong đồng vắng; trụ mây dẫn đường ban ngày, không lìa khỏi trên chúng, hoặc trụ lửa lúc ban đêm chiếu sáng và chỉ cho chúng con đường chúng phải đi."},
	{20, "20 Chúa cũng ban cho chúng Thần linh lương thiện của Chúa, để dạy dỗ cho chúng, không từ chối cho miệng chúng ăn ma-na, và ban cho chúng nước đặng uống khát."},
	{21, "21 Phải, Chúa nuôi dưỡng chúng bốn mươi năm trong đồng vắng: chúng chẳng thiếu thốn chi cả; quần áo chúng không cũ rách, và chơn chúng chẳng phù lên."},
	{22, "22 Vả lại, Chúa ban cho chúng những nước và dân tộc, và phân phát nước ấy cho chúng; chúng nhận được nước của Si-hôn, tức nước của vua Hết-bôn, và nước của Oùc, vua Ba-san."},
	{23, "23 Chúa cũng thêm nhiều con cháu chúng đông như sao trên trời, đưa chúng vào xứ mà Chúa đã phán cùng tổ phụ chúng rằng chúng sẽ vào đó đặng nhận lấy xứ ấy."},
	{24, "24 ậy, con cháu chúng bèn vào nhận lấy xứ, và Chúa bắt phục trước mặt họ các dân của xứ, là dân Ca-na-an, phó dân ấy và những vua chúng nó cùng các dân tộc của xứ vào tay họ, đặng họ đãi các dân ấy theo ý mình muốn."},
	{25, "25 Họ chiếm lấy những thành bền vững, và ruộng đất mầu mỡ, được những nhà đầy các vật tốt lành, nhưng giếng đào, vườn nho, vườn ô-li-ve, và cây trái rất nhiều; chúng ăn, bèn được no nê, và mập béo, vui lòng trong sự nhân từ lớn lao của Chúa."},
	{26, "26 Dầu vậy, chúng chẳng vâng theo, bèn phản nghịch với Chúa, ném bỏ luật pháp Chúa sau lưng, giết các đấng tiên tri của Chúa, là người làm chứng nghịch cùng họ đặng đem họ trở lại cùng Chúa; song chúng lại chọc giận Chúa nhiều thay."},
	{27, "27 Vì vậy, Chúa phó họ vào tay cừu địch của họ; chúng nó hà hiếp họ. Trong thời hoạn nạn chúng kêu cầu cùng Chúa, thì từ các từng trời Chúa có nghe đến; theo lòng nhân từ lớn lao của Chúa, Chúa bèn ban cho những đấng giải cứu để cứu chúng khỏi tay kẻ cừu địch mình."},
	{28, "28 Nhưng khi chúng được an tịnh, bèn khởi làm lại điều ác trước mặt Chúa; vì vậy, Chúa bỏ chúng vào tay kẻ thù nghịch để quản hạt chúng; song khi chúng trở lại, kêu cầu cùng Chúa, thì Chúa từ trên trời nghe đến, và vì lòng thương xót Chúa, nên giải cứu chúng nhiều lần."},
	{29, "29 Chúa cũng làm chứng nghịch cùng chúng, để dẫn dắt chúng trở lại luật pháp của Ngài. Dầu vậy, chúng cư xử cách kiêu ngạo không nghe các điều răn Chúa, phạm đến luật lệ của Chúa, là luật lệ nếu người nào vâng làm theo, tất sẽ được sống; chúng ở chấp nhất, cứng cổ mình, và không khứng nghe theo."},
	{30, "30 Chúa dung thứ chúng nhiều năm và cậy Thần linh và các tiên tri của Chúa mà làm chứng nghịch cùng chúng; nhưng chúng không lắng tai nghe, nên Chúa phó chúng vào tay dân tộc của các xứ."},
	{31, "31 Song, vì lòng thương xót lớn lao của Chúa, Chúa không có tận diệt chúng, cũng không lìa bỏ chúng; vì Chúa vốn là Ðức Chúa Trời hay thương xót và nhơn từ."},
	{32, "32 Vậy bây giờ, hỡi Ðức Chúa Trời của chúng tôi ơi! là Ðức Chúa Trời rất lớn rất quyền năng và đáng sợ, xin chớ coi nhỏ mọn trước mặt Chúa các việc đau đớn cực nhọc đã giáng trên chúng tôi, trên các vua và các quan trưởng, trên những thầy tế lễ và các tiên tri, trên tổ phụ chúng tôi, và trên hết thảy dân sự của Chúa, từ đời các vua A-si-ri cho đến ngày nay."},
	{33, "33 Trong mọi việc đã xảy đến cho chúng tôi, Chúa vẫn là công bình; vì Chúa thi hành cách chơn thật, còn chúng tôi lại làm cách hung ác."},
	{34, "34 Các vua chúng tôi, các quan trưởng, những thầy tế lễ, và tổ phụ chúng tôi, không có gìn giữ luật pháp của Chúa, cũng chẳng nghe theo các điều răn và chứng cớ mà Chúa phán dạy nghịch cùng chúng."},
	{35, "35 Tại trong xứ của chúng, giữa đều ơn lành dư dật mà Chúa đã ban cho chúng, tại trong đất rộng rãi và màu mỡ mà Chúa đã đặt trước mặt chúng, chúng không phục sự Chúa, chẳng trở bỏ các công việc ác của họ."},
	{36, "36 Kìa, ngày nay chúng tôi làm tôi mọi; này chúng tôi làm tôi trong xứ để ăn bông trái và thổ sản tốt tươi của nó."},
	{37, "37 Xứ sanh ra nhiều thổ sản cho các vua mà Chúa đã lập trên chúng tôi tại cớ tội lỗi của chúng tôi: các vua ấy cũng tự ý mình lấn lướt trên thân thể chúng tôi và các súc vật của chúng tôi, và chúng tôi đương bị hoạn nạn lớn."},
	{38, "38 Dầu các sự này, chúng tôi lập giao ước chắc chắn, và chúng tôi ghi chép nó các quan trưởng, người Lê-vi, và những thầy tế lễ của chúng tôi đóng ấn cho."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
