static void view5() {
struct vi5 poems[] = {
	{1, "1 Khi người vào nhà Ðức Chúa Trời, hãy giữ chừng chơn mình. Thà lại gần mà nghe, hơn là dâng của tế lễ kẻ ngu muội; vì nó không hiểu biết mình làm ác."},
	{2, "2 Chớ vội mở miệng ra, và lòng ngươi chớ lật đật nói lời trước mặt Ðức Chúa Trời; vì Ðức Chúa Trời ở trên trời, còn ngươi ở dưới đất. Vậy nên ngươi khá ít lời."},
	{3, "3 Hễ nhiều sự lo lắng ắt sanh ra chiêm bao; còn nhiều lời thì sanh ra sự ngu dại."},
	{4, "4 Khi ngươi khấn hứa sự gì với Ðức Chúa Trời, chớ chậm mà hoàn nguyện; vì Ngài chẳng vui thích kẻ dại: vậy, khá trả điều gì ngươi hứa."},
	{5, "5 Thà đừng khấn hứa, hơn là khấn hứa mà lại không trả."},
	{6, "6 Ðừng cho miệng ngươi làm cho xác thịt mình phạm tội, và chớ nói trước mặt sứ giả của Ðức Chúa Trời rằng ấy là lầm lỗi. Sao làm cho Ðức Chúa Trời nổi giận vì lời nói ngươi, và Ngài làm hư công việc của tay ngươi?"},
	{7, "7 Ðâu có chiêm bao vô số và nhiều lời quá, đó cũng có sự hư không nhiều; song ngươi hãy kính sợ Ðức Chúa Trời."},
	{8, "8 Khi ngươi thấy trong xứ có kẻ nghèo bị hà hiếp, hoặc thấy sự phạm đến lẽ chánh trực, sự trái phép công bình, thì chớ lấy làm lạ; vì có kẻ cao hơn kẻ cao vẫn coi chừng, lại còn có Ðấng cao hơn nữa."},
	{9, "9 Song một vua hay cày cấy ruộng, ấy vẫn ích lợi cho xứ mọi bề."},
	{10, "10 Kẻ tham tiền bạc chẳng hề chán lắc tiền bạc; kẻ ham của cải chẳng hề chán về hoa lợi. Ðiều đó cũng là sự hư không."},
	{11, "11 Hễ của cải thêm nhiều chừng nào, kẻ ăn cũng thêm nhiều chừng nấy. Chủ của cải được ích gì hơn là xem thấy nó trước mặt chăng?"},
	{12, "12 Giấc ngủ của người làm việc là ngon, mặc dầu người ăn ít hay nhiều; nhưng sự chán lắc làm cho người giàu không ngủ được."},
	{13, "13 Có một tai nạn dữ mà ta đã thấy dưới mặt trời: ấy là của cải mà người chủ dành chứa lại, trở làm hại cho mình,"},
	{14, "14 hoặc vì cớ tai họa gì, cả của cải nầy phải mất hết; nếu người chủ sanh một con trai, thì để lại cho nó hai tay không."},
	{15, "15 Mình lọt ra khỏi lòng mẹ trần truồng thể nào, ắt sẽ trở về thể ấy, và về các huê lợi của sự lao khổ mình, chẳng có vậy gì tay mình đem theo được."},
	{16, "16 Ðiều nầy cũng là một tai nạn lớn: người ra đời thể nào, ắt phải trở về thể ấy. Vậy, chịu lao khổ đặng theo luồng gió thổi, có ích lợi gì chăng?"},
	{17, "17 Lại trọn đời mình ăn trong sự tối tăm, phải nhiều sự buồn rầu đau đớn và phiền não."},
	{18, "18 Kìa, ta đã nhìn thấy rằng ăn, uống, và hưởng phải của sự lao khổ mình làm ở dưới mặt trời trọn trong những ngày mà Ðức Chúa Trời ban cho, ấy thật là tốt và hay; vì đó là kỷ phần mình."},
	{19, "19 Hễ Ðức Chúa Trời ban cho người nào giàu có, của cải, làm cho người có thế ăn lấy, nhận lãnh kỷ phần, và vui vẻ trong công lao của mình, ấy là một sự ban cho của Ðức Chúa Trời;"},
	{20, "20 nhân người sẽ ít nhớ những ngày của đời mình; vì Ðức Chúa Trời ứng đáp người bằng sự vui mừng trong lòng người."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view6() {
struct vi6 poems[] = {
	{1, "1 Có một tai nạn khác mà ta đã thấy dưới mặt trời, thường làm nặng nề cho loài người."},
	{2, "2 Kìa có một người mà Ðức Chúa Trời đã ban cho sự giàu có, của cải, va sang trọng, đến đỗi mọi sự lòng người ước ao, chẳng thiếu gì hết; nhưng Ðức Chúa Trời không cho người có thể ăn lấy, bèn là một người khác được ăn. Ấy là một sự hư không, một tai nạn cực khổ."},
	{3, "3 Nếu một người sanh trăm con trai, và sống nhiều năm cho đến cao niên trường thọ, mà lòng chẳng được hưởng phước, và chết chẳng được chôn; ta nói đứa con sảo còn hơn người ấy;"},
	{4, "4 vì đứa con sảo ra hư không, lại trở về sự tối tăm, và tên nó bị sự u ám vùi lấp."},
	{5, "5 Nó không thấy mặt trời, cũng không biết đến; nó có được phần an nghỉ hơn người kia."},
	{6, "6 Người dầu sống đến hai ngàn năm, mà không hưởng được chút phước nào, cuối cùng cả thảy há chẳng về chung một chỗ sao?"},
	{7, "7 Mọi sự lao khổ của loài người là vì miệng mình, song không hề được thỏa nguyện."},
	{8, "8 Người khôn ngoan hơn chi kẻ ngu muội? Người nghèo biết đạo ăn ở trước mặt người ta, nào có ích chi?"},
	{9, "9 Sự gì thấy bằng mắt hơn là sự tham muốn buông tuồng; điều đó lại là một sự hư không, theo luồng gió thổi."},
	{10, "10 Phàm điều gì có trước thì đã được đặt tên từ lâu rồi; loài người sẽ ra thể nào, thì đã biết từ trước rồi; người không thể cãi trả với Ðấng mạnh hơn mình."},
	{11, "11 Có nhiều điều gia thêm sự hư không; vậy, người ta được ích chi hơn;"},
	{12, "12 vả, trong những ngày của đời hư không mà loài người trải qua như bóng, ai biết điều gì là ích cho mình? Vì ai có thể nói trước cho người nào về điều sẽ xảy ra sau mình dưới mặt trời?"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view7() {
struct vi7 poems[] = {
	{1, "1 Danh tiếng hơn dầu quí giá; ngày chết hơn ngày sanh."},
	{2, "2 Ði đến nhà tang chế hơn là đến nhà yến tiệc; vì tại đó thấy sự cuối cùng của mọi người; và người sống để vào lòng."},
	{3, "3 Buồn rầu hơn vui vẻ; vì nhờ mặt buồn, lòng được vui."},
	{4, "4 Lòng người khôn ngoan ở trong nhà tang chế, còn lòng kẻ ngu muội ở tại nhà vui sướng."},
	{5, "5 Thà nghe lời quở trách của người khôn ngoan, còn hơn là câu hát của kẻ ngu muội."},
	{6, "6 Vì tiếng cười của kẻ ngu muội giống như tiếng gai nhọn nổ dưới nồi. Ðiều đó cũng là sự hư không."},
	{7, "7 Phải, sự sách thủ tiền tài làm cho kẻ khôn ra ngu, và của hối lộ khiến cho hư hại lòng."},
	{8, "8 Cuối cùng của một việc hơn sự khởi đầu nó; lòng kiên nhẫn hơn lòng kiêu ngạo."},
	{9, "9 Chớ vội giận; vì sự giận ở trong lòng kẻ ngu muội."},
	{10, "10 Chớ nói rằng: Nhơn sao ngày trước tốt hơn ngày bây giờ? Vì hỏi vậy là chẳng khôn."},
	{11, "11 Sự khôn ngoan cũng tốt bằng cơ nghiệp; nó có ích cho những người thấy mặt trời."},
	{12, "12 Vì sự khôn ngoan che thân cũng như tiền bạc che thân vậy; nhưng sự khôn ngoan thắng hơn, vì nó giữ mạng sống cho người nào đã được nó."},
	{13, "13 Hãy xem xét công việc của Ðức Chúa Trời; vì vật gì Ngài đã đánh cong, ai có thể làm ngay lại được?"},
	{14, "14 Trong ngày thới thạnh hãy vui mừng, trong ngày tai nạn hay coi chừng; vì Ðức Chúa Trời đặt ngày nầy đối với ngày kia, hầu cho người đời chẳng thấy trước đặng điều xảy ra sau mình."},
	{15, "15 Trong những người hư không của ta, ta đã thấy cả điều nầy: Người công bình tuy công bình cũng phải hư mất; và kẻ hung ác tuy hung ác lại được trường thọ."},
	{16, "16 Chớ công bình quá, cũng đừng làm ra mình khôn ngoan quá; cớ sao làm thiệt hại cho mình?"},
	{17, "17 Cũng chớ nên hung hăng quá, và đừng ăn ở như kẻ ngu muội; cớ sao ngươi chết trước kỳ định?"},
	{18, "18 Ngươi giữ được điều nầy, ấy là tốt; mà lại cũng đừng nới tay khỏi điều kia; vì ai kính sợ Ðức Chúa Trời ắt tránh khỏi mọi điều đó."},
	{19, "19 Sự khôn ngoan khiến cho người khôn có sức mạnh hơn là mười kẻ cai trị ở trong thành."},
	{20, "20 Thật, chẳng có người công bình ở trên đất làm điều thiện, mà không hề phạm tội."},
	{21, "21 Chớ để lòng về mọi lời người ta nói, e ngươi nghe kẻ tôi tớ mình chưởi rủa mình chăng."},
	{22, "22 Vì thường khi lòng ngươi cũng biết mình đã rủa kẻ khác."},
	{23, "23 Ta đã lấy sự khôn ngoan nghiệm xét mọi sự ấy; ta nói rằng: Ta sẽ ở khôn ngoan; nhưng sự khôn ngoan cách xa ta."},
	{24, "24 Vả, điều chi xa quá, điều chi sâu thẩm lắm, ai có thể tìm được?"},
	{25, "25 Ta lại hết lòng chuyên lo cho hiểu biết, xét nét, kiếm tìm sự khôn ngoan và chánh lý muôn vật, lại cho biết rằng gian ác là dại dột và sự dại dột là điên cuồng."},
	{26, "26 Ta tìm thấy một điều cay đắng hơn sự chết, ấy là một người đờn bà có lòng giống như lưới bẫy, tay tợ như dây tói: ai ở đẹp lòng Ðức Chúa Trời sẽ thoát khỏi nó; còn kẻ có tội sẽ bị nó vấn lấy."},
	{27, "27 Kẻ truyền đạo nói: Sau khi xem xét muôn sự từng điều từng vật, đặng tìm chánh lý, thì nầy là điều ta tìm được;"},
	{28, "28 ấy là lòng ta hãy còn tìm mà chưa tìm ra: trong một ngàn người đờn ông ta đã tìm được một người; còn trong cả thảy người đờn bà ta chẳng tìm được một ai hết."},
	{29, "29 Nhưng nầy là điều ta tìm được: Ðức Chúa Trời đã dựng nên người ngay thẳng; song loài người có tìm kiếm ra lắm mưu kế."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view8() {
struct vi8 poems[] = {
	{1, "1 Ai sánh được với người khôn ngoan? Ai biết giải nghĩa các vật? Sự khôn ngoan làm cho sáng sủa mắt người, và đổi sắc nghiêm trang đi."},
	{2, "2 Ta khuyên rằng: Vì cớ lời thề cùng Ðức Chúa Trời, khá gìn giữ mạng vua."},
	{3, "3 Chớ vội lui ra khỏi trước mặt người; đừng ghì mài trong sự ác, vì vua làm điều chi đẹp lòng mình."},
	{4, "4 Vả lại, lời vua có quyền; ai dám nói với người rằng: Ngươi làm chi?"},
	{5, "5 Ai tuân theo lịnh người thì chẳng biết sự tai nạn, và lòng người khôn ngoan biết thì thế và phép lệ."},
	{6, "6 Vì tuy loài người bị sự tai nạn bối rối nhiều, phàm sự gì cũng có thì thế và phép lệ."},
	{7, "7 Vì người chẳng biết điều chi sẽ xảy đến; ai có thế nói trước được các việc được xảy ra làm sao?"},
	{8, "8 Chẳng có ai cai trị được sanh khí để cầm sanh khí lại; lại chẳng ai có quyền về ngày chết; trong khi tranh chiến chẳng có sự miễn thứ; sự gian ác nào cứu được kẻ làm ra nó."},
	{9, "9 Ta có thấy các điều đó; ta chuyên lòng suy nghĩ về mọi việc làm ra ở dưới mặt trời. Có khi người nầy cai trị trên người kia mà làm tai hại cho người ấy."},
	{10, "10 Ta cùng thấy những kẻ gian ác qua đời, và được chôn bình yên; còn những người làm lành lại phải lìa khỏi nơi thánh, bị người đồng thành quên đi. Ấy cũng là sự hư không."},
	{11, "11 Bởi chẳng thi hành ngay án phạt những việc ác, nên lòng con loài người chuyên làm điều ác."},
	{12, "12 Vì kẻ có tội làm ác một trăm lần vẫn được trường thọ; dầu vậy, ta biết rằng kẻ kính sợ trước mặt Ðức Chúa Trời, sau rất ắt được phước."},
	{13, "13 Nhưng kẻ ác chẳng sẽ được phước; cũng sẽ không được sống lâu, vì đời nó giống như bóng qua; ấy tḀ¡i nó không kính sợ trước mặt Ðức Chúa Trời."},
	{14, "14 Lại còn có một sự hư không khác trên thế gian: lắm người công bình bị đãi theo công việc kẻ ác, và lắm kẻ ác lại được đãi theo công việc người công bình. Ta nói rằng điều đó cũng là sự hư không."},
	{15, "15 Ta bèn khen sự vui mừng, bởi vì dưới mặt trời chẳng có điều gì tốt cho loài người hơn là ăn, uống, và vui sướng; vì ấy là điều còn lại cho mình về công lao trọn trong các ngày của đời mình, mà Ðức Chúa Trời ban cho mình dưới mặt trời."},
	{16, "16 Khi ta chuyên lòng để học biết sự khôn ngoan, để hiểu mọi việc làm ra trên đất, thì ta thấy có người ngày và đêm không cho mắt ngủ,"},
	{17, "17 cũng thấy mọi công việc Ðức Chúa Trời, và hiểu biết người ta không thể dò được mọi việc làm ra dưới mặt trời, dầu chịu cực nhọc ngần nào đặng tìm biết, cũng chẳng tìm được; và dầu cho người khôn ngoan tưởng rằng sẽ chắc biết, thì cũng chẳng tìm được."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view9() {
struct vi9 poems[] = {
	{1, "1 Nầy là các việc khác mà ta đã suy nghĩ và chuyên lòng hiểu rõ ràng: ta thấy người công bình và khôn ngoan, luôn với việc làm của họ, đều ở trong tay Ðức Chúa Trời; hoặc sự yêu hay là sự ghét, loài người chẳng biết rõ: thảy đều thuộc về cuộc tương lai."},
	{2, "2 Mọi sự xảy đến cho mọi người như nhau: người công bình hay là kẻ hung ác, người hiền lành, thanh sạch, người dâng của tế lễ hay là người không dâng, người thiện hay là kẻ có tội, kẻ phát thề hay là người sợ lời thề, cả thảy đều đồng hưởng một số phận."},
	{3, "3 Tai nạn lớn hơn dưới mặt trời, ấy là mọi người đồng gặp một số phận, lòng loài người đầy dẫy sự hung ác, và sự điên cuồng choán trong lòng họ đương khi sống, rồi họ đi về cùng kẻ chết."},
	{4, "4 Một người ở trong hội kẻ sống, còn có sự trông mong; vì con chó sống hơn là sư tử chết."},
	{5, "5 Kẻ sống biết mình sẽ chết; nhưng kẻ chết chẳng biết chi hết, chẳng được phần thưởng gì hết; vì sự kỷ niệm họ đã bị quên đi."},
	{6, "6 Sự yêu, sự ghét, sự ganh gỗ của họ thảy đều tiêu mất từ lâu; họ chẳng hề còn có phần nào về mọi điều làm ra dưới mặt trời."},
	{7, "7 Hãy đi ăn bánh cách vui mừng, và uống rượu cách hớn hở; vì Ðức Chúa Trời đã nhận các công việc ngươi."},
	{8, "8 Khá hằng mặc áo trắng, chớ thiếu dầu thơm trên đầu ngươi."},
	{9, "9 Trọn các ngày của đời hư không ngươi, mà Ðức Chúa Trời ban cho ngươi dưới mặt trời, hãy ở vui vẻ cùng vợ yêu dấu; vì đó là phần của ngươi trong đời nầy, và là điều ngươi được bởi công lao mình làm ở dưới mặt trời."},
	{10, "10 Mọi việc tay ngươi làm được, hãy làm hết sức mình; vì dưới âm phủ, là nơi ngươi đi đến, chẳng có việc làm, chẳng có mưu kế, cũng chẳng có tri thức, hay là sự khôn ngoan."},
	{11, "11 Ta xây lại thấy dưới mặt trời có kẻ lẹ làng chẳng được cuộc chạy đua, người mạnh sức chẳng được thắng trận, kẻ khôn ngoan không đặng bánh, kẻ thông sáng chẳng hưởng của cải, và người khôn khéo chẳng được ơn; vì thời thế và cơ hội xảy đến cho mọi người."},
	{12, "12 Lại, loài người chẳng hiểu biết thời thế của mình: như cá mắc lưới, chỉ phải bẫy dò thể nào, thì loài người cũng bị vấn vương trong thời tai họa xảy đến thình lình thể ấy."},
	{13, "13 Ta cũng đã thấy sự khôn ngoan nầy dưới mặt trời, thật lấy làm cả thể."},
	{14, "14 Có thành nhỏ kia được một ít dân sự, một vua cường thạnh đến vây hãm nó, đắp những lũy cao lớn cùng nó."},
	{15, "15 Vả, trong thành ấy có một người nghèo mà khôn, dùng sự khôn ngoan mình giải cứu thành ấy; song về sau không ai nhớ đến người nghèo đó."},
	{16, "16 Ta bèn nói rằng: Sự khôn ngoan hơn sức mạnh; nhưng mà sự khôn ngoan của người nghèo bị khinh dể, và lời nói của người không ai nghe."},
	{17, "17 Lời của người khôn ngoan được nghe tại nơi êm lặng còn hơn tiếng kỳ lạ của kẻ cai trị trong đám dại dột."},
	{18, "18 Sự khôn ngoan hơn đồ binh khí; nhưng chỉ một người có tội phá hủy được nhiều sự lành."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view10() {
struct vi10 poems[] = {
	{1, "1 Con ruồi chết làm cho thúi dầu thơm của thợ hòa hương; cũng vậy, một chút điên dại làm nhẹ danh một người khôn ngoan sang trọng."},
	{2, "2 Trái tim của người khôn ở bên hữu; còn trái tim của kẻ dại ở bên tả."},
	{3, "3 Lại khi kẻ dại đi đường, rõ ra là thiếu mất lẽ phải; nó nói cho mọi người rằng mình là kẻ dại."},
	{4, "4 Nếu người cai quản nổi giận cùng ngươi, chớ lìa khỏi chỗ mình; vì sự mềm mại ngăn ngừa được tội lỗi lớn."},
	{5, "5 Có một tai nạn ta đã thấy dưới mặt trời, như một sự lỗi lầm do quan trưởng phạm:"},
	{6, "6 ấy là kẻ ngu muội được đặt ở nơi cao, còn người giàu lại ngồi chỗ thấp."},
	{7, "7 Ta đã thấy kẻ tôi tớ đi ngựa, còn quan trưởng đi bộ như tôi tớ vậy."},
	{8, "8 Kẻ nào đào hầm sẽ sa xuống đó, còn kẻ phá vách tường phải bị rắn cắn."},
	{9, "9 Ai lăn đã khỏi chỗ nó, sẽ bị thương; kẻ bửa củi có khi phải hiểm nghèo."},
	{10, "10 Cái rìu lụt mà không mài lưỡi nó lại, ắt phải rán sức còng nhiều; nhưng sự khôn ngoan có ích đặng dẫn dắt."},
	{11, "11 Nếu rắn cắn trước khi bị ếm chú, thì thầy ếm chú chẳng làm ích gì."},
	{12, "12 Lời của miệng người khôn ngoan có ơn; nhưng môi của kẻ khờ dại lại nuốt lấy nó."},
	{13, "13 Lời nói của miệng nó, khởi đầu là ngược đãi, cuối cùng vẫn điên cuồng nguy hiểm."},
	{14, "14 Kẻ ngu muội hay nói nhiều lời; dầu vậy, người ta chẳng biết điều sẽ xảy đến; và ai là kẻ tỏ ra được cho nó sự sẽ có sau mình?"},
	{15, "15 Công lao kẻ ngu muội làm cho mệt nhọc chúng nó, vì chẳng biết phải bắt đường nào đặng đi đến thành."},
	{16, "16 Hỡi xứ, khốn thay cho mầy khi có vua còn thơ ấu, và các quan trưởng mầy ăn từ lúc buổi sáng."},
	{17, "17 Hỡi xứ, phước cho mầy khi có vua là dòng cao sang, và các quan trưởng mầy ăn theo giờ xứng đáng, để bổ sức lại, chớ chẳng phải để đắm say!"},
	{18, "18 Tại tay làm biếng nên rường nhà sụp; vì tay nhác nhớn nên nhà dột."},
	{19, "19 Người ta bày tiệc đặng vui chơi; rượu khiến cho đời vui; có tiền bạc thì ứng cho mọi sự."},
	{20, "20 Dầu trong tư tưởng ngươi cũng chớ nguyền rủa vua; dầu trong phòng ngủ ngươi cũng chớ rủa sả kẻ giàu có; vì chim trời sẽ đến ra tiếng ngươi, và loài có cánh sẽ thuật việc ra."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view11() {
struct vi11 poems[] = {
	{1, "1 Hãy liệng bánh ngươi nơi mặt nước, vì khỏi lâu ngày ngươi sẽ tìm nó lại."},
	{2, "2 Hãy phân phát nó cho bảy hoặc cho tám người; vì ngươi không biết tai nạn nào sẽ xảy ra trên đất."},
	{3, "3 Khi mây đầy nước, nó bèn mưa xuống đất; khi một cây ngã về hướng nam hay về hướng bắc, hễ ngã chỗ nào nó phải ở chỗ đó."},
	{4, "4 Ai xem gió sẽ không gieo; ai xem mây sẽ không gặt."},
	{5, "5 Người không biết đường của gió đi, cũng không biết xương cốt kết cấu trong bụng đờn bà mang thai thể nào, thì cũng một thể ấy, ngươi chẳng hiểu biết công việc của Ðức Chúa Trời, là Ðấng làm nên muôn vật."},
	{6, "6 Vừa sáng sớm, khá gieo giống ngươi, và buổi chiều, chớ nghỉ tay ngươi; vì ngươi chẳng biết giống nào sẽ mọc tốt, hoặc giống nầy, hoặc giống kia, hoặc là cả hai đều sẽ ra tốt."},
	{7, "7 Ánh sáng thật là êm dịu; con mắt thấy mặt trời lấy làm vui thích."},
	{8, "8 Nếu một người được sống lâu năm, thì khá vui vẻ trong trọn các năm ấy; song cũng chớ quên những ngày tối tăm, vì những ngày ấy nhiều. Phàm việc gì xảy đến đều là sự hư không."},
	{9, "9 Hỡi kẻ trẻ kia, hãy vui mừng trong buổi thiếu niên, khá đem lòng hớn hở trong khi còn thơ ấu, hãy đi theo đường lối lòng mình muốn, và nhìn xem sự mắt mình ưa thích, nhưng phải biết rằng vì mọi việc ấy, Ðức Chúa Trời sẽ đòi ngươi đến mà đoán xét."},
	{10, "10 Vậy, khá giải sầu khỏi lòng ngươi, và cất điều tai hại khỏi xác thịt ngươi; vì lúc thiếu niên và thì xuân xanh là sự hư không mà thôi."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view12() {
struct vi12 poems[] = {
	{1, "1 Trong buổi còn thơ ấu hãy tưởng nhớ Ðấng Tạo hóa ngươi, trước khi người ngày gian nan chưa đến, trước khi những năm tới mà ngươi nói rằng: Ta không lấy làm vui lòng;"},
	{2, "2 trước khi ánh sáng mặt trời, mặt trăng, và các ngôi sao chưa tối tăm, và mây chưa lại tuôn đến sau cơn mưa;"},
	{3, "3 trong ngày ấy kẻ giữ nhà run rẩy, những người mạnh sức cong khom, kẻ xay cối ngừng lại bởi vì số ít, nhiều kẻ trông xem qua cửa sổ đã làng mắt,"},
	{4, "4 hai cánh cửa bên đường đóng lại, và tiếng xay mỏn lần; lúc ấy người ta nghe tiếng chim kêu bèn chờ dậy, và tiếng con gái hát đều hạ hơi;"},
	{5, "5 lại người ta sợ sệt mà lên cao, và hãi hùng lúc đi đường; lúc ấy cây hạnh trổ bông, cào cào trở nên nặng, và sự ước ao chẳng còn nữa; vì bấy giờ người đi đến nơi ở đời đời của mình, còn những kẻ tang chế đều đi vòng quanh các đường phố:"},
	{6, "6 lại hãy tưởng nhớ Ðấng Tạo hóa trước khi dây bạc đất, và chén vàng bể, trước khi vò vỡ ra bên suối, và bánh xe gãy ra trên giếng;"},
	{7, "7 và bụi tro trở vào đất y như nguyên cũ, và thần linh trở về nơi Ðức Chúa Trời, là Ðấng đã ban nó."},
	{8, "8 Kẻ truyền đạo nói: Hư không của sự hư không; mọi sự đều hư không."},
	{9, "9 Vả lại, bởi vì kẻ truyền đạo là người khôn ngoan, nên cũng cứ dạy sự tri thức cho dân sự; người đã cân nhắc, tra soát, và sắp đặt thứ tự nhiều câu châm ngôn."},
	{10, "10 Kẻ truyền đạo có chuyên lo tìm kiếm những câu luận tốt đẹp; và các lời đã viết ra đều là chánh trực và chơn thật."},
	{11, "11 Lời của người khôn ngoan giống như đót; sắp chọn các câu châm ngôn khác nào đinh đóng chặt: nó do một đấng chăn chiên mà truyền ra."},
	{12, "12 Lại, hỡi con, hãy chịu dạy: người ta chép nhiều sách chẳng cùng; còn học quá thật làm mệt nhọc cho xác thịt."},
	{13, "13 Chúng ta hãy nghe lời kết của lý thuyết nầy: Khá kính sợ Ðức Chúa Trời và giữ các điều răn Ngài; ấy là trọn phận sự của ngươi."},
	{14, "14 Vì Ðức Chúa Trời sẽ đem đoán xét các công việc, đến đỗi việc kín nhiệm hơn hết, hoặc thiện hoặc ác cũng vậy."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
