#include <map>
#include <string>
class Bible29
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
	struct vi3	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Nầy là lời Ðức Giê-hô-va phán cùng Giô-ên, con trai của Phê-thu-ên:"},
	{2, "2 Hỡi kẻ già cả, hãy nghe điều nầy! Các ngươi hết thảy là dân cư trong đất hãy lắng tai! Há có sự gì như vậy đã xảy đến trong ngày các ngươi, hay là trong ngày tổ phụ các ngươi sao?"},
	{3, "3 Hãy kể chuyện nầy lại cho con cái các ngươi; con cái các ngươi kể cho con cái chúng nó, con cái chúng nó kể cho dòng dõi nối theo."},
	{4, "4 Cái gì sâu keo còn để lại cào cào ăn; cái gì cào cào còn để lại, sâu lột vỏ ăn; cái gì sâu lột vỏ còn để lại, châu chấu ăn."},
	{5, "5 Hỡi kẻ say sưa, hãy thức dậy và khóc lóc! Hỡi các ngươi hết thảy là kẻ hay uống rượu, hãy than vãn vì cớ rượu ngọt đã bị chất khỏi miệng các ngươi!"},
	{6, "6 Vì một dân mạnh và vô số, đã lên nghịch cùng đất ta; răng nó là răng của sư tử, nó có răng hàm của sư tử cái."},
	{7, "7 Nó đã hủy phá cây nho ta và lột vỏ cây vả ta. Nó đã lột tiệt cây ấy và bỏ đi; nhánh nó đã trở nên trắng."},
	{8, "8 Ngươi khá than khóc như một người nữ đồng trinh mặc bao gai đặng khóc chồng mình thuở còn trẻ tuổi!"},
	{9, "9 Của lễ chay và lễ quán đã cất khỏi nhà Ðức Giê-hô-va; các thầy tế lễ hầu việc Ðức Giê-hô-va đương ở trong sự tang chế."},
	{10, "10 Ðồng ruộng bị phá hoang, và đất đương sầu thảm; vì lúa mì bị hủy hại, dầu mới đã cạn, dầu đã hao tổn."},
	{11, "11 Hỡi kẻ cày ruộng, hãy hổ thẹn; hỡi kẻ làm vườn nho hãy than khóc, vì cớ lúa mì và mạch nha, vì mùa ngoài đồng đã mất."},
	{12, "12 Cây nho khô héo, cây vả hao mòn; cây lựu cũng như cây chà là, cây tần, mọi cây cối ngoài đồng đều khô héo; sự vui vẻ đã khô héo đi khỏi con trai loài người!"},
	{13, "13 Hỡi các thầy tế lễ, hãy nịt lưng và than khóc. Hỡi các ngươi là kẻ làm việc ở bàn thờ, hãy thở than. Hỡi kẻ chức dịch của Ðức Chúa Trời ta, hãy đến mặc áo bao gai mà nằm cả đêm! Vì của lễ chay và lễ quán không được vào trong nhà Ðức Chúa Trời các ngươi!"},
	{14, "14 Khá định sự kiêng ăn, gọi một hội đồng trọng thể, nhóm các trưởng lão và hết thảy dân cư trong đất lại nơi nhà Giê-hô-va Ðức Chúa Trời các ngươi; và hãy kêu cùng Ðức Giê-hô-va!"},
	{15, "15 Ôi ngày ấy! vì ngày Ðức Giê-hô-va đã gần! Nó đến như một tai vạ thả ra bởi Ðấng Toàn năng."},
	{16, "16 Lương thực há chẳng phải đã dứt khỏi trước mặt chúng ta, và sự vui mừng hớn hở há chẳng phải đã dứt khỏi nhà Ðức Chúa Trời chúng ta hay sao?"},
	{17, "17 Hột giống đã teo đi dưới những cục đất nó; kho đụn đã hủy phá, huân lẫm đổ nát, vì lúa mì khô héo."},
	{18, "18 Kìa, súc vật rên siếc! Kìa, bầy bò bối rối! Ấy là tại chúng nó không có đồng cỏ nữa! Những bầy chiên cũng khốn khổ."},
	{19, "19 Hỡi Ðức Giê-hô-va, tôi kêu cầu Ngài; vì lửa đã nuốt những đám cỏ nơi đồng vắng, ngọn lửa đã thiêu hết thảy cây cối ngoài đồng!"},
	{20, "20 Thật, những thú đồng cũng thở giốc cho Ngài, vì các dòng nước đã khô, lửa đã nuốt những đám cỏ nơi đồng vắng."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Hãy thổi kèn trong Si-ôn; hãy thổi vang ra trên núi thánh ta! Hết thảy dân cư trong đất khá đều run rẩy! Vì ngày Ðức Giê-hô-va đến, ngày ấy đã gần:"},
	{2, "2 tức là ngày mờ mịt và tối tăm, ngày của mây và sương mù. Có một dân lớn và mạnh đến như ánh sáng sớm mai giãi ra trên núi, đến nỗi từ trước chưa hề có, mà về sau nầy, về muôn đời sau nầy cũng sẽ không có như vậy nữa."},
	{3, "3 Trước mặt nó, có lửa thiêu nuốt, sau lưng nó, có ngọn lửa cháy tiêu. Lúc trước nó, đất nầy như vườn Ê-đen; khi sau nó, ấy là một đồng vắng hoang vu, và không có chi trốn khỏi nó hết."},
	{4, "4 Hình trạng chúng nó như hình trạng ngựa, và chúng nó chạy tới như người cỡi ngựa."},
	{5, "5 Khi chúng nó nhảy lên trên chót núi, thì nghe như tiếng xe cộ, hay là như tiếng ngọn lửa cháy rơm: ấy là như một dân mạnh sắp hành nơi chiến trận."},
	{6, "6 Khi thấy chúng nó, các dân đều rầu rĩ, hết thảy đều sự tái mặt."},
	{7, "7 Chúng nó chạy như những người bạo mạnh; trèo các vách thành như những lính chiến; mỗi người bước đi trước mặt mình, chẳng hề sai hàng ngũ mình."},
	{8, "8 Chúng nó chẳng hề đụng chạm nhau, ai đi đường nấy; xông qua những khí giới mà chẳng bỏ đường mình."},
	{9, "9 Chúng nó nhảy trên thành phố, chạy trên tường, trèo lên các nhà, vào bởi các cửa sổ như là kẻ trộm."},
	{10, "10 Ðất run rẩy, trước mặt chúng nó, các từng trời rung rinh mặt trời mặt trăng đều tối tăm, các ngôi sao thâu sự sáng lại."},
	{11, "11 Ðức Giê-hô-va làm cho vang tiếng ra trước mặt cơ binh Ngài; vì trại quân Ngài rất lớn; và những kẻ làm theo mạng lịnh Ngài rất mạnh. Thật, ngày của Ðức Giê-hô-va là lớn và đáng khiếp; ai có thể đương lại?"},
	{12, "12 Ðức Giê-hô-va phán: Bây giờ cũng hãy hết lòng trở về cùng ta, kiêng ăn, khóc lóc và buồn rầu."},
	{13, "13 Hãy xé lòng các ngươi, và đừng xé áo các ngươi. Khá trở lại cùng Giê-hô-va Ðức Chúa Trời các ngươi; vì Ngài là nhơn từ và hay thương xót, chậm giận và giàu ơn, đổi ý về sự tai vạ."},
	{14, "14 Ai biết được Ngài sẽ chẳng xây lòng đổi ý, chẳng để lại phước lành sau mình, tức là của lễ chay, lễ quán cho Giê-hô-va Ðức Chúa Trời các ngươi, hay sao?"},
	{15, "15 Hãy thổi kèn trong Si-ôn; hãy định sự kiêng ăn, gọi một hội đồng trọng thể!"},
	{16, "16 Hãy nhóm dân sự, biệt riêng hội chúng nên thánh; hãy mời các trưởng lão đến đó! Hãy họp lại các con trẻ và những đứa đương bú vú! Người chồng mới khá ra khỏi buồng mình, và người vợ mới khá ra khỏi chốn động phòng!"},
	{17, "17 Các thầy tế lễ, là những kẻ hầu việc Ðức Giê-hô-va, khá khóc lóc giữa hiên cửa và bàn thờ, và nói rằng: Hãy Ðức Giê-hô-va, xin hãy tiếc dân Ngài, và chớ để cơ nghiệp Ngài bị sỉ nhục, và bị các nước cai trị! Làm sao để người ta nói giữa các dân tộc rằng: Nào Ðức Chúa Trời chúng nó ở đâu!"},
	{18, "18 Ðức Giê-hô-va đã vì đất mình động lòng ghen; Ngài đã động lòng thương xót dân mình."},
	{19, "19 Ðức Giê-hô-va đã trả lời, và đã phán cùng dân mình rằng: Nầy, ta sẽ ban cho các ngươi lúa mì, rượu mới, và dầu, các ngươi sẽ no nê về các thứ đó. Ta sẽ chẳng làm các ngươi nên cớ sỉ nhục giữa các dân nữa."},
	{20, "20 Ta sẽ khiến đạo binh đến từ phương bắc lánh xa các ngươi, và đuổi nó vào đất khô khan và hoang vu, quân tiên phong nó vào biển đông. Quân hậu tập nó và biển tây. Mùi hôi nó bay lên, mùi tanh nó dậy lên, vì nó đã làm những việc lớn."},
	{21, "21 Hỡi đất, chớ sợ chi; hãy nức lòng vui vẻ và mừng rỡ; vì Ðức Giê-hô-va đã làm những việc lớn."},
	{22, "22 Hỡi các loài thú đồng, đừng sợ; vì những đám cỏ nơi đồng vắng nứt đọt, cây ra trái, cây vả và cây nho đều ra sức nó."},
	{23, "23 Hỡi con cái Si-ôn, các ngươi hãy nức lòng vui vẻ và mừng rỡ trong Giê-hô-va Ðức Chúa Trời các ngươi! Vì Ngài ban mưa phải thời cho các ngươi về mùa đầu, và xuống mưa dồi dào về mùa đầu và mùa cuối cho các ngươi nơi tháng đầu tiên."},
	{24, "24 Những sân sẽ đầy lúa mì, những thùng tràn rượu mới và dầu."},
	{25, "25 Ta sẽ đền bù cho các ngươi về mấy năm đã bị cắn phá bởi cào cào, sâu lột vỏ, sâu keo, và châu chấu, là đạo binh lớn mà ta đã sai đến giữa các ngươi."},
	{26, "26 Các ngươi sẽ đủ ăn và được no nê. Các ngươi sẽ ngợi khen danh Giê-hô-va Ðức Chúa Trời mình, là Ðấng đã xử với các ngươi cách lạ lùng. Rồi dân ta sẽ không hề bị xấu hổ nữa."},
	{27, "27 Chúng nó sẽ biết rằng ta ở giữa Y-sơ-ra-ên, biết rằng ta là Giê-hô-va Ðức Chúa Trời các ngươi, và chẳng có ai khác; dân ta sẽ không hề bị xấu hổ nữa."},
	{28, "28 Sau đó, ta sẽ đổ Thần ta trên các loài xác thịt; con trai và con gái các ngươi sẽ nói tiên tri; những người già cả các ngươi sẽ thấy chiêm bao, những kẻ trai trẻ các ngươi sẽ xem sự hiện thấy."},
	{29, "29 Trong những ngày đó, dầu những đầy tớ trai và đầy tớ gái, ta cũng đổ Thần ta lên."},
	{30, "30 Ta sẽ tỏ những sự lạ ra trong các từng trời và trên đất: tức là máu lửa, và những trụ khói."},
	{31, "31 Mặt trời sẽ đổi ra tối tăm, mặt trăng ra máu, trước khi ngày lớn và kinh khiếp của Ðức Giê-hô-va chưa đến."},
	{32, "32 Bấy giờ ai cầu khẩn danh Ðức Giê-hô-va thì sẽ được cứu; vì theo lời Ðức Giê-hô-va, thì ở trên núi Si-ôn và trong Giê-ru-sa-lem, sẽ có những người còn sống sót, sẽ có kẻ mà Ðức Giê-hô-va kêu gọi."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct vi3 poems[] = {
	{1, "1 Vì nầy, trong những ngày đó, và trong kỳ đó, khi ta sẽ đem phu tù của Giu-đa và Giê-ru-sa-lem trở về,"},
	{2, "2 thì ta sẽ nhóm hết thảy các nước lại, và đem chúng nó xuống trong trũng Giô-sa-phát. Tại đó, ta sẽ phán xét chúng nó vì cớ dân ta, và Y-sơ-ra-ên là sản nghiệp ta, mà chúng nó đã làm cho tan tác giữa các dân tộc; và chúng nó đã chia đất ta ra,"},
	{3, "3 và bắt thăm về dân ta; đã đổi con trai mà lấy đĩ, và bán con gái đặng lấy rượu để uống."},
	{4, "4 Hỡi Ty-rơ và Si-đôn, và hết thảy địa hạt của Phi-li-tin, các ngươi có quan hệ gì cùng ta? Các ngươi muốn báo trả ta, ta sẽ khiến sự báo trả đổ trên đầu các ngươi cách mau kíp và thình lình."},
	{5, "5 Vì các ngươi đã lấy bạc vàng ta đi, đã đem đồ châu báu rất quí của ta vào trong những đền miếu các ngươi."},
	{6, "6 Các ngươi đã bán con cái của Giu-đa và con cái của Giê-ru-sa-lem cho các con trai Gờ-réc, đặng làm cho chúng nó lìa khỏi bờ cõi mình."},
	{7, "7 nầy, ta sẽ giục chúng nó dấy lên từ nơi mà các ngươi đã bán chúng nó cho, và sẽ khiến sự báo trả các ngươi về trên đầu các ngươi."},
	{8, "8 Ta sẽ bán con trai con gái các ngươi và tay con cái Giu-đa, Giu-đa sẽ bán chúng nó cho người Sa-bê, là dân ở xa cách; vì Ðức Giê-hô-va đã phán vậy."},
	{9, "9 Hãy rao điều nầy ra giữa các nước: Khá sắm sửa sự đánh giặc, giục lòng những người mạnh bạo dấy lên. Hết thảy những lính chiến khá sấn tới, và xông vào trận!"},
	{10, "10 Hãy lấy lưỡi cày rèn gươm, lấy liềm rèn giáo. Kẻ yếu khá nói rằng: Ta là mạnh!"},
	{11, "11 Các ngươi hết thảy, là những nước xung quanh, hãy vội vàng đến, và hãy nhóm lại. Hỡi Ðức Giê-hô-va, xin khiến những người mạnh mẽ của Ngài xuống đó!"},
	{12, "12 Các nước khá dấy lên, khá lên trong trũng Giô-sa-phát, vì ở đó là nơi ta sẽ ngồi đặng phán xét hết thảy các dân tộc xung quanh."},
	{13, "13 Hãy tra lưỡi lái, vì mùa gặt đã chín. Hãy đến, hãy đạp, vì bàn ép đã đầy; các thùng đã tràn, vi tội ác chúng nó là lớn."},
	{14, "14 Ðoàn đông dường nào, đoàn đông dường nào tại trong trũng đoán định! Vì trong trũng đoán định, ngày Ðức Giê-hô-va đã gần."},
	{15, "15 Mặt trời và mặt trăng tối tăm, các ngôi sao thâu sự sáng lại."},
	{16, "16 Ðức Giê-hô-va gầm thét từ Si-ôn; Ngài là cho vang tiếng mình ra từ Giê-ru-sa-lem; các từng trời và đất đầu rúng động. Nhưng Ðức Giê-hô-va và nơi ẩn náu cho dân mình, là đồn lũy cho con cái Y-sơ-ra-ên."},
	{17, "17 Như vậy các ngươi sẽ biết ta là Giê-hô-va Ðức Chúa Trời các ngươi, ở tại Si-ôn, là núi thánh ta. Bấy giờ Giê-ru-sa-lem sẽ là thánh, và những người ngoại sẽ không đi qua trong đó nữa."},
	{18, "18 Trong ngày đó, các núi nhỏ rượu ngọt ra; sữa chảy ra từ các đồi, hết thảy các khe Giu-đa sẽ có nước chảy. Một cái suối sẽ ra từ nhà Ðức Giê-hô-va và tưới trũng Si-tim."},
	{19, "19 Ê-díp-tô sẽ trở nên hoang vu Ê-đôm thành ta đồng vắng hoang vu, vì cớ chúng nó lấy sự bạo ngược đãi con cái Giu-đa, và làm đổ máu vô tội ra trong đất mình."},
	{20, "20 Nhưng Giu-đa sẽ còn đời đời, Giê-ru-sa-lem sẽ còn đời nầy qua đời khác."},
	{21, "21 Ta sẽ làm sạch máu chúng nó mà ta chưa từng làm sạch; vì Ðức Giê-hô-va ngự trong Si-ôn."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};