#include <map>
#include <string>
class Bible34
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
	struct vi3	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Gánh nặng của Ni-ni-ve. Sách sự hiện thấy của Na-hum, người Eân-cốt."},
	{2, "2 Giê-hô-va là Ðức Chúa Trời ghen và báo thù; Ðức Giê-hô-va báo thù, và đầy sự thạnh nộ. Ðức Giê-hô-va báo thù kẻ cừu địch mình và cưu giận cho kẻ thù mình."},
	{3, "3 Ðức Giê-hô-va chậm giận và có quyền lớn; nhưng Ngài chẳng cầm kẻ mắc tội là vô tội. Ðức Giê-hô-va đi trong gió lốc và bão tố, những đám mây là bụi dưới chơn Ngài."},
	{4, "4 Ngài quở trách biển, làm cho nó khô; và làm cạn hết thảy các sông. Ba-san và Cạt-mên đều mòn mỏi, hoa của Li-ban đều héo rụng."},
	{5, "5 Các núi run rẩy vì cớ Ngài, các đồi tan chảy; đất và thế gian cũng dân cư trên đất đều dậy lên trước mặt Ngài."},
	{6, "6 Ai đứng được trước sự thạnh nộ Ngài? Ai đương nổi sự nóng giận Ngài? Sự tức giận của Ngài đổ ra như lửa, những vầng đá vỡ ra bởi Ngài."},
	{7, "7 Ðức Giê-hô-va là tốt lành, làm đồn lũy trong ngày hoạn nạn, và biết những kẻ ẩn náu nơi Ngài."},
	{8, "8 Ngài dùng nước lụt hủy diệt chỗ nó, và đuổi kẻ thù nghịch mình vào sự tối tăm."},
	{9, "9 Các ngươi sẽ lập mưu gì nghịch cùng Ðức Giê-hô-va? Ngài sẽ diệt hết cả, sẽ chẳng có tai nạn dậy lên lần thứ hai."},
	{10, "10 Vì chúng nó xỏ xen như gai gốc, và mê man như say rượu, thì sẽ bị thiêu hủy hết như rơm khô."},
	{11, "11 Ấy là từ ngươi mà ra một kẻ mưu sự dữ nghịch cùng Ðức Giê-hô-va, và toan sự gian ác."},
	{12, "12 Ðức Giê-hô-va phán như vầy: Dầu chúng nó sức mạnh đầy đủ và người đông cũng sẽ bị từ đi và trở nên hư không. Dầu ta đã làm khổ ngươi, song ta sẽ chẳng làm khổ ngươi nữa."},
	{13, "13 Nhưng bấy giờ ta sẽ bẻ gãy ách nó khỏi ngươi, và bứt dứt dây ngươi."},
	{14, "14 Ðức Giê-hô-va đã truyền lịnh về ngươi, từ danh ngươi chẳng sanh ra nữa. Ta sẽ trừ bỏ tượng chạm và tượng đúc khỏi nhà các thần ngươi; ta sẽ làm mồ mả cho ngươi, vì ngươi là hèn mạt."},
	{15, "15 Nầy, trên các núi có chơn của kẻ đem tin lành và rao sự bình an! Hỡi Giu-đa, ngươi nữa, nó sẽ bị diệt sạch."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Kẻ tàn phá đã đến nghịch cũng ngươi; hãy giữ đồn lũy, canh phòng đường sá, thắt chặt lưng ngươi, và thâu góp cả sức mạnh ngươi!"},
	{2, "2 Vì Ðức Giê-hô-va khôi phục sự vinh hiển của Gia-cốp như sự vinh hiển của Y-sơ-ra-ên, bởi những kẻ bóc lột đã bóc lột chúng nó, và đã phá hại những nhành nho chúng nó."},
	{3, "3 Thuẫn của những người mạnh là đỏ, những lính chiến nó mặc áo màu điều; trong ngày nó dàn trận, gang thép của xe cộ sẽ lòe ra như lửa, và gươm giáo đều lay động."},
	{4, "4 Những xe đi hỗn độn trong đường phố, và chạy đi chạy lại trong các đường rộng, thấy nó như những ngọn đuốc, và chạy như chớp!"},
	{5, "5 Nó nhớ lại những người mạnh dạn của mình, nhưng chúng nó bước đi vấp váp; chúng vội vả đến nơi vách thành nó, sắm sẵn để chống cự."},
	{6, "6 Các cửa sông mở ra, điên dại tan mất?"},
	{7, "7 Ðã nhứt định: hoàng hậu bị bóc lột và bị bắt đi; các nàng hầu hạ người đi theo, rên siếc như bò câu, và đấm ngực mình."},
	{8, "8 Vả, Ni-ni-ve, từ lâu nay nó đã như ao chứa nước. Nhưng bây giờ chúng nó chạy trốn! Có kẻ kêu rằng: Hãy đứng, hãy đứng! Song không ai quay lại!"},
	{9, "9 Hãy cướp bạc, cướp vàng; vì của báu nó vô ngần, mọi đồ đạc quí nhiều vô số."},
	{10, "10 Nó đã bị tàn phá, trống không, và hủy hoại hết; lòng chúng nó tan chảy; đầu gối chúng nó chạm nhau; mọi lưng đều đau, hết thảy mặt đều xám ngắt."},
	{11, "11 Nào cái hang của sư tử, và nơi mà sư tử con nuôi nấng, là nơi sư tử đực, sư tử cái và con nó bước đi, mà không ai làm cho chúng nó sợ hãi bây giờ ở đâu?"},
	{12, "12 Sư tử đực xé đủ mồi cho con nó, và bóp mồi cho sư tử cái, hang nó đầy mồi, ổ nó đầy của đã bắt được."},
	{13, "13 Ðức Giê-hô-va vạn quân phán rằng: Nầy, ta nghịch cùng ngươi, sẽ đốt xe cộ ngươi, và làm cho tan ra khỏi; gươm sẽ nuốt những sư tử con của ngươi; ta sẽ dứt mồi ngươi khỏi đất; và tiếng của những sứ giả ngươi sẽ không nghe nữa."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct vi3 poems[] = {
	{1, "1 Khốn thay cho thành đổ máu! Nó đầy dẫy những sự dối trá và cường bạo, cướp bóc không thôi."},
	{2, "2 Người ta nghe tăm roi, tiếng ầm của bánh xe; ngựa thì phóng đại, xe thì chạy mau."},
	{3, "3 Những lính kỵ sấn tới; gươm sáng lòe, giáo nhấp nháng. Có đoàn đông kẻ bị giết, có từng đống thây lớn, xác chết nhiều vô số! Người ta vấp ngã trên những thây!"},
	{4, "4 Ðó là vì cớ rất nhiều sự dâm đãng của con đĩ tốt đẹp khéo làm tà thuật ấy; nó bán các nước bởi sự dâm đãng, và bán các họ hàng bởi sự tà thuật."},
	{5, "5 Ðức Giê-hô-va vạn quân phán: Nầy, ta nghịch cũng ngươi; ta lột áo xống ngươi, phô bày sự trần truồng ngươi ra cho các dân tộc, và tỏ sự xấu hổ ngươi ra cho các nước."},
	{6, "6 Ta sẽ ném sự ô uế gớm ghiếc của ngươi trên ngươi, làm cho ngươi nên khinh hèn, làm trò cho mọi ngươi xem."},
	{7, "7 Xảy ra có ai thấy ngươi thì sẽ lánh xa và nói rằng: Ni-ni-ve đã hoang vu! Ai sẽ có lòng thương xót người? Ta bởi đâu tìm cho ngươi những kẻ yên ủi?"},
	{8, "8 Ngươi muốn hơn Nô-a-môn ở giữa các sông, nước bọc chung quanh, lấy biển làm đồn lũy, lấy biển làm vách thành hay sao?"},
	{9, "9 Ê-thi-ô-bi và Ê-díp-tô là sức mạnh vô cùng của nó. Phút và Li-by là kẻ cứu giúp ngươi."},
	{10, "10 Dầu vậy, chính nó cũng đã bị đày khỏi đất mình; bị bắt đi làm phu tù; con cái nó cũng đã bị nghiền nát nơi ngã ba các đường phố. Người ta đã ném thăm trên các kẻ cả nó, và các quan trưởng nó đều bị mang xiềng."},
	{11, "11 Ngươi cũng vậy, ngươi sẽ mê man vì say, sẽ được ẩn náu, và tìm nơi vững bền vì cớ kẻ thù."},
	{12, "12 Hết thảy những đồn lũy ngươi như cây vả có trái chín đầu mùa, hễ lung lay thì rụng xuống trong miệng của kẻ muốn ăn nó."},
	{13, "13 Nầy, dân sự ngươi là đờn bà ở giữa ngươi. Các cửa thành của đất ngươi sẽ mở rộng cho quân thù ngươi; lửa đã thiêu nuốt những then ngươi!"},
	{14, "14 Ngươi khá múc nước để phòng cơn vây hãm, làm vững chắc đồn lũy ngươi. Khá đạp bùn, nhồi đất sét, và xây lại lò gạch!"},
	{15, "15 Tại đó lửa sẽ thiêu ngươi, gươm sẽ diệt ngươi, nuốt ngươi như cào cào vậy, ngươi khá nhóm lại đông như cào cào, nhiều như châu chấu!"},
	{16, "16 Ngươi đã thêm kẻ buôn bán ngươi nhiều ra như sao trên trời; cào cào đã cắn phá hết rồi trốn đi."},
	{17, "17 Các quan trưởng ngươi như cào cào, các quan tướng ngươi như bầy châu chấu đậu trên hàng rào trong khi trời lạnh, đến chừng mặt trời mọc, nó đi mất, người ta không biết nó ở đâu."},
	{18, "18 Hỡi vua A-si-ri, những kẻ chăn của ngươi ngủ rồi! Những kẻ sang trọng của ngươi đều yên nghỉ; dân sự ngươi tan lạc nơi các núi, không ai nhóm họp chúng nó lại."},
	{19, "19 Vết thương ngươi không thuốc chữa, dấu vít ngươi rất hiểm nghèo; phàm kẻ nghe nói về ngươi đều vỗ tay trên ngươi; vì ai là kẻ chẳng từng chịu luôn sự hung ác của ngươi!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};