#include <map>
#include <string>
class Bible35
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
	struct vi3	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Nầy là gánh nặng mà đấng tiên tri Ha-ba-cúc đã xem thấy."},
	{2, "2 Hỡi Ðức Giê-hô-va! Tôi kêu van mà Ngài không nghe tôi cho đến chừng nào? Tôi vì sự bạo ngược kêu van cùng Ngài, mà Ngài chẳng khứng giải cứu tôi!"},
	{3, "3 Nhơn sao Chúa khiến tôi thấy sự gian ác, và Ngài nhìn xem sự ngang trái? Sự tàn hại bạo ngược ở trước mặt tôi; sự tranh đấu cãi lẫy dấy lên."},
	{4, "4 Vậy nên luật pháp không quyền, sự chánh trực không hề tỏ ra. Kẻ hung ác vây chung quanh ngươi công bình, vì cớ đó sự xét đoán ra trái ngược."},
	{5, "5 Hãy nhìn trong các nước và xem, hãy lấy làm lạ và sững sờ! Vì ta đang làm ra trong ngày các ngươi một việc, mà dầu có ai thuật lại cho ngươi, các ngươi cũng không tin."},
	{6, "6 Nầy, ta khiến người Canh-đê dấy lên, nó là một dân dữ tợn lung lăng, hay đi khắp đất được chiếm lấy những chỗ ở không thuộc về mình."},
	{7, "7 Nó thật là đáng sợ và đáng ghê, oai nghi và sự phán xét của nó đều là từ nó mà đến."},
	{8, "8 Những ngựa nó nhặm lẹ hơn con beo, và hung hơn muông sói ban đêm. Những lính kỵ nó tràn khắp đây đó, những lính kỵ nó đến từ phương xa; chúng nó bay như chim ưng lẹ, nôn nả kiếm ăn."},
	{9, "9 Cả dân ấy đến đặng làm sự bạo ngược; chúng nó mạnh dạn đi thẳng tối, và dồn phu tù lại như cát."},
	{10, "10 Nó nhạo cười các vua, chế báng các quan trưởng, và chê cười mỗi đồn lũy: nó đắp lũy, rồi chiếm lấy."},
	{11, "11 Bấy giờ nó sẽ sấn tới như gió thổi qua; vì nó lấy sức mạnh mình là thần mình, nên phạm tội trọng."},
	{12, "12 Hỡi Giê-hô-va, Ðức Chúa Trời tôi, Ðấng Thánh của tôi! Ngài há chẳng phải là từ đời đời vô cùng sao? Vậy chúng tôi sẽ không chết! Hỡi Ðức Giê-hô-va, Ngài đã lập dân nầy đặng làm sự xét đoán của Ngài. Hỡi vầng Ðá! Ngài đã đặt nó đặng làm sự sửa phạt!"},
	{13, "13 Mắt Chúa thánh sạch chẳng nhìn sự dữ, chẳng có thể nhìn được sự trái ngược. Sao Ngài nhìn xem kẻ l@ m sự dối trá, khi kẻ dữ nuốt người công bình hơn nó, sao Ngài nín lặng đi?"},
	{14, "14 Sao Ngài khiến loài người như cá biển và như loài côn trùng vô chủ?"},
	{15, "15 Nó đã dùng lưỡi câu móc lấy cả, thâu góp trong chài mình, và nhóm lại trong lưới mình, vậy nên nó vui mừng và lấy làm thích."},
	{16, "16 Vì cớ đó, nó dâng tế cho lưới, đốt hương cho chài, bởi chưng nó nhờ đó mà được phần béo tốt và của ăn dư dật."},
	{17, "17 Có lẽ nào nó cứ đổ lưới mình ra đặng làm sự giết lát các dân không hề thôi sao?"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Ta sẽ đứng nơi vọng canh, chôn chơn nơi đồn lũy, rình xem Ngài bảo ta điều gì, và ta trả lời thế nào về sự đối nại của ta."},
	{2, "2 Ðức Giê-hô-va đáp lại cũng ta mà rằng: Ngươi khá chép lấy sự hiện thấy, và rõ rệt nó ra trên bảng, hầu cho người đương chạy đọc được."},
	{3, "3 Vì sự hiện thấy còn phải ứng nghiệm trong kỳ nhứt định, sau cùng nó sẽ kíp đến, không phỉnh dối đâu; nếu nó chậm trễ, ngươi hãy đợi; bởi nó chắc sẽ đến, không chậm trễ."},
	{4, "4 Nầy, lòng người kiêu ngạo, không có sự ngay thẳng trong nó; song người công bình thì sống bởi đức tin mình."},
	{5, "5 Nó ghiền rượu, làm sự dối trá, kiêu ngạo và chẳng ở yên chỗ mình, mở rộng lòng ham mê nó như âm phủ, như sự chết chẳng được no chán; nó thâu góp mọi nước và hội hiệp mọi dân cho mình."},
	{6, "6 Chớ thì những kẻ đó há chẳng lấy thí dụ nhạo cười nó, lấy lời kín biếm nhẻ nó mà rằng: Khốn thay cho kẻ nhóm góp của chẳng thuộc về mình! Nó gánh vác của cầm rất nặng cho đến chừng nào?"},
	{7, "7 Há chẳng sẽ có kẻ vụt dấy lên đặng cắn ngươi sao? Kẻ đuổi bắt ngươi há chẳng tỉnh thức sao? Ngươi sẽ bị nó cướp bóc."},
	{8, "8 Vì ngươi đã cướp nhiều nước, thì cả phần sót lại của các dân sẽ cướp ngươi, vì cớ huyết người ta, vì sự bạo ngược làm ra cho đất, cho thành, và cho hết thảy dân cư nó."},
	{9, "9 Khốn thay cho kẻ tìm lợi bất nghĩa cho nhà mình, để lót ổ mình trong nơi cao, để được cứu khỏi tay hung ác."},
	{10, "10 Ngươi đã dùng mưu xấu hổ cho nhà mình mà diệt nhiều dân tộc, ngươi đã phạm tội nghịch cùng linh hồn ngươi."},
	{11, "11 Vì đá trong vách sẽ kêu lên, rường trên mái sẽ đáp lại."},
	{12, "12 Khốn thay cho kẻ lấy huyết dựng ấp, và lấy sự gian ác xây thành!"},
	{13, "13 Phải, ấy há chẳng phải bởi Ðức Giê-hô-va vạn quân mà các dân làm việc cho lửa, và các nước nhọc nhằn cho sự hư không hay sao?"},
	{14, "14 Vì sự nhận biết vinh quang Ðức Giê-hô-va sẽ đầy dẫy khắp đất như nước đầy tràn biển."},
	{15, "15 Khốn thay cho kẻ pha đồ độc cho người lân cận mình uống, làm cho nó say, đặng xem sự lõa lồ nó!"},
	{16, "16 Ngươi đầy sự sỉ nhục mà chẳng đầy sự vinh hiển. Ngươi cũng hãy uống đi, và làm như người chưa chịu phép cắt bì. Chén của Ðức Giê-hô-va cầm trong tay hữu sẽ đến phiên trao cho ngươi, và sự nhuốc nha sẽ đổ ra trên sự vinh hiển ngươi."},
	{17, "17 Vì sự bạo ngược đã làm ra cho Li-ban sẽ che phủ ngươi, và sự tàn hại các loài thú làm cho nó kinh sợ, lại vì cớ huyết người ta, vì sự bạo ngược làm ra cho đất, cho thành, và cho hết thảy dân cư nó."},
	{18, "18 Tượng chạm mà thợ đã chạm ra, có ích gì cho nó chăng? Tượng đúc, là thầy giả dối, thi có ích gì, mà người thợ làm thần tượng câm ấy lại tin cậy nơi việc mình làm ra?"},
	{19, "19 Khốn tay cho kẻ nói với gỗ rằng: Hãy tỉnh thức! và với đá câm rằng: Hãy chổi dậy! Nó có dạy dỗ được chăng? nầy, nó là bọc vàng và bạc, song chẳng có một chút hơi thổ nào ở giữa nó."},
	{20, "20 Nhưng Ðức Giê-hô-va ở trong đền thánh của Ngài, trước mặt Ngài, cả đất hãy làm thinh!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct vi3 poems[] = {
	{1, "1 Lời cầu nguyện của đấng tiên tri Ha-ba-cúc, về thể thi ca."},
	{2, "2 Hỡi Ðức Giê-hô-va! Tôi đã nghe danh tiếng Ngài thì tôi sợ hãi. Hỡi Ðức Giê-hô-va! xin Ngài lại khiến công việc Ngài dấy lên giữa các năm, Tỏ ra cho biết giữa các năm; Khi Ngài đương giận, xin hãy nhớ lại sự thương xót!"},
	{3, "3 Ðức Chúa Trời đến từ Thê-man, Ðấng Thánh đến từ núi Pha-ran. Vinh hiển Ngài bao phủ các từng trời, Ðất đầy sự khen ngợi Ngài."},
	{4, "4 Sự chói rạng của Ngài như ánh sáng, Những tia sáng ra từ Ngài, Quyền năng Ngài giấu trong nơi đó."},
	{5, "5 Ôn dịch đi trước mặt Ngài, Tên lửa ra nơi chơn Ngài."},
	{6, "6 Ngài đứng và đo đất; Ngài nhìn xem, làm tan tác các dân tộc; Các núi hằng còn đều tan nát, Các đồi đời đời đều quì xuống: Các đường lối Ngài giống như thuở xưa."},
	{7, "7 Ta thấy những trại của Cu-san bị hoạn nạn, Những màn cháng của đất Ma-đi-an lung lay."},
	{8, "8 Có phải Ðức Giê-hô-va nổi giận nghịch cùng các sông chăng? Cơn giận Ngài có phải nổi lên nghịch cùng các sông chăng? Có phải sự thạnh nộ Ngài nổi lên nghịch cũng biển, Khi Ngài cỡi ngựa cỡi xe đặng giải cứu chăng?"},
	{9, "9 Cung Ngài ra khỏi bao; Lời thề cũng các chi phái là lời chắc chắn. Ngài phân rẽ đất làm cho sông chảy ra."},
	{10, "10 Các núi thấy Ngài thì đều sợ hãi; Các dòng nước chảy mạnh, Vực sâu vang tiếng, Rún biển giơ tay lên cao."},
	{11, "11 Mặt trời mặt trăng dừng lại trong nơi ở nó; Vì cớ bóng sáng của những tên Ngài, Và chớp nháng của gươm giáo Ngài chiếu ra."},
	{12, "12 Ngài nổi giận bước qua đất; Và đương cơn thạnh nộ giày đạp các dân tộc."},
	{13, "13 Ngài đi ra đặng cứu dân mình, Ði ra đặng cứu đấng chịu xức dầu của mình; Ngài đánh thương đầu kẻ ác, phá nhà nó, Làm trần trụi nền nó cho đến cổ."},
	{14, "14 Ngài lấy giáo nó đâu đầu lính chiến nó, Chúng nó đến như trận bão đặng làm tan tác tôi; Chúng nó vui thích như nuốt kẻ bần cùng cách kín giấu."},
	{15, "15 Ngài cỡi ngựa đạp trên biển, Trong đống nước lớn."},
	{16, "16 Tôi đã nghe, thì thân thể tôi run rẩy, Nghe tiếng Ngài thì môi tôi rung động; Sự mục nát nhập vào xương tôi; và tôi run rẩy trong chỗ tôi, Vì tôi phải nín lặng để chờ ngày hoạn nạn, Là ngày có dân đến xâm chiếm chúng tôi."},
	{17, "17 Vì dầu cây vả sẽ không nứt lộc nữa, Và sẽ không có trái trên những cây nho; Cây ô-li-ve không sanh sản, đồ ăn; Bầy chiên sẽ bị dứt khỏi ràn, Và không có bầy bò trong chuồng nữa."},
	{18, "18 Dầu vậy, tôi sẽ vui mừng trong Ðức Giê-hô-va, Tôi sẽ hớn hở trong Ðức Chúa Trời của sự cứu rỗi tôi."},
	{19, "19 Ðức Giê-hô-va là Chúa, là sức mạnh của tôi, Ngài làm cho chơn tôi giống như chơn con hươu, Khiến tôi đi trên các nơi cao của mình. (Phó cho quản phường nhạc, khảy đờn mà hát bài nầy)."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};