#include <map>
#include <string>
class Bible36
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
	struct vi3	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Nầy là lời Ðức Giê-hô-va phán cùng Sô-phô-ni, con trai Cu-si cháu Ghê-đa-lia, chắt A-ma-ria, chít Ê-xê-chia, về đời Giô-si-a, con trai A-môn, vua Giu-đa."},
	{2, "2 Ta sẽ diệt sạch mọi sự khỏi mặt đất, Ðức Giê-hô-va phán vậy."},
	{3, "3 Ta sẽ diệt loài người và loài thú, chim trời và cá biển, cùng mọi sự ngăn trở với kẻ dữ; và ta sẽ trừ tiệt loài người khỏi mặt đất, Ðức Giê-hô-va phán vậy."},
	{4, "4 Ta sẽ giang tay ta trên Giu-đa và trên hết thảy dân cư Giê-ru-sa-lem; sẽ trừ tiệt phần sót lại của Ba-anh, các tên của thầy cả và thầy tế lễ nó khỏi nơi nầy."},
	{5, "5 Ta sẽ diệt những kẻ lạy cơ binh trên trời nơi nóc nhà; và những kẻ thờ lạy, những kẻ thề với Ðức Giê-hô-va và cũng thề bởi vua mình;"},
	{6, "6 cùng với những kẻ xây bỏ không theo Ðức Giê-hô-va, và những kẻ không tìm kiếm Ðức Giê-hô-va, không cầu hỏi Ngài."},
	{7, "7 Hãy nín lặng, trước mặt Chúa Giê-hô-va! Vì ngày của Ðức Giê-hô-va đã gần, Ðức Giê-hô-va đã sửa soạn của lễ, đã biệt riêng ra tân khách Ngài."},
	{8, "8 Trong ngày tế lễ của Ðức Giê-hô-va, ta sẽ phạt các quan trưởng và các con trai của vua, và hết thảy những kẻ mặc áo lạ."},
	{9, "9 Trong ngày đó, ta sẽ phạt hết thảy những kẻ nhảy qua ngạch cửa, và những kẻ làm đầy dẫy sự bạo ngược và sự quỷ quyệt trong nhà chủ mình."},
	{10, "10 Ðức Giê-hô-va phán: Trong ngày đó sẽ có tiếng kêu lớn nơi cửa cá, tiếng la lối nơi phố thứ hai, tiếng vỡ lở lớn nơi các đồi."},
	{11, "11 Hỡi dân cư trong thành thấp, hãy than khóc; vì mọi kẻ buôn bán đã mòn mỏi, và mọi kẻ chở bạc đi đã bị diệt mất."},
	{12, "12 Trong lúc đó, ta sẽ dùng đèn đuốc lục soát trong Giê-ru-sa-lem, sẽ phạt những kẻ đọng trong cặn rượu và tự nói trong lòng mình rằng: Ðức Giê-hô-va sẽ chẳng xuống phước cũng chẳng xuống họa."},
	{13, "13 Của cải chúng nó sẽ bị cướp, nhà cửa chúng nó sẽ hoang vu; chúng nó sẽ xây nhà mà không được ở, trồng nho mà không được uống rượu."},
	{14, "14 Ngày lớn của Ðức Giê-hô-va đã gần, đã gần rồi; nó đến rất kíp. Ngày của Ðức Giê-hô-va có tiếng động; bấy giờ người mạnh dạn sẽ kêu khóc đắng cay."},
	{15, "15 Ngày ấy là ngày thạnh nộ, ngày hoạn nạn và buồn rầu, ngày hủy phá và hoang vu, ngày tối tăm và mờ mịt, ngày mây và sương mù,"},
	{16, "16 ngày mà tiếng kèn và tiếng báo giặc nghịch cùng các thành bền vững và các tháp cao góc thành."},
	{17, "17 Ta sẽ đem sự hoạn nạn trên loài người, chúng nó sẽ đi như kẻ mù, vì đã phạm tội nghịch cùng Ðức Giê-hô-va; máu chúng nó sẽ đổ ra như bụi, và thịt như phân."},
	{18, "18 Hoặc bạc hoặc vàng của chúng nó, đều không có thể giải cứu chúng nó trong ngày thạnh nộ của Ðức Giê-hô-va; nhưng cả đất nầy sẽ bị lửa ghen Ngài thiêu nuốt; vì Ngài sẽ diệt hết dân cư đất nầy cách thình lình."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Hỡi dân chẳng biết xấu hổ! hãy nhóm hiệp lại, phải, hãy nhóm hiệp lại,"},
	{2, "2 trước khi mạng lịnh chưa ra, ngày giờ chưa qua như trấu, trước khi sự nóng giận của Ðức Giê-hô-va chưa đến trên các ngươi, trước khi ngày thạnh nộ của Ðức Giê-hô-va chưa đến trên các ngươi."},
	{3, "3 Hỡi các ngươi là mọi kẻ nhu mì của đất, làm theo mạng lịnh của Chúa, hãy tìm kiếm Ðức Giê-hô-va, tìm kiếm sự công bình, tìm kiếm sự nhu mì, hoặc giả các ngươi sẽ được giấu kín trong ngày thạnh nộ của Ðức Giê-hô-va."},
	{4, "4 Thật vậy, Ga-xa sẽ bỏ hoang, Ách-ca-lôn sẽ hoang vu; người ta sẽ đuổi dân cư Ách-đốt đương lúc giữa trưa, và Éc-rôn sẽ bị nhổ đi."},
	{5, "5 Khốn thay cho dân cư ở miền biển, tức là dân tộc Cơ-rết! Hỡi Ca-na-an là đất của người Phi-li-tin, lời của Ðức Giê-hô-va cũng nghịch cùng các ngươi; ta sẽ hủy diệt ngươi cho đến không còn dân cư nữa."},
	{6, "6 Miền biển sẽ trở nên đồng cỏ, với những lều của kẻ chăn và chuồng của bầy chiên."},
	{7, "7 Miền ấy sẽ thuộc về phần sót lại của nhà Giu-đa; chúng nó sẽ chăn trong các nơi đó, ban tối sẽ nghỉ trong các nhà của Ách-ca-lôn; vì Giê-hô-va Ðức Chúa Trời chúng nó sẽ thăm viếng chúng nó, và đem phu tù chúng nó trở về."},
	{8, "8 Ta đã nghe lời Mô-áp chế báng, lời con cái Am-môn sỉ nhục, chúng nó xỉ vả dân ta, khoe mình nghịch cùng bờ cõi nó."},
	{9, "9 Vậy nên, Ðức Giê-hô-va vạn quân, tức là Ðức Chúa Trời của Y-sơ-ra-ên, phán rằng: Thật như ta hằng sống, Mô-áp chắc sẽ giống như Sô-đôm và con cái Am-môn sẽ giống như Gô-mô-rơ, thành ra một nơi đầy gai gốc, một hầm muối, một chỗ hoang vu đời đời; những kẻ sống sót của dân ta sẽ cướp bóc chúng nó, phần sót lại của dân ta sẽ được chúng nó làm sản nghiệp."},
	{10, "10 Ðiều đó xảy đến cho chúng nó vì chúng nó kiêu ngạo, xỉ vả và khoe mình nghịch cùng dân của Ðức Giê-hô-va vạn quân."},
	{11, "11 Ðức Giê-hô-va sẽ tỏ ra là đáng sợ cho chúng nó; vì Ngài sẽ làm cho các thần trên đất chịu đói; và người ta ai nầy sẽ từ nơi mình thờ lạy Ngài, dầu hết thảy các cù lao các nước cũng vậy."},
	{12, "12 Hỡi dân cư Ê-thi-ô-bi, các ngươi cũng sẽ bị đâm bởi gươm ta."},
	{13, "13 Ðoạn, Ðức Giê-hô-va sẽ giang tay về phía bắc, sẽ hủy diệt A-si-ri, làm cho Ni-ni-ve hoang vu, thành ra một chỗ khô khan như đồng vắng."},
	{14, "14 Những bầy thú vật sẽ nằm ở giữa nó; hết thảy các loài thú khác, cả đến chim bò nông và con nhím cũng ở trên những đầu trụ nó. Tiếng chim kêu nghe nơi cửa sổ, sự hoang vu ở nơi ngạch cửa, và những mái gỗ bá hương đã bị dỡ đi."},
	{15, "15 Kìa, thành vui vẻ nầy đã ở yên không lo lắng và nói trong lòng mình rằng: Ta đây, ngoài ta không còn ai hết! Nó đã trở nên hoang vu, làm chỗ nằm cho loài thú vật là dường nào! Phàm kẻ đi qua sẽ khoa tay mà nhạo cười!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct vi3 poems[] = {
	{1, "1 Khốn thay cho thành bạn nghịch và ô uế, làm sự bạo ngược!"},
	{2, "2 Nó không nghe lời; không chịu sửa dạy; không nhờ cậy Ðức Giê-hô-va; không đến gần Ðức Chúa Trời mình."},
	{3, "3 Các quan trưởng ở giữa nó như sư tử gầm thét, các quan xét nó như muông sói ban đêm, không để dành chi lại đến sáng mai."},
	{4, "4 Các kẻ tiên tri nó đều là càn dỡ dối trá; các thầy tế lễ nó làm ô uế nơi thánh, và làm quanh quẹo luật pháp."},
	{5, "5 Ðức Giê-hô-va ở giữa nó là công bình, chẳng hề làm sự gian ác; mỗi buổi mai Ngài tỏ sự công bình mình ra giữa sự sáng, chẳng hề cách dứt; song kẻ bất nghĩa chẳng biết xấu hổ."},
	{6, "6 Ta đã diệt các dân tộc, tháp góc thành chúng nó đều hoang vu. Ta làm cho phố chợ nó ra vắng vẻ, đến nỗi không ai đi qua. Thành nó đã bị phá diệt, không còn một người nào, và chẳng có ai ở đó."},
	{7, "7 Ta đã bảo rằng: Ngươi chỉ nên kính sợ ta, chịu sửa dạy, thì chỗ ở nó sẽ không bị dứt đi, theo mọi sự ta đã định về nó. Nhưng chúng nó chổi dậy sớm, làm bại hoại mọi công việc mình."},
	{8, "8 Vậy nên, Ðức Giê-hô-va phán: Các ngươi khá đợi ta cho đến ngày ta sẽ dấy lên đặng cướp bắt; vì ta đã định thâu góp các dân tộc và nhóm hiệp các nước, hầu cho ta đổ sự thạnh nộ và cả sự nóng giận ta trên chúng nó, và cả đất sẽ bị nuốt bởi lửa ghen ta."},
	{9, "9 Vì bấy giờ ta sẽ ban môi miếng thanh sạch cho các dân, hầu cho chúng nó thảy đều kêu cầu danh Ðức Giê-hô-va, đặng một lòng hầu việc Ngài."},
	{10, "10 Những kẻ cầu khẩn ta, tức là con gái của kẻ tan lạc ta ở bên kia các sông Ê-thi-ô-bi, sẽ đem dâng lễ vật cho ta."},
	{11, "11 Trong ngày đó, ngươi sẽ không còn xấu hổ vì cớ mọi việc mình làm, là những việc đã phạm tội nghịch cùng ta; vì bấy giờ ta sẽ cất bỏ kẻ vui sướng kiêu ngạo khỏi giữa ngươi; rày về sau ngươi sẽ không còn kiêu ngạo trên núi thánh ta nữa."},
	{12, "12 Ta sẽ để sót lại ở giữa ngươi một dân khốn cùng nghèo thiếu, nó sẽ ẩn náu trong danh Ðức Giê-hô-va."},
	{13, "13 Những kẻ sót lại của Y-sơ-ra-ên sẽ không làm sự gian ác, không nói dối, không có lưỡi phỉnh gạt trong miệng chúng nó; vì chúng nó sẽ ăn và nằm ngủ, không ai làm cho kinh hãi."},
	{14, "14 Hỡi con gái Si-ôn, hãy hát! Hỡi Y-sơ-ra-ên, hãy kêu la! Hỡi con gái Giê-ru-sa-lem, hãy hết lòng mừng rỡ và vui thích!"},
	{15, "15 Ðức Giê-hô-va đã trừ bỏ sự phán xét ngươi, khiến kẻ thù nghịch cách xa ngươi; Vua của Y-sơ-ra-ên, tức là Ðức Giê-hô-va, ở giữa ngươi, ngươi sẽ không gặp tai vạ nữa."},
	{16, "16 Trong ngày đó, người ta sẽ nói cùng Giê-ru-sa-lem rằng: Hỡi Si-ôn! chớ sợ, tay ngươi chớ yếu đuối!"},
	{17, "17 Giê-hô-va Ðức Chúa Trời ngươi ở giữa ngươi; Ngài là Ðấng quyền năng sẽ giải cứu ngươi: Ngài sẽ vui mừng cả thể vì cớ ngươi; vì lòng yêu thương mình, Ngài sẽ nín lặng; và vì cớ ngươi Ngài sẽ ca hát mừng rỡ."},
	{18, "18 Ta sẽ nhóm lại những kẻ đương buồn rầu, vì cớ hội lớn; chúng nó đã ra từ ngươi, lấy sự xấu hổ làm gánh nặng."},
	{19, "19 Nầy, trong lúc đó, ta sẽ diệt mọi kẻ làm cho ngươi buồn rầu; ta sẽ cứu kẻ què, thâu kẻ đã bị đuổi, làm cho chúng nó được khen ngợi và nổi danh tiếng, tức là những kẻ đã bị sỉ nhục trong cả đất."},
	{20, "20 Trong lúc đó, ta sẽ đem các ngươi trở về, trong lúc đó ta sẽ nhóm các ngươi lại; vì ta sẽ làm cho các ngươi nổi danh tiếng và được khen lao giữa mọi dân trên đất, khi ta đem phu tù các ngươi về trước mặt các ngươi, Ðức Giê-hô-va có phán vậy."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};