#include <map>
#include <string>
class Bible37
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Năm thứ hai đời vua Ða-ri-út, ngày mồng một tháng sáu, có lời của Ðức Giê-hô-va cậy đấng tiên tri A-ghê phán cho Xô-rô-ba-bên, con trai Sa-la-thi-ên, quan trấn thủ xứ Giu-đê, và cho Giê-hô-sua, con trai Giô-xa-đác, thầy tế lễ cả, mà rằng:"},
	{2, "2 Ðức Giê-hô-va vạn quân có phán như vầy: Dân nầy nói rằng: Thì giờ chưa đến, tức là thì giờ xây lại nhà Ðức Giê-hô-va."},
	{3, "3 Vậy nên có lời của Ðức Giê-hô-va phán ra bởi đấng tiên tri A-ghê rằng:"},
	{4, "4 Nay có phải là thì giờ các ngươi ở trong nhà có trần ván, khi nhà nầy hoang vu sao?"},
	{5, "5 Vậy bây giờ Ðức Giê-hô-va vạn quân phán như vầy: Các ngươi khá xem xét đường lối mình."},
	{6, "6 Các ngươi gieo nhiều mà gặt ít; ăn mà không no; uống mà không đủ; mặc mà không ấm; và kẻ nào làm thuê, đựng tiền công mình trong túi lủng."},
	{7, "7 Ðức Giê-hô-va vạn quân phán như vầy: Các ngươi khá xem xét đường lối mình."},
	{8, "8 Hãy lên núi, đem gỗ về, và xây nhà nầy, thì ta sẽ lấy nó làm vui lòng, và ta sẽ được sáng danh, Ðức Giê-hô-va phán vậy."},
	{9, "9 Các ngươi trông nhiều mà được ít; các ngươi đem vào nhà, thì ta đã thổi lên trên. Ðức Giê-hô-va vạn quân phán: Ấy là tại làm sao? Ấy là tại nhà ta thì hoang vu, mà các ngươi ai nấy lo xây nhà mình."},
	{10, "10 Cho nên, vì cớ các ngươi, trời giữ móc lại, và đất giữ bông trái lại."},
	{11, "11 Ta đã gọi cơn hạn hán đến trên đất, trên các núi, trên lúa mì, trên rượu mới, trên dầu, và trên sản vật đất sanh ra, trên loài người, trên loài vật, và trên mọi việc tay làm."},
	{12, "12 Vậy Xô-rô-ba-bên, con trai Sa-la-thi-ên, và Giê-hô-sua, con trai Giô-xa-đác, thầy tế lễ cả, cùng cả dân sót lại vâng theo tiếng của Giê-hô-va Ðức Chúa Trời mình, và lời của đấng tiên tri A-ghê mà Giê-hô-va Ðức Chúa Trời mình đã sai đến, thì dân sự đều sợ sệt trước mặt Ðức Giê-hô-va."},
	{13, "13 A-ghê, sứ giả của Ðức Giê-hô-va bèn nói cùng dân sự theo lịnh truyền của Ðức Giê-hô-va, mà rằng: Ta ở cùng các ngươi, Ðức Giê-hô-va phán vậy."},
	{14, "14 Ðoạn, Ðức Giê-hô-va giục lòng Xô-rô-ba-bên, con trai Sa-la-thi-ên, quan trấn thủ Giu-đa, và giục lòng Giê-hô-sua, con trai Giô-xa-đác, thầy tế lễ cả, và giục lòng cả dân sự sót lại; họ đều đến, làm việc nơi nhà Ðức Giê-hô-va vạn quân, là Ðức Chúa Trời mình."},
	{15, "15 Ấy là ngày hai mươi bốn tháng sáu về năm thứ hai đời vua Ða-ri-út."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Ngày hai mươi mốt tháng bảy có lời của Ðức Giê-hô-va phán ra bởi đấng tiên tri A-ghê mà rằng:"},
	{2, "2 Bây giờ hãy nói cùng Xô-rô-ba-bên, con trai Sa-la-thi-ên, quan trấn thủ Giu-đa, và Giê-hô-sua, con trai Giô-xa-đác, thầy tế lễ cả, và dân sự sót lại, bảo chúng nó rằng:"},
	{3, "3 Ai là kẻ còn sót lại ở trong các ngươi đã xem thấy vinh quang khi trước của nhà nầy? và bây giờ các ngươi xem nó ra làm sao? Nó ở trên mắt các ngươi, há chẳng phải như là hư không sao?"},
	{4, "4 Vậy bây giờ, Ðức Giê-hô-va phán: Hỡi Xô-rô-ba-bên, ngươi khá can đảm; còn ngươi, Giê-hô-sua, con trai Giô-xa-đác, thầy tế lễ cả, cũng khá can đảm, và hãy làm việc; vì ta ở cùng các ngươi, Ðức Giê-hô-va vạn quân phán vậy."},
	{5, "5 Lời giao ước mà ta lập với các ngươi khi các ngươi ra khỏi Ê-díp-tô cùng Thần ta ở giữa các ngươi: chớ sợ hãi."},
	{6, "6 Vì Ðức Giê-hô-va vạn quân phán như vầy: Còn một lần, khỏi ít lâu nữa, ta sẽ lam rúng động các từng trời và đất, biển và đất khô."},
	{7, "7 Ta cũng làm rúng động hết thảy các nước hầu đến; rồi ta sẽ làm cho vinh quang đầy dẫy nhà nầy, Ðức Giê-hô-va vạn quân phán vậy."},
	{8, "8 Bạc là của ta, vàng là của ta, Ðức Giê-hô-va vạn quân phán vậy."},
	{9, "9 Vinh quang sau rốt của nhà nầy sẽ lớn hơn vinh quang trước, Ðức Giê-hô-va vạn quân phán vậy; và ta sẽ ban sự bình an trong chốn nầy, Ðức Giê-hô-va vạn quân phán vậy."},
	{10, "10 Ngày hai mươi bốn tháng chín về năm thứ hai của Ða-ri-út, có lời của Ðức Giê-hô-va phán ra bởi đấng tiên tri A-ghê, mà rằng:"},
	{11, "11 Ðức Giê-hô-va vạn quân phán như vầy: Bây giờ hãy hỏi các thầy tế lễ về luật pháp rằng:"},
	{12, "12 Nếu có kẻ gói thịt thánh trong chéo áo, và chéo áo mình đụng đến bánh, hoặc vật gì nấu chín, hoặc rượu, hoặc dầu, hay là đồ ăn gì, thì vậy đó có nên thánh không? Các thầy tế lễ đều đáp rằng: Không."},
	{13, "13 A-ghê lại rằng: Nếu ai bị ô uế vì thây chết đụng đến các vật ấy, thì các vật ấy có lây ô uế không? Các thầy tế lễ trả lời rằng: Các vật ấy sẽ bị ô uế."},
	{14, "14 A-ghê bèn nói rằng: Ðức Giê-hô-va phán: Dân nầy nước nầy ở trước mặt ta cũng vậy; cả công việc tay chúng nó làm ra cũng vậy; vật chúng nó dâng tại đây cũng bị ô uế."},
	{15, "15 Vậy bây giờ, ta xin các ngươi khá suy nghĩ, từ ngày nầy về trước, khi chưa đặt hòn đá nầy trên hòn đá kia nơi đền thờ Ðức Giê-hô-va."},
	{16, "16 Trải qua cả lúc đó, khi người ta đến một đống lúa đáng được hai mươi lường, chỉ được có mười; và khi người ta đến bàn ép rượu, đáng được năm mươi lường, chỉ được có hai mươi."},
	{17, "17 Ta đã dùng những hạn hán, ten rét, mưa đá đánh phạt các ngươi trong mọi việc tay các ngươi làm, mà các ngươi không trở lại cùng ta, Ðức Giê-hô-va phán vậy."},
	{18, "18 Bây giờ hãy suy nghĩ; từ ngày nay về trước, tức là từ ngày hai mươi bốn tháng chín, từ ngày đặt nền của đền thờ Ðức Giê-hô-va; hãy suy nghĩ điều đó!"},
	{19, "19 Các ngươi còn có hột giống nơi hầm vựa không? Dầu cho đến cây nho, cây vả, cây lựu, cây ô-li-ve, cũng không có cây nào ra trái. Nhưng từ ngày nầy, ta sẽ ban phước cho các ngươi."},
	{20, "20 Lại có lời Ðức Giê-hô-va phán lần thứ hai cho A-ghê trong ngày hai mươi bốn tháng ấy, rằng:"},
	{21, "21 Hãy nói cùng Xô-rô-ba-bên, quan trấn thủ Giu-đa, mà rằng: Ta sẽ làm rúng động các từng trời và đất;"},
	{22, "22 Ta sẽ lật đổ ngôi của các nước, diệt quyền thế các nước của các dân; ta sẽ đánh đổ các xe cộ và người ngồi ở trên; các ngựa và người cỡi, ai nấy sẽ bị đánh ngã bởi gươm của anh em mình."},
	{23, "23 Ðức Giê-hô-va vạn quân phán: Hỡi Xô-rô-ba-bên, con trai Sa-la-thi-ên, trong ngày đó, ta sẽ lấy ngươi làm đầy tớ ta! Ðức Giê-hô-va phán: Ta sẽ đặt ngươi như ấn tín; vì ta đã chọn ngươi, Ðức Giê-hô-va vạn quân phán vậy."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};