#include <map>
#include <string>
class Bible39
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
	struct vi3	{ int val; const char *msg; };
	struct vi4	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Gánh nặng lời Ðức Giê-hô-va cho Y-sơ-ra-ên bởi Ma-la-chi."},
	{2, "2 Ðức Giê-hô-va có phán: Ta yêu các ngươi; và các ngươi nói rằng: Chúa yêu chúng tôi ở đâu? Ðức Giê-hô-va phán: Ê-sau há chẳng phải là anh Gia-cốp sao?"},
	{3, "3 Nhưng ta yêu Gia-cốp, mà ghét Ê-sau, ta làm cho những núi nó nên hoang vu, và phó sản nghiệp nó cho những chó nơi đồng vắng."},
	{4, "4 Nếu Ê-đôm nói rằng: Chúng ta đã bị hủy diệt, song chúng ta sẽ trở về dựng lại những nơi đổ nát, thì Ðức Giê-hô-va vạn quân cũng phán rằng: Chúng nó sẽ dựng lại, nhưng ta sẽ đổ xuống, người ta sẽ gọi chúng nó là Cõi độc ác, và là dân mà Ðức Giê-hô-va nổi giận nghịch cùng đời đời."},
	{5, "5 Mắt các ngươi sẽ thấy, và các ngươi sẽ nói rằng: Nguyền Ðức Giê-hô-va là lớn ngoài cõi Y-sơ-ra-ên!"},
	{6, "6 Con trai tôn kính cha mình, đầy tớ tôn kính chủ mình. Vậy nếu ta là cha, nào sự tôn kính thuộc về ta ở đâu? và nếu ta là chủ, nào sự kính sợ ta ở đâu? Ðức Giê-hô-va vạn quân phán vậy cùng các ngươi, hỡi các thầy tế lễ khinh dể danh ta! Các ngươi lại nói rằng: Chúng tôi có khinh dể danh Ngài ở đâu?"},
	{7, "7 Các ngươi dâng bánh ô uế trên bàn thờ ta, rồi các ngươi nói rằng: Chúng tôi có làm ô uế Ngài ở đâu? Ấy là ở điều các ngươi nói rằng: Bàn của Ðức Giê-hô-va đáng khinh dể."},
	{8, "8 Khi các ngươi dâng một con vật mù làm của lễ, điều đó há chẳng phải là dữ sao? Khi các ngươi dâng một con vật què hoặc đau, điều đó há chẳng phải là dữ sao? Thử dân nó cho quan trấn thủ ngươi, thì người há đẹp lòng và vui nhận cho ngươi sao? Ðức Giê-hô-va vạn quân phán như vậy."},
	{9, "9 Các ngươi đã làm điều đó, nay ta xin các ngươi hãy nài xin ơn Ðức Chúa Trời, hầu cho Ngài làm ơn cho chúng ta, thì Ngài há sẽ nhận một người nào trong các ngươi sao? Ðức Giê-hô-va vạn quân phán vậy."},
	{10, "10 Ước gì trong các ngươi có một người đóng các cửa, hầu cho các ngươi không nhen lửa vô ích nơi bàn thờ ta. Ðức Giê-hô-va vạn quân phán: Ta chẳng lấy làm vui lòng nơi các ngươi, và ta chẳng nhận nơi tay các ngươi một của dâng nào hết."},
	{11, "11 Vì từ nơi mặt trời mọc cho đến nơi mặt trời lặn, danh ta sẽ là lớn giữa các dân ngoại. Trong khắp mọi nơi, người ta sẽ dâng hương và của lễ thanh sạch cho danh ta; vì danh ta sẽ là lớn giữa các dân ngoại, Ðức Giê-hô-va vạn quân phán vậy."},
	{12, "12 Song các ngươi đã làm uế tục danh ta mà rằng: Bàn của Ðức Giê-hô-va là ô uế, đồ ăn đến từ trên nó là đáng khinh dể."},
	{13, "13 Các ngươi lại nói rằng: Ôi! việc khó nhọc là dường nào! rồi các ngươi khinh dể nó, Ðức Giê-hô-va vạn quân phán vậy! Các ngươi đem đến vật bị cướp, vật què và đang, đó là của các ngươi đem dâng cho ta. Ta há có thể nhận vật nầy nơi tay các ngươi sao? Ðức Giê-hô-va phán vậy."},
	{14, "14 Ðáng rủa thay là kẻ hay lừa dối, trong bầy nó có con đực, mà nó hứa nguyện và dâng con tàn tật làm của lễ cho Chúa! Ðức Giê-hô-va vạn quân phán: Vì ta là Vua lớn, danh ta là đáng sợ giữa các dân ngoại."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Hỡi các thầy tế lễ, bây giờ ta truyền lịnh nầy về các ngươi."},
	{2, "2 Nếu các ngươi chẳng nghe, và không để lòng dâng sự vinh hiển cho danh ta, thì, Ðức Giê-hô-va vạn quân phán, ta sẽ giáng sự rủa sả trên các ngươi, và sẽ rủa sả những phước lành của các ngươi; và ta đã rủa sả rồi, vì các ngươi không để điều đó vào lòng."},
	{3, "3 Nầy, ta sẽ quở trách giống gieo của các ngươi, rải phân của những lễ các ngươi; các ngươi sẽ bị đem đi với phân ấy."},
	{4, "4 Các ngươi sẽ biết rằng ta đã truyền lịnh nầy cho các ngươi, để đã làm giao ước của ta với Lê-vi, Ðức Giê-hô-va vạn quân phán vậy."},
	{5, "5 Giao ước của ta với Lê-vi vốn là sự sống và sự bình an mà ta đã ban cho nó, hầu cho nó kính sợ ta; thì nó đã kính sợ ta, và run rẩy trước danh ta."},
	{6, "6 Luật pháp của sự chơn thật đã ở trong miệng nó, trong môi miếng nó chẳng có một sự không công bình nào; nó đã bước đi với ta trong sự bình an và ngay thẳng, làm cho nhiều người xây bỏ khỏi sự gian ác."},
	{7, "7 Vì môi miếng của thầy tế lễ nên giữ sự thông biết, người ta tìm luật pháp trong miệng nó, vì nó là sứ giả của Ðức Giê-hô-va vạn quân."},
	{8, "8 Nhưng, trái lại, các ngươi đã xây khỏi đường lối, làm cho nhiều người vấp ngã trong luật pháp, và đã làm sai giao ước của Lê-vi, Ðức Giê-hô-va vạn quân phán vậy."},
	{9, "9 Vậy nên ta cũng đã làm cho các ngươi ra khinh bỉ hèn hạ trước mặt cả dân, vì các ngươi chẳng giữ đường lối ta, hay vị nể người ta trong luật pháp."},
	{10, "10 Hết thảy chúng ta chẳng phải là có chung một cha sao? Chẳng phải chỉ có một Ðức Chúa Trời đã dựng nên chúng ta sao? Vậy sao ai nấy đãi anh em mình cách gian dối, phạm giao ước của tổ phụ chúng ta?"},
	{11, "11 Giu-đa đã ăn ở cách lừa phỉnh, và đã phạm một sự gớm ghiếc trong Y-sơ-ra-ên và trong Giê-ru-sa-lem; vì Giu-đa đã làm uế sự thánh khiết của Ðức Giê-hô-va, là sự Ngài ưa, mà cưới con gái của thần ngoại."},
	{12, "12 Phàm ai làm sự đó, hoặc kẻ thức, hoặc kẻ trả lời, hoặc kẻ dân của lễ cho Ðức Giê-hô-va vạn quân, Ðức Giê-hô-va sẽ trừ họ khỏi các trại của Gia-cốp."},
	{13, "13 Các ngươi lại còn làm sự nầy: các ngươi lấy nước mắt, khóc lóc, than thở mà che lấp bàn thờ Ðức Giê-hô-va, nên nỗi Ngài không nhìn đến của lễ nữa, và không vui lòng nhận lấy vật dân bởi tay các ngươi."},
	{14, "14 Các ngươi lại nói rằng: Vì sao? Ấy là vì Ðức Giê-hô-va làm chứng giữa ngươi và vợ ngươi lấy lúc tuổi trẻ, mà ngươi đãi nó cách phỉnh dối, dầu rằng nó là bạn ngươi và là vợ giao ước của ngươi."},
	{15, "15 Vả, hơi sống của Ðức Chúa Trời dầu có dư dật, chỉ làm nên một người mà thôi. Nhưng vì sao chỉ làm một người? Ấy là vì tìm một dòng dõi thánh. Vậy các ngươi khá cẩn thận trong tâm thần mình; chớ đãi cách phỉnh dối với vợ mình lấy lúc tuổi trẻ."},
	{16, "16 Vì Giê-hô-va Ðức Chúa Trời của Y-sơ-ra-ên có phán rằng ta ghét người nào bỏ vợ, và người nào lấy sự hung dữ che áo mình, Ðức Giê-hô-va vạn quân phán vậy. Vậy hãy giữ trong tâm thần các ngươi, chớ đãi cách phỉnh dối."},
	{17, "17 Các ngươi đã làm phiền Ðức Giê-hô-va bởi những lời nói mình; các ngươi lại nói rằng: Chúng tôi có làm phiền Ngài ở đâu? Ấy là khi các ngươi nói: Ai làm điều dữ thì đẹp mắt Ðức Giê-hô-va, và Ngài ưa thích những người như vậy; bằng chẳng vậy, thì nào Ðức Chúa Trời của sự chánh trực ở đâu?"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct vi3 poems[] = {
	{1, "1 Nầy, ta sai sứ giả ta, người sẽ dọn đường trước mặt ta; và Chúa mà các ngươi tìm kiếm sẽ thình lình vào trong đền thờ Ngài, tức là thiên sứ của sự giao ước mà các ngươi trông mong. Nầy, Ngài đến, Ðức Giê-hô-va vạn quân phán vậy."},
	{2, "2 Nhưng ai sẽ đương nổi ngày Ngài đến, và ai đứng được khi Ngài hiện ra? Vì Ngài giống như lửa của thợ luyện, như tro thợ giặt."},
	{3, "3 Ngài sẽ ngồi như kẻ luyện bạc và làm cho sạch; Ngài sẽ chùi các con trai Lê-vi, làm cho chúng nó sạch như vàng và bạc; chúng nó sẽ dân của lễ cho Ðức Giê-hô-va trong sự công bình."},
	{4, "4 Bấy giờ của lễ của Giu-đa và của Giê-ru-sa-lem sẽ được đẹp lòng Ðức Giê-hô-va, như những ngày xưa, và như những năm thượng cổ."},
	{5, "5 Ta sẽ đến gần các ngươi đặng làm sự đoán xét, và ta sẽ vội vàng làm chứng nghịch cùng những kẻ đồng bóng, tà dâm, những kẻ thề dối, những kẻ gạt tiền công của người làm thuê, hiếp đáp kẻ góa bụa và kẻ mồ côi, những kẻ làm hại người khách lạ, và những kẻ không kính sợ ta, Ðức Giê-hô-va vạn quân phán vậy."},
	{6, "6 Vì ta là Ðức Giê-hô-va, ta không hề thay đổi; bởi cớ đó, các ngươi là con trai Gia-cốp, chẳng bị diệt vong."},
	{7, "7 Từ những ngày tổ phụ các ngươi, các ngươi đã xây bỏ luật lệ ta và không vâng giữ. Hãy trở lại cùng ta, thì ta sẽ trở lại cùng các ngươi, Ðức Giê-hô-va vạn quân phán vậy. Nhưng các ngươi nói rằng: Bởi đâu chúng tôi sẽ trở lại?"},
	{8, "8 Người ta có thể ăn trộm Ðức Chúa Trời sao? mà các ngươi ăn trộm ta. Các ngươi nói rằng: Chúng tôi ăn trộm Chúa ở đâu? Các ngươi đã ăn trộm trong các phần mười và trong các của dâng."},
	{9, "9 Các ngươi bị rủa sả, vì các ngươi, thảy các nước, đều ăn trộm ta."},
	{10, "10 Các ngươi hãy đem hết thảy phần mười vào kho, hầu cho có lương thực trong nhà ta; và từ nay các ngươi khá lấy điều nầy mà thử ta, Ðức Giê-hô-va vạn quân phán, xem ta có mở các cửa sổ trên trời cho các ngươi, đổ phước xuống cho các ngươi đến nỗi không chỗ chứa chăng!"},
	{11, "11 Ta sẽ vì các ngươi ngăn cấm kẻ cắn nuốt, nó sẽ không phá hại bông trái của đất các ngươi; và những cây nho các ngươi trong đồng ruộng cũng sẽ không rụng trái trước khi đến mùa, Ðức Giê-hô-va phán vậy."},
	{12, "12 Mọi nước sẽ xưng các ngươi là là có phước, vì các ngươi sẽ là đất vui thích, Ðức Giê-hô-va vạn quân phán vậy."},
	{13, "13 Ðức Giê-hô-va phán: Các ngươi đã lấy lời ngang trái nghịch cùng ta. Nhưng các ngươi nói rằng: Chúng tôi có nói gì nghịch cùng Ngài?"},
	{14, "14 Các ngươi có nói: Người ta hầu việc Ðức Chúa Trời là vô ích; chúng ta giữ điều Ngài dạy phải giữ, và bước đi cách buồn rầu trước mặt Ðức Giê-hô-va vạn quân, thì có lợi gì?"},
	{15, "15 Rày chúng ta kể kẻ kiêu ngạo là có phước, kẻ phạm sự hung ác là tấn tới: họ đã thử Ðức Chúa Trời, và đã được giải thoát!"},
	{16, "16 Bấy giờ những kẻ kính sợ Ðức Giê-hô-va nói cùng nhau, thì Ðức Giê-hô-va để ý mà nghe; và một sách để ghi nhớ được chép trước mặt Ngài cho những kẻ kính sợ Ðức Giê-hô-va và tưởng đến danh Ngài."},
	{17, "17 Ðức Giê-hô-va vạn quân phán: Những kẻ ấy sẽ thuộc về ta, làm cơ nghiệp riêng của ta trong ngày ta làm; và ta sẽ tiếc chúng nó như một người tiếc con trai mình hầu việc mình."},
	{18, "18 Bấy giờ các ngươi sẽ trở lại và sẽ phân biệt giữa kẻ công bình và kẻ gian ác, giữa kẻ hầu việc Ðức Chúa Trời và kẻ không hầu việc Ngài."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct vi4 poems[] = {
	{1, "1 Vì nầy, ngày đến, cháy như lò lửa. Mọi kẻ kiêu ngạo, mọi kẻ làm sự gian ác sẽ như rơm cỏ; Ðức Giê-hô-va vạn quân phán: Ngày ấy đến, thiêu đốt chúng nó, chẳng để lại cho chúng nó hoặc rễ hoặc nhành."},
	{2, "2 Nhưng về phần các ngươi là kẻ kính sợ danh ta, thì mặt trời công bình sẽ mọc lên cho, trong cánh nó có sự chữa bịnh; các ngươi sẽ đi ra và nhảy nhót như bò tơ của chuồng."},
	{3, "3 Các ngươi sẽ giày đạp những kẻ ác, vì trong ngày ta làm, chúng nó sẽ như là tro dưới bàn chơn các ngươi, Ðức Giê-hô-va vạn quân phán vậy."},
	{4, "4 Các ngươi khá nhớ lại luật pháp của đầy tớ ta là Môi-se, mà ta đã truyền cho nó tại Hô-rép, tức là những lề luật và mạng lịnh cho cả Y-sơ-ra-ên."},
	{5, "5 Nầy, ta sẽ sai đấng tiên tri Ê-li đến cùng các ngươi trước ngày lớn và đáng sợ của Ðức Giê-hô-va chưa đến."},
	{6, "6 Người sẽ làm cho lòng cha trở lại cùng con cái, lòng con cái trở lại cùng cha, kẻo ta đến lấy sự rủa sả mà đánh đất nầy."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};