static void view8() {
struct vi8 poems[] = {
	{1, "1 Luận đến của cùng các thần tượng thì chúng ta biết mình đều có sự hay biết cả. Song sự hay biết sanh kiêu căng, còn sự yêu thương làm gương tốt."},
	{2, "2 Nếu có ai tưởng mình biết điều gì, thật người ấy chưa biết như mình nên biết."},
	{3, "3 Nhưng nếu có một người yêu mến Ðức Chúa Trời, thì Ðức Chúa Trời biết người đó."},
	{4, "4 Vậy, về sự ăn ở của cúng tế các thần tượng, chúng ta biết thần tượng trong thế gian thật là hư không, chỉ có một Ðức Chúa Trời, chớ không có thần nào khác."},
	{5, "5 Thật người ta xưng có các thần khác, hoặc ở trên trời, hoặc ở dưới đất, (bởi đó họ thờ nhiều thần nhiều Chúa),"},
	{6, "6 về phần chúng ta, chỉ có một Ðức Chúa Trời mà thôi, là Ðức Chúa Cha, muôn vật bởi Ngài mà ra, và chúng ta hướng về Ngài; lại chỉ có một Chúa mà thôi, là Ðức Chúa Jêsus Christ, muôn vật đều nhờ Ngài mà có, và chúng ta cũng vậy."},
	{7, "7 Nhưng chẳng phải mọi người đều có sự hay biết đó. Một đôi người vì nghĩ thường có thần tượng, nên khi ăn của cúng tế đó cho là của cúng tế thần tượng; thì lương tâm yếu đuối của họ bởi đó ra ô uế."},
	{8, "8 Vả, ấy chẳng phải là đồ ăn làm cho chúng ta được đẹp lòng Ðức Chúa Trời; nếu chúng ta ăn, chẳng được ích gì, bằng không ăn, cũng chẳng tổn gì."},
	{9, "9 Nhưng hãy giữ lấy, kẻo sự tự do mình làm dịp cho kẻ yếu đuối vấp phạm."},
	{10, "10 Bởi vì, nếu có kẻ lương tâm yếu đuối thấy ngươi là người có sự hay biết ngồi ăn trong miếu tà thần, thì há chẳng cũng bắt chước mà ăn của cúng tế sao?"},
	{11, "11 Thế thì ngươi lấy sự hay biết mình mà làm hư mất kẻ yếu đuối, là người anh em mà Ðấng Christ đã chết thế cho!"},
	{12, "12 Khi nào mình phạm tội cùng anh em như vậy, làm cho lương tâm yếu đuối của họ bị thương, thì là phạm tội cùng Ðấng Christ."},
	{13, "13 Cho nên, nếu đồ ăn xui anh em tôi vấp phạm, thì tôi sẽ chẳng hề ăn thịt, hầu cho khỏi làm dịp vấp phạm cho anh em tôi."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view9() {
struct vi9 poems[] = {
	{1, "1 Tôi chẳng được tự do sao? Tôi chẳng phải là sứ đồ sao? Tôi há chẳng từng thấy Ðức Chúa Jêsus là Chúa chúng ta sao? Anh em há chẳng phải là công việc tôi trong Chúa sao?"},
	{2, "2 Nếu tôi không phải là sứ đồ cho kẻ khác, ít nữa cũng là sứ đồ cho anh em; vì chính anh em là ấn tín của chức sứ đồ tôi trong Chúa."},
	{3, "3 Ấy là lẽ binh vực của tôi đối với kẻ kiện cáo mình."},
	{4, "4 Chúng tôi há không có phép ăn uống sao?"},
	{5, "5 Há không có phép dắt một người chị em làm vợ đi khắp nơi với chúng tôi như các sứ đồ khác cùng các anh em Chúa và Sê-pha đã làm, hay sao?"},
	{6, "6 Hay là chỉ một tôi với Ba-na-ba không có phép được khỏi làm việc?"},
	{7, "7 Vậy thì có ai ăn lương nhà mà đi đánh giặc? Có ai trồng vườn nho mà không ăn trái? Hay là có ai chăn bầy vật mà không dùng sữa nó để nuôi mình?"},
	{8, "8 Tôi nói vậy, nào phải chỉ theo thói người ta quen nói đâu? Luật pháp há chẳng nói như vậy sao?"},
	{9, "9 Vì chưng có chép trong luật pháp Môi-se rằng: Ngươi chớ khớp miệng con bò đang đạp lúa. Há phải Ðức Chúa Trời lo cho bò sao?"},
	{10, "10 Quả thật Ngài nói câu đó về chúng ta mà có chép rằng ai cày ruộng phải trông cậy mình sẽ có phần mà đạp lúa."},
	{11, "11 Nếu chúng tôi đã gieo của thiêng liêng cho anh em, mà muốn thâu lại của vật chất của anh em, thì nào có phải một việc quá lẽ đâu?"},
	{12, "12 Người khác còn có quyền ấy trên anh em, huống chi là chúng tôi? Dầu vậy, chúng tôi chẳng từng dùng quyền ấy; nhưng chúng tôi chịu mọi sự, hầu cho khỏi làm ngăn trở Tin Lành của Ðấng Christ chút nào."},
	{13, "13 Anh em há chẳng biết rằng ai hầu việc thánh thì ăn của dâng trong đền thờ, còn ai hầu việc nơi bàn thờ thì có phần nơi bàn thờ sao?"},
	{14, "14 Cũng vậy, Chúa có truyền rằng ai rao giảng Tin Lành thì được nuôi mình bởi Tin Lành."},
	{15, "15 Phần tôi thì chẳng từng dùng quyền ấy chút nào, và viết thơ nầy chẳng phải để đòi quyền ấy lại; vì tôi thà chết còn hơn là chịu ai cất lấy cớ khoe mình nầy đi."},
	{16, "16 Ví bằng tôi rao truyền Tin Lành, tôi chẳng có cớ khoe mình, vì có lẽ cần buộc tôi; còn không rao truyền Tin Lành, thì khốn khó cho tôi thay."},
	{17, "17 Nếu tôi vui lòng làm việc đó, thì được thưởng; lại nếu tôi không vui lòng mà làm, thì cái chức vụ cũng vẫn phó thác cho tôi."},
	{18, "18 Thế thì phần thưởng của tôi là gì? Ấy là khi giảng Tin Lành, thì giảng nhưng không, chẳng dùng quyền tôi có như người giảng Tin Lành."},
	{19, "19 Vả, tôi đối với ai vẫn cũng là được tự do, mà tôi đành phục mọi người, hầu cho tôi được nhiều người hơn."},
	{20, "20 Với người Giu-đa, tôi ở như một người Giu-đa, hầu được những người Giu-đa; với những người dưới quyền luật pháp, (dầu chính tôi chẳng ở dưới quyền luật pháp), tôi cũng ở như kẻ dưới quyền luật pháp, hầu được những người dưới quyền luật pháp;"},
	{21, "21 với những người không luật pháp, (dầu đối với Ðức Chúa Trời tôi không phải là không luật pháp, vì tôi ở dưới luật pháp của Ðấng Christ), song tôi cũng ở như người không luật pháp."},
	{22, "22 Tôi ở yếu đuối với những người yếu đuối, hầu được những người yếu đuối; tôi đã trở nên mọi cách cho mọi người, để cứu chuộc được một vài người không cứ cách nào."},
	{23, "23 Mọi điều tôi làm, thì làm vì cớ Tin Lành, hầu cho tôi cũng có phần trong đó."},
	{24, "24 Anh em há Chúa Jêsus biết rằng trong cuộc chạy thi nơi trường đua, hết thảy đều chạy, nhưng chỉ một người được thưởng sao? Vậy, anh em hãy chạy cách nào cho được thưởng."},
	{25, "25 Hết thảy những người đua tranh, tự mình chịu lấy mọi sự kiêng kỵ, họ chịu vậy để được mão triều thiên hay hư nát. Nhưng chúng ta chịu vậy để được mão triều thiên không hay hư nát."},
	{26, "26 Vậy thì, tôi chạy, chẳng phải là chạy bá vơ; tôi đánh, chẳng phải là đánh gió;"},
	{27, "27 song tôi đãi thân thể mình cách nghiêm khắc, bắt nó phải phục, e rằng sau khi tôi đã giảng dạy kẻ khác, mà chính mình phải bị bỏ chăng."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view10() {
struct vi10 poems[] = {
	{1, "1 Vả, hỡi anh em, tôi chẳng muốn cho anh em không biết tổ phụ chúng ta đều đã ở dưới đám mây, đi ngang qua biển,"},
	{2, "2 chịu Môi-se làm phép báp tem trong đám mây và dưới biển,"},
	{3, "3 ăn một thứ ăn thiêng liêng;"},
	{4, "4 và uống một thứ uống thiêng liêng; vì họ uống nơi một hòn đá thiêng liêng theo mình và đá ấy tức là Ðấng Christ."},
	{5, "5 Song phần nhiều trong vòng họ không đẹp lòng Ðức Chúa Trời, nên đã ngã chết nơi đồng vắng."},
	{6, "6 Mọi điều đó đã xảy ra để làm gương cho chúng ta, hầu cho chúng ta chớ buông mình theo tình dục xấu, như chính tổ phụ chúng ta đã buông mình."},
	{7, "7 Cũng đừng thờ hình tượng nữa, như mấy người trong họ, theo lời chép rằng: Dân sự ngồi mà ăn uống, rồi đứng dậy mà chơi dỡn."},
	{8, "8 Chúng ta chớ dâm dục như mấy người trong họ đã dâm dục, mà trong một ngày có hai vạn ba ngàn người bị bỏ mạng."},
	{9, "9 Cũng chớ thử thách Chúa như mấy người trong họ đã thử thách mà bị loài rắn hủy diệt."},
	{10, "10 Lại cũng chớ lằm bằm như nấy người trong họ đã lằm bằm mà bị chết bởi kẻ hủy diệt."},
	{11, "11 Những sự ấy có nghĩa hình bóng, và họ đã lưu truyền để khuyên bảo chúng ta là kẻ ở gần cuối cùng các đời."},
	{12, "12 Vậy thì, ai tưởng mình đứng, hãy giữ kẻo ngã."},
	{13, "13 Những sự cám dỗ đến cho anh em, chẳng có sự nào quá sức loài người. Ðức Chúa Trời là thành tín, Ngài chẳng hề cho anh em bị cám dỗ quá sức mình đâu; nhưng trong sự cám dỗ, Ngài cũng mở đàng cho ra khỏi, để anh em có thể chịu được."},
	{14, "14 Hỡi kẻ yêu dấu của tôi, vậy nên hãy tránh khỏi sự thờ lạy hình tượng."},
	{15, "15 Tôi nói với anh em cũng như nói với kẻ thông minh; chính anh em hãy suy xét điều tôi nói."},
	{16, "16 Cái chén phước lành mà chúng ta chúc phước, há chẳng phải là thông với huyết của Ðấng Christ sao? Cái bánh mà chúng ta bẻ, há chẳng phải là thông với thân thể của Ðấng Christ sao?"},
	{17, "17 Vì chỉ có một cái bánh, chúng ta dầu nhiều, cũng chỉ một thân thể; bởi chưng chúng ta đều có phần chung trong một cái bánh."},
	{18, "18 Hãy xem dân Y-sơ-ra-ên theo phần xác; những kẻ ăn thịt con sinh tế, há không thông đồng với bàn thờ sao?"},
	{19, "19 Nói vậy có ý chi? Của cùng thần tượng có ra gì chẳng?"},
	{20, "20 Chắc là không; nhưng đồ người ngoại đạo cúng tế là cúng tế các quỉ, chớ không phải cúng tế Ðức Chúa Trời. Vậy, tôi không muốn anh em thông đồng với các quỉ."},
	{21, "21 Anh em chẳng có thể uống chén của Chúa và cũng uống chén của các quỉ; chẳng có thể dự tiệc của Chúa, lại dự tiệc của các quỉ."},
	{22, "22 Hay là chúng ta muốn trêu lòng Chúa ghen chăng? Chúng ta há mạnh hơn Ngài sao?"},
	{23, "23 Mọi sự đều có phép làm, nhưng chẳng phải mọi sự đều có ích; mọi sự đều có phép làm, nhưng chẳng phải mọi sự đều làm gương tốt."},
	{24, "24 Chớ ai tìm lợi riêng cho mình, nhưng ai nấy hãy tìm cho kẻ khác."},
	{25, "25 Phàm vật gì bán ở hàng thịt, hãy ăn, đừng vì cớ lương tâm mà hỏi chi về việc đó;"},
	{26, "26 bởi chưng đất và mọi vật chứa trong đất đều thuộc về Chúa."},
	{27, "27 Nếu có người chẳng tin mời anh em, và anh em muốn đi, thì không cứ họ dọn ra đồ gì, hãy ăn đi hết thảy, đừng vì cớ lương tâm mà hỏi chi hết."},
	{28, "28 Song nếu có ai nói với anh em rằng: Cái nầy đã dâng làm của cúng, thì chớ ăn, vì cớ người đã bảo trước mình, lại vì cớ lương tâm:"},
	{29, "29 tôi chẳng nói về lương tâm anh em, nhưng về lương tâm người đó. Vả, vì cớ nào sự tự do tôi phải bị lương tâm kẻ khác đoán xét?"},
	{30, "30 Nếu tôi tạ ơn rồi ăn, cớ nào vì một bữa ăn đã cảm ơn mà lại bị chê bai?"},
	{31, "31 Vậy, anh em hoặc ăn, hoặc uống, hay là làm sự chi khác, hãy vì sự vinh hiển Ðức Chúa Trời mà làm."},
	{32, "32 Ðừng làm gương xấu cho người Giu-đa, người Gờ-réc, hay là Hội thánh của Ðức Chúa Trời;"},
	{33, "33 hãy như tôi gắng sức đẹp lòng mọi người trong mọi việc, chẳng tìm ích lợi riêng cho mình, nhưng cho phần nhiều người, để họ được cứu."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view11() {
struct vi11 poems[] = {
	{1, "1 Hãy bắt chước tôi, cũng như chính mình tôi bắt chước Ðấng Christ vậy."},
	{2, "2 Tôi khen anh em vì hay nhớ đến tôi trong mọi dịp, và lấy lòng trung tín mà giữ những điều tôi đã dạy dỗ anh em."},
	{3, "3 Dầu vậy, tôi muốn anh em biết Ðấng Christ là đầu mọi người; người đờn ông là đầu người đờn bà; và Ðức Chúa Trời là đầu của Ðấng Christ."},
	{4, "4 Phàm người đờn ông cầu nguyện hoặc giảng đạo mà trùm đầu lại, thì làm nhục đầu mình."},
	{5, "5 Nhưng phàm người đờn bà cầu nguyện hoặc giảng đạo mà không trùm đầu lại, thì làm nhục đầu mình: thật chẳng khác gì đã cạo đầu vậy."},
	{6, "6 Ví bằng người đờn bà không trùm đầu, thì cũng nên hớt tóc đi. Song nếu người đờn bà lấy sự hớt tóc hoặc cạo đầu làm xấu hổ, thì hãy trùm đầu lại."},
	{7, "7 Vả, đờn ông là hình ảnh và sự vinh hiển của Ðức Chúa Trời, thì không nên trùm đầu; nhưng đờn bà là sự vinh hiển của đờn ông."},
	{8, "8 Bởi chưng không phải đờn ông ra từ đờn bà, bèn là đờn bà ra từ đờn ông;"},
	{9, "9 không phải đờn ông vì cớ đờn bà mà được dựng nên, bèn là đờn bà vì cớ đờn ông vậy."},
	{10, "10 Bởi đó, vì cớ các thiên sứ, đờn bà phải có dấu hiệu trên đầu chỉ về quyền phép mình nương cậy."},
	{11, "11 Song trong Chúa thì chẳng phải đờn bà ngoại đờn ông, cũng chẳng phải đờn ông ngoại đờn bà;"},
	{12, "12 vì, như đờn bà đã ra từ đờn ông, thì đờn ông cũng sanh bởi đờn bà, và cả thảy đều ra bởi Ðức Chúa Trời."},
	{13, "13 Chính anh em hãy đoán xét điều nầy: đờn bà cầu nguyện Ðức Chúa Trời mà không trùm đầu là hiệp lẽ chăng?"},
	{14, "14 Chính lẽ tự nhiên há chẳng dạy cho anh em biết đờn ông để tóc dài thì lấy làm hổ thẹn sao?"},
	{15, "15 Nhưng, nếu đờn bà để tóc dài thì là lịch sự cho mình, và đã ban tóc dài cho người, dường như khăn trùm vậy."},
	{16, "16 Bằng có ai muốn cãi lẽ, thì chúng tôi không có thói quen ấy, mà các Hội thánh Ðức Chúa Trời cũng không có nữa."},
	{17, "17 Trong khi tôi truyền lời cáo dụ nầy, chẳng phải khen anh em, vì sự nhóm lại của anh em không làm cho mình hay hơn mà làm cho mình dở hơn."},
	{18, "18 Trước hết, tôi nghe rằng khi anh em có sự nhóm họp Hội thánh, thì sanh ra sự phân rẽ; tôi cũng hơi tin điều đó."},
	{19, "19 Vì chắc trong vòng anh em phải có bè đảng, hầu cho được nhận biết trong anh em những kẻ nào thật là trung thành."},
	{20, "20 Vậy, khi anh em nhóm nhau lại một chỗ, ấy chẳng còn phải là Tiệc thánh của Chúa mà anh em ăn;"},
	{21, "21 bởi vì lúc ngồi ăn, ai cũng nôn nả mà ăn bữa riêng mình, đến nỗi người nọ chịu đói, còn kẻ kia thì quá độ."},
	{22, "22 Anh em há chẳng có nhà để ăn uống sao? Hay là anh em khinh bỉ Hội thánh của Ðức Chúa Trời, muốn làm cho những kẻ không có gì phải hổ thẹn? Tôi nên nói chi với anh em? Có nên khen anh em không? Trong việc nầy tôi chẳng khen đâu."},
	{23, "23 Vả, tôi có nhận nơi Chúa điều tôi đã dạy cho anh em: ấy là Ðức Chúa Jêsus, trong đêm Ngài bị nộp, lấy bánh,"},
	{24, "24 tạ ơn, rồi bẻ ra mà phán rằng: Nầy là thân thể ta, vì các ngươi mà phó cho; hãy làm điều nầy để nhớ ta."},
	{25, "25 Cũng một lẽ ấy, sai khi ăn bữa tối rồi, Ngài lấy chén và phán rằng: Chén nầy là sự giao ước mới trong huyết ta; hễ khi nào các ngươi uống, hãy làm điều nầy để nhớ ta."},
	{26, "26 Ấy vậy, mỗi lần anh em ăn bánh nầy, uống chén nầy, thì rao sự chết của Chúa cho tới lúc Ngài đến."},
	{27, "27 Bởi đó, ai ăn bánh hoặc uống chén của Chúa cách không xứng đáng, thì sẽ mắc tội với thân và huyết của Chúa."},
	{28, "28 Vậy mỗi người phải tự xét lấy mình, và như thế mới ăn bánh uống chén ấy;"},
	{29, "29 vì người nào không phân biệt thân Chúa mà ăn bánh uống chén đó, tức là ăn uống sự xét đoán cho mình."},
	{30, "30 Ấy vì cớ đó mà trong anh em có nhiều kẻ tật nguyền, đau ốm, và có lắm kẻ ngủ."},
	{31, "31 Nếu chúng ta biết xét đoán lấy mình, thì khỏi bị xét đoán."},
	{32, "32 Song khi chúng ta bị Chúa xét đoán, thì bị Ngài sửa phạt, hầu cho khỏi bị án làm một với người thế gian."},
	{33, "33 Hỡi anh em, vậy thì lúc anh em hội lại để ăn chung với nhau, hãy chờ đợi nhau."},
	{34, "34 Ví bằng có ai đói, hãy ăn tại nhà mình, hầu cho anh em không nhóm lại để mà chuốc lấy sự đoán xét. Còn các việc khác, lúc tôi đến sẽ đoán định."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view12() {
struct vi12 poems[] = {
	{1, "1 Hỡi anh em, tôi không muốn anh em chẳng biết về các sự ban cho thiêng liêng."},
	{2, "2 Anh em biết khi mình còn là người ngoại đạo, bị khuyên dỗ hướng về các thần tượng câm, tùy ý người ta dẫn dụ mình."},
	{3, "3 Bởi đó, tôi tỏ cùng anh em, chẳng ai cảm Thánh Linh của Ðức Chúa Trời, mà nói rằng: Ðức Chúa Jêsus đáng nguyền rủa! Nếu không cảm Ðức Thánh Linh, cũng chẳng ai xưng Ðức Chúa Jêsus là Chúa!"},
	{4, "4 Vả, có các sự ban cho khác nhau, nhưng chỉ có một Ðức Thánh Linh."},
	{5, "5 Có các chức vụ khác nhau, nhưng chỉ có một Chúa."},
	{6, "6 Có các việc làm khác nhau, nhưng chỉ có một Ðức Chúa Trời, là Ðấng làm mọi việc trong mọi người."},
	{7, "7 Ðức Thánh Linh tỏ ra trong mỗi một người, cho ai nấy đều được sự ích chung."},
	{8, "8 Vả, người nầy nhờ Ðức Thánh Linh, được lời nói khôn ngoan; kẻ kia nhờ một Ðức Thánh Linh ấy, cũng được lời nói có tri thức."},
	{9, "9 Bởi một Ðức Thánh Linh, cho người nầy được đức tin; cũng bởi một Ðức Thánh Linh ấy, cho kẻ kia được ơn chữa tật bịnh;"},
	{10, "10 người thì được làm phép lạ; kẻ thì được nói tiên tri; người thì được phân biệt các thần; kẻ thì được nói nhiều thứ tiếng khác nhau, người thì được thông giải các thứ tiếng ấy."},
	{11, "11 Mọi điều đó là công việc của đồng một Ðức Thánh Linh mà thôi, theo ý Ngài muốn, phân phát sự ban cho riêng cho mỗi người."},
	{12, "12 Vả, như thân là một, mà có nhiều chi thể, và như các chi thể của thân dầu có nhiều, cũng chỉ hiệp thành một thân mà thôi, Ðấng Christ khác nào như vậy."},
	{13, "13 Vì chưng chúng ta hoặc người Giu-đa, hoặc người Gờ-réc, hoặc tôi mọi, hoặc tự chủ, đều đã chịu phép báp-tem chung một Thánh Linh để hiệp làm một thân; và chúng tôi đều đã chịu uống chung một Thánh Linh nữa."},
	{14, "14 Thân cũng chẳng phải có một chi thể, bèn là nhiều chi thể."},
	{15, "15 Nếu chơn rằng: vì ta chẳng phải là tay, nên ta không thuộc về thân, thì chẳng phải bởi đó chơn không có phần trong thân."},
	{16, "16 Và nếu tai rằng: Vì ta chẳng phải là mắt, nên ta không thuộc về thân, thì tai chẳng phải bởi đó không có phần trong thân."},
	{17, "17 Ví bằng cả thân đều là mắt, thì sự nghe ở đâu? Nếu cả thân đều là tai, thì sự ngửi ở đâu?"},
	{18, "18 Nhưng bây giờ, Ðức Chúa Trời đã sắp đặt các chi thể của thân chúng ta, ban cho mỗi một chi thể cái địa vị theo ý Ngài lấy làm tốt mà chỉ định."},
	{19, "19 Nếu chỉ có một chi thể mà thôi, thì cái thân ở đâu?"},
	{20, "20 vậy, có nhiều chi thể, song chỉ có một thân."},
	{21, "21 Mắt không được nói với bàn tay rằng: Ta chẳng cần đến mầy; đầu cũng chẳng được nói với chơn rằng: Ta chẳng cần đến bay."},
	{22, "22 Trái lại, các chi thể của thân xem ra rất yếu đuối lại là cần dùng."},
	{23, "23 Chi thể nào trong thân chúng ta nghĩ là hèn hạ hơn, thì chúng ta tôn trọng hơn; chi thể nào chẳng đẹp, thì chúng ta lại trau giồi hơn,"},
	{24, "24 còn như cái nào đã đẹp rồi, thì không cần trau giồi. Nhưng Ðức Chúa Trời đã sắp đặt thân người, để chi thể nào thiếu thốn thì được quí trọng hơn,"},
	{25, "25 hầu cho trong thân không có sự phân rẽ, mà các chi thể phải đồng lo tưởng đến nhau."},
	{26, "26 Lại, trong các chi thể, khi có một cái nào chịu đau đớn, thì các cái khác đều cùng chịu; và khi một cái nào được tôn trọng, thì các cái khác cùng vui mừng."},
	{27, "27 Vả, anh em là thân của Ðấng Christ, và là chi thể của thân, ai riêng phần nấy."},
	{28, "28 Ðức Chúa Trời đã lập trong Hội thánh, thứ nhứt là sứ đồ, thứ nhì là đấng tiên tri, thứ ba là thầy giáo, kế đến kẻ làm phép lạ, rồi kẻ được ơn chữa bịnh, cứu giúp, cai quản, nói các thứ tiếng."},
	{29, "29 Có phải cả thảy là sứ đồ sao? Cả thảy là tiên tri sao? Cả thảy đều là thầy giáo sao? Cả thảy đều làm phép lạ sao?"},
	{30, "30 Cả thảy đều được ơn chữa bịnh sao? Cả thảy đều nói tiếng lạ sao? Cả thảy đều thông giải tiếng lạ sao?"},
	{31, "31 Hãy ước ao cho được sự ban cho lớn hơn hết. Bây giờ, tôi sẽ chỉ dẫn cho anh em con đường tốt lành hơn."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view13() {
struct vi13 poems[] = {
	{1, "1 Dầu tôi nói được các thứ tiếng loài người và thiên sứ, nếu không có tình yêu thương, thì tôi chỉ như đồng kêu lên hay là chập chỏa vang tiếng."},
	{2, "2 Dầu tôi được ơn nói tiên tri, cùng biết đủ các sự mầu nhiệm, và mọi sự hay biết; dầu tôi có cả đức tin đến nỗi dời núi được, nhưng không có tình yêu thương, thì tôi chẳng ra gì."},
	{3, "3 Dầu tôi phân phát gia tài để nuôi kẻ nghèo khó, lại bỏ thân mình để chịu đốt, song không có tình yêu thương, thì điều đó chẳng ích chi cho tôi."},
	{4, "4 Tình yêu thương hay nhịn nhục; tình yêu thương hay nhơn từ; tình yêu thương chẳng ghen tị, chẳng khoe mình, chẳng lên mình kiêu ngạo,"},
	{5, "5 chẳng làm điều trái phép, chẳng kiếm tư lợi, chẳng nóng giận, chẳng nghi ngờ sự dữ,"},
	{6, "6 chẳng vui về điều không công bình, nhưng vui trong lẽ thật."},
	{7, "7 Tình yêu thương hay dung thứ mọi sự, tin mọi sự, trông cậy mọi sự, nín chịu mọi sự."},
	{8, "8 Tình yêu thương chẳng hề hư mất bao giờ. Các lời tiên tri sẽ hết, sự ban cho nói tiếng lạ sẽ thôi, sự thông biết hầu bị bỏ."},
	{9, "9 Vì chưng chúng ta hiểu biết chưa trọn vẹn, nói tiên tri cũng chưa trọn vẹn;"},
	{10, "10 song lúc sự trọn lành đã đến, thì sự chưa được trọn lành sẽ bị bỏ."},
	{11, "11 Khi tôi còn trẻ, tôi nói như con trẻ, tư tưởng như con trẻ, suy xét như con trẻ; khi tôi đã thành nhơn bèn bỏ những điều thuộc về con trẻ."},
	{12, "12 Ngày nay chúng ta xem như trong một cái gương, cách mập mờ: đến bấy giờ chúng ta sẽ thấy hai mặt đối nhau; ngày nay tôi biết chưa hết: đến bấy giờ tôi sẽ biết như Chúa đã biết tôi vậy."},
	{13, "13 Nên bây giờ còn có ba điều nầy: đức tin, sự trông cậy, tình yêu thương; nhưng điều trọng hơn trong ba điều đó là tình yêu thương."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view14() {
struct vi14 poems[] = {
	{1, "1 Hãy nôn nả tìm kiếm tình yêu thương. Cũng hãy ước ao các sự ban cho thiêng liêng, nhứt là sự ban cho nói tiên tri."},
	{2, "2 Vì người nào nói tiếng lạ, thì không phải nói với người ta, bèn là với Ðức Chúa Trời, bởi chẳng có ai hiểu (ấy là trong tâm thần mà người kia nói lời mầu nhiệm);"},
	{3, "3 còn như kẻ nói tiên tri, thì nói với người ta để gây dựng, khuyên bảo và yên ủi."},
	{4, "4 Kẻ nói tiếng lạ, tự gây dựng lấy mình; song kẻ nói tiên tri, gây dựng cho Hội thánh."},
	{5, "5 Tôi ước ao anh em đều nói tiếng lạ cả, song tôi còn ước ao hơn nữa là anh em nói tiên tri. Người nói tiên tri là trọng hơn kẻ nói tiếng lạ mà không giải nghĩa để cho Hội thánh được gây dựng."},
	{6, "6 Hỡi anh em, vì bằng tôi đến cùng anh em, nói các thứ tiếng lạ, mà lời nói tôi chẳng tỏ sự mầu nhiệm, chẳng có sự thông biết, chẳng có lời tiên tri, chẳng có sự khuyên dạy, thì ích gì đến anh em?"},
	{7, "7 Vậy, dẫu vật không linh tánh phát ra tiếng, như ống tiêu, đờn cầm: nếu không có âm điệu phân biệt nhau, thể nào nhận biết được ống tiêu hay là đờn cầm thổi và khảy cái chi?"},
	{8, "8 Lại nếu kèn trổi tiếng lộn xộn, thì ai sửa soạn mà ra trận?"},
	{9, "9 Anh em cũng vậy, nếu dùng lưỡi nói chẳng rõ ràng cho người ta nghe, thì làm sao họ được biết điều anh em nói? Vì anh em nói bông lông."},
	{10, "10 Trong thế gian có lắm thứ tiếng, mỗi thứ đều có nghĩa."},
	{11, "11 Vậy nếu tôi chẳng biết nghĩa của tiếng nói đó, thì người nói tiếng đó sẽ coi tôi là mọi, và tôi cũng coi họ là mọi nữa."},
	{12, "12 Ấy vậy, vì anh em nôn nả ước ao các sự ban cho thiêng liêng, hãy tìm cho được dư dật đặng gây dựng Hội thánh."},
	{13, "13 Bởi đó, kẻ nói tiếng lạ, hãy cầu nguyện để được thông giải tiếng ấy."},
	{14, "14 Vì nếu tôi cầu nguyện bằng tiếng lạ, thì tâm thần tôi cầu nguyện, nhưng trí khôn tôi lơ lửng."},
	{15, "15 Vậy thì tôi sẽ làm thể nào? Tôi sẽ cầu nguyện theo tâm thần, nhưng cũng cầu nguyện bằng trí khôn. Tôi sẽ hát theo tâm thần, nhưng cũng hát bằng trí khôn."},
	{16, "16 Bằng không, nếu ngươi chỉ lấy tâm thần mà chúc tạ, thì người tầm thường ngồi nghe, chẳng hiểu ngươi nói gì, thể nào họ theo lời chúc tạ mình mà đáp lại A-men được?"},
	{17, "17 Thật vậy, lời chúc tạ của ngươi vẫn tốt lành; song kẻ khác chẳng nhờ đó mà được gây dựng."},
	{18, "18 Tôi tạ ơn Ðức Chúa Trời, vì đã được ơn nói tiếng lạ nhiều hơn hết thảy anh em;"},
	{19, "19 nhưng thà tôi lấy năm lời bằng trí khôn mình mà rao giảng trong Hội thánh, để được dạy bảo kẻ khác, hơn là lấy một vạn lời bằng tiếng lạ."},
	{20, "20 Hỡi Anh em, về sự khôn sáng, chớ nên như trẻ con; nhưng về sự gian ác, thật hãy nên như trẻ con vậy. Còn về sự khôn sáng, hãy nên như kẻ thành nhơn."},
	{21, "21 Trong luật pháp có chép rằng: Chúa phán: Ta sẽ nhờ những người nói tiếng lạ, và môi miệng người ngoại quốc mà phán cho dân nầy; dầu vậy họ cũng chẳng nghe ta."},
	{22, "22 Thế thì, các thứ tiếng là một dấu, chẳng cho người tin Chúa, song cho người chẳng tin; còn như lời tiên tri là một dấu, chẳng phải cho người không tin, song cho người tin Chúa."},
	{23, "23 Vậy thì cả Hội thánh nhóm lại một nơi, nếu ai nấy đều nói tiếng lạ, mà có kẻ tầm thường hoặc người chẳng tin vào nghe, họ há chẳng nói anh em là điên cuồng sao?"},
	{24, "24 Song nếu ai nấy đều nói tiên tri, mà có người chẳng tin hoặc kẻ tầm thường vào nghe, thì họ sẽ bị mọi người bắt phục, và bị mọi người xét đoán,"},
	{25, "25 sự kín giấu trong lòng họ đã tỏ ra; họ bèn sấp mặt xuống đất, thờ lạy Ðức Chúa Trời, và nói rằng thật có Ðức Chúa Trời ở giữa anh em."},
	{26, "26 Hỡi Anh em, nên nói thể nào? Khi Anh em nhóm lại với nhau, trong anh em, ai có bài ca, hoặc bài giảng dạy, hoặc lời tỏ sự kín nhiệm, hoặc nói tiếng lạ, hoặc giải tiếng lạ chăng? Hãy làm hết thảy cho được gây dựng."},
	{27, "27 Ví bằng có người nói tiếng lạ, chỉ nên hai hoặc ba người là cùng; mỗi người phải nói theo lượt mình, và phải có một người thông giải."},
	{28, "28 Nếu không có ai thông giải, thì người đó phải làm thinh ở trong Hội thánh, mình nói với mình và với Ðức Chúa Trời."},
	{29, "29 Người nói tiên tri cũng chỉ nên có hai hoặc ba người nói mà thôi, còn những kẻ khác thì suy xét."},
	{30, "30 Song, nếu một người trong bọn người ngồi, có lời tỏ kín nhiệm, thì người thứ nhất phải nín lặng."},
	{31, "31 Bởi vì anh em đều cứ lần lượt mà nói tiên tri được cả, để ai nấy đều được dạy bảo, ai nấy đều được khuyên lơn."},
	{32, "32 Tâm thần của các đấng tiên tri suy phục các đấng tiên tri."},
	{33, "33 Vả, Ðức Chúa Trời chẳng phải là Chúa của sự loạn lạc, bèn là Chúa sự hòa bình. Hãy làm như trong cả Hội thánh của các thánh đồ,"},
	{34, "34 đờn bà phải nín lặng trong đám hội của anh em: họ không có phép nói tại nơi đó, nhưng phải phục tùng cũng như luật pháp dạy."},
	{35, "35 Nhược bằng họ muốn học khôn điều gì, thì mỗi người trong đám họ phải hỏi chồng mình ở nhà; bởi vì đờn bà nói lên trong Hội thánh là không hiệp lẽ."},
	{36, "36 Có phải là đạo Ðức Chúa Trời ra từ nơi anh em, hoặc chỉ đến cùng anh em mà thôi chăng?"},
	{37, "37 Nếu ai tưởng mình là tiên tri, hay là được Ðức Thánh Linh soi sáng, thì người ấy hãy biết rằng đều tôi viết cho anh em đây là mạng lịnh của Chúa."},
	{38, "38 Mà nếu ai muốn bỏ qua thì mặc họ bỏ qua!"},
	{39, "39 Ấy vậy, hỡi anh em, hãy trông mong ơn nói tiên tri, và đừng ngăn trở chi về điều nói tiếng lạ."},
	{40, "40 Nhưng mọi sự đều nên làm cho phải phép và theo thứ tự."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view15() {
struct vi15 poems[] = {
	{1, "1 Hỡi anh em, tôi nhắc lại cho anh em Tin Lành mà tôi đã rao giảng và anh em đã nhận lấy, cùng đứng vững vàng trong đạo ấy,"},
	{2, "2 và nhờ đạo ấy, anh em được cứu rỗi, miễn là anh em giữ lấy y như tôi đã giảng cho; bằng không, thì anh em dầu có tin cũng vô ích."},
	{3, "3 Vả trước hết tôi đã dạy dỗ anh em điều mà chính tôi đã nhận lãnh, ấy là Ðấng Christ chịu chết vì tội chúng ta theo lời Kinh Thánh;"},
	{4, "4 Ngài đã bị chôn, đến ngày thứ ba, Ngài sống lại, theo lời Kinh Thánh;"},
	{5, "5 và Ngài đã hiện ra cho Sê-pha, sau lại hiện ra cho mười hai sứ đồ."},
	{6, "6 Rồi đó, cùng trong một lần, Ngài hiện ra cho hơn năm trăm anh em xem thấy, phần nhiều người trong số ấy hiện bây giờ còn sống, nhưng có mấy người đã ngủ rồi."},
	{7, "7 Ðoạn, Ngài hiện ra cho Gia-cơ, rồi cho các sứ đồ."},
	{8, "8 Rồi lại, sau những người ấy, Ngài cũng hiện ra cho tôi xem, như cho một thai sanh non vậy."},
	{9, "9 Vì tôi là rất hèn mọn trong các sứ đồ, không đáng gọi là sứ đồ, bởi tôi đã bắt bớ Hội thánh của Ðức Chúa Trời."},
	{10, "10 Nhưng tôi nay là người thể nào, là nhờ ơn Ðức Chúa Trời, và ơn Ngài ban cho tôi cũng không phải là uổng vậy. Trái lại, tôi đã làm nhiều việc hơn các người khác, nhưng nào phải tôi, bèn là ơn Ðức Chúa Trời đã ở cùng tôi."},
	{11, "11 Ấy vậy, dầu tôi, dầu các người khác, đó là điều chúng tôi giảng dạy, và là điều anh em đã tin."},
	{12, "12 Vả, nếu giảng dạy rằng Ðấng Christ đã từ kẻ chết sống lại, thì sao trong anh em có kẻ nói rằng những kẻ chết chẳng sống lại?"},
	{13, "13 Nếu những kẻ không sống lại, thì Ðấng Christ cũng đã chẳng sống lại nữa."},
	{14, "14 Lại nếu Ðấng Christ đã chẳng sống lại, thì sự giảng dạy của chúng tôi ra luống công, và đức tin anh em cũng vô ích."},
	{15, "15 Lại nếu kẻ chết quả thật không sống lại, thì Ðức Chúa Trời đã chẳng khiến Ðấng Christ sống lại, mà chúng tôi đã làm chứng trái với Ðức Chúa Trời rằng Ngài đã làm cho Ðấng Christ sống lại, té ra chúng tôi làm chứng dối cho Ðức Chúa Trời."},
	{16, "16 Vì nếu kẻ chết chẳng sống lại, thì Ðấng Christ cũng đã chẳng sống lại nữa."},
	{17, "17 Và nếu Ðấng Christ đã chẳng sống lại, thì đức tin anh em cũng vô ích, anh em còn ở trong tội lỗi mình."},
	{18, "18 Vậy, những kẻ ngủ trong Ðấng Christ cũng phải hư mất đời đời."},
	{19, "19 Nếu chúng ta chỉ có sự trông cậy trong Ðấng Christ về đời nầy mà thôi, thì trong cả mọi người, chúng ta là kẻ khốn nạn hơn hết."},
	{20, "20 Nhưng bây giờ, Ðấng Christ đã từ kẻ chết sống lại, Ngài là trái đầu mùa của những kẻ ngủ."},
	{21, "21 Vả, vì chưng bởi một người mà có sự chết, thì cũng bởi một người mà có sự sống lại của những kẻ chết."},
	{22, "22 Như trong A-đam mọi người đều chết, thì cũng một lẽ ấy, trong Ðấng Christ mọi người đều sẽ sống lại,"},
	{23, "23 nhưng mỗi người theo thứ tự riêng của mình: Ðấng Christ là trái đầu mùa; rồi tới ngày Ðấng Christ đến, những kẻ thuộc về Ngài sẽ sống lại."},
	{24, "24 Kế đó, cuối cùng sẽ đến là lúc Ngài sẽ giao nước lại cho Ðức Chúa Trời là Cha, sau khi Ngài đã phá diệt mọi đế quốc, mọi quyền cai trị, và mọi thế lực;"},
	{25, "25 vì Ngài phải cầm quyền cho đến chừng đặt những kẻ thù nghịch dưới chơn mình."},
	{26, "26 Kẻ thù bị hủy diệt sau cùng, tức là sự chết."},
	{27, "27 Vả, Ðức Chúa Trời đã để muôn vật dưới chơn Ngài; mà đã nói rằng muôn vật phục Ngài, thì chắc phải trừ ra Ðấng làm cho muôn vật phục Ngài."},
	{28, "28 Khi muôn vật đã phục Ngài, thì bấy giờ chính mình Con sẽ phục Ðấng đã làm cho muôn vật phục mình, hầu cho Ðức Chúa Trời làm muôn sự trong muôn sự."},
	{29, "29 Bằng chẳng vậy, những người vì kẻ chết chịu phép báp tem sẽ làm chi? Nếu kẻ chết quả thật không sống lại, thì sao họ vì những kẻ ấy mà chịu phép báp tem?"},
	{30, "30 Lại sao chính mình chúng tôi giờ nào cũng ở trong sự nguy hiểm?"},
	{31, "31 Hỡi anh em, tôi chết hằng ngày, thật cũng như anh em là sự vinh hiển cho tôi trong Ðức Chúa Jêsus Christ, là Chúa chúng ta."},
	{32, "32 Nếu tôi theo cách loài người mà đã đánh cùng các loài thú ở thành Ê-phê-sô, thì có ích gì cho tôi? Nếu kẻ chết chẳng sống lại, thì hãy ăn, hãy uống, vì ngày mai chúng ta sẽ chết!"},
	{33, "33 Anh em chớ mắc lừa: bạn bè xấu làm hư thói nết tốt."},
	{34, "34 Hãy tỉnh biết, theo các công bình, và chớ phạm tội; vì có người không biết Ðức Chúa Trời chút nào, tôi nói vậy để anh em hổ thẹn."},
	{35, "35 Nhưng có kẻ sẽ nói rằng: Người chết sống lại thể nào, lấy xác nào mà trở lại?"},
	{36, "36 Hỡi kẻ dại kia, vật gì ngươi gieo, nếu không chết đi trước đã, thì không sống lại được."},
	{37, "37 Còn như vật ngươi gieo, ấy không phải là chính hình thể sẽ sanh ra, chẳng qua là một cái hột, như hột lúa mì hay là hột giống nào khác."},
	{38, "38 Ðức Chúa Trời cho nó hình thể tùy ý Ngài lấy làm tốt, mỗi một hột giống, cho một hình thể riêng."},
	{39, "39 Mọi xác thịt chẳng phải là đồng một xác thịt; nhưng xác thịt loài người khác, xác thịt loài thú khác, loài chim khác, loài cá khác."},
	{40, "40 Lại cũng có hình thể thuộc về trời, hình thể thuộc về đất, nhưng vinh quang của hình thể thuộc về trời với vinh quang của hình thể thuộc về đất thì khác nhau."},
	{41, "41 Vinh quang của mặt trời khác, vinh quang của mặt trăng khác, vinh quang của ngôi sao khác; vinh quang của ngôi sao nầy với vinh quang của ngôi sao kia cũng khác."},
	{42, "42 Sự sống lại của kẻ chết cũng như vậy. Thân thể đã gieo ra là hay hư nát, mà sống lại là không hay hư nát;"},
	{43, "43 đã gieo ra là nhục, mà sống lại là vinh; đã gieo ra là yếu, mà sống lại là mạnh;"},
	{44, "44 đã gieo ra là thể huyết khí, mà sống lại là thể thiêng liêng. Nếu đã có thể huyết khí, thì cũng có thể thiêng liêng;"},
	{45, "45 ấy vậy, có lời chép rằng: Người thứ nhứt là A-đam đã nên linh hồn sống. A-đam sau hết là thần ban sự sống."},
	{46, "46 Nhưng chẳng phải thể thiêng liêng đến trước, ấy là thể huyết khí; rồi thể thiêng liêng đến sau."},
	{47, "47 Người thứ nhứt bởi đất mà ra, là thuộc về đất, người thứ hai bởi trời mà ra."},
	{48, "48 Người thuộc về đất thể nào, thì những kẻ thuộc về đất cũng thể ấy; người thuộc về trời thể nào, thì những kẻ thuộc về trời cũng thể ấy."},
	{49, "49 Như chúng ta đã mang ảnh tượng của người thuộc về đất, thì chúng ta cũng sẽ mang ảnh tượng của người thuộc về trời."},
	{50, "50 Hỡi anh em, tôi đoán quyết rằng thịt và máu chẳng hưởng nước Ðức Chúa Trời được, và sự hay hư nát không hưởng sự không hay hư nát được."},
	{51, "51 Nầy là sự mầu nhiệm tôi tỏ cho anh em: Chúng ta không ngủ hết, nhưng hết thảy đều sẽ biến hóa,"},
	{52, "52 trong giây phút, trong nháy mắt, lúc tiếng kèn chót; vì kèn sẽ thổi, kẻ chết đều sống lại được không hay hư nát, và chúng ta đều sẽ biến hóa."},
	{53, "53 Vả, thể hay hư nát nầy phải mặc lấy sự không hay hư nát, và thể hay chết nầy phải mặc lấy sự không hay chết."},
	{54, "54 Khi nào thể hay hư nát nầy mặc lấy sự không hay hư nát, thể hay chết nầy mặc lấy sự không hay chết, thì được ứng nghiệm lời Kinh Thánh rằng: Sự chết đã bị nuốt mất trong sự thắng."},
	{55, "55 Hỡi sự chết, sự thắng của mầy ở đâu? Hỡi sự chết, cái nọc của mầy ở đâu?"},
	{56, "56 Cái nọc sự chết là tội lỗi, sức mạnh tội lỗi là luật pháp."},
	{57, "57 Nhưng, tạ ơn Ðức Chúa Trời đã cho chúng ta sự thắng, nhờ Ðức Chúa Jêsus Christ chúng ta."},
	{58, "58 Vậy, hỡi anh em yêu dấu của tôi, hãy vững vàng, chớ rúng động, hãy làm công việc Chúa cách dư dật luôn, vì biết rằng công khó của anh em trong Chúa chẳng phải là vô ích đâu."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view16() {
struct vi16 poems[] = {
	{1, "1 Về việc góp tiền cho thánh đồ, thì anh em cũng hãy theo như tôi đã định liệu cho các Hội thánh xứ Ga-la-ti."},
	{2, "2 Cứ ngày đầu tuần lễ. mỗi một người trong anh em khá tùy sức mình chắt lót được bao nhiêu thì để dành tại nhà mình, hầu cho khỏi đợi khi tôi đến rồi mới góp."},
	{3, "3 Khi tôi đến, tôi sẽ sai những kẻ mà anh em đã chọn cầm thơ tôi đi, đặng đem tiền bố thí của anh em đến thành Giê-ru-sa-lem."},
	{4, "4 Ví bằng việc đáng chính mình tôi phải đi, thì những kẻ ấy sẽ đi với tôi."},
	{5, "5 Vậy, tôi sẽ đến cùng anh em sau khi ghé qua xứ Ma-xê-đoan, vì tôi phải ghé qua xứ Ma-xê-đoan."},
	{6, "6 Có lẽ tôi trú lại nơi anh em, hoặc cũng ở trọn mùa đông tại đó nữa, để anh em đưa tôi đến nơi tôi muốn đi."},
	{7, "7 Lần nầy tôi chẳng muốn chỉ gặp anh em trong khi ghé qua mà thôi; nếu Chúa cho phép, thì tôi rất mong ở cùng anh em ít lâu."},
	{8, "8 Nhưng tôi sẽ ở lại thành Ê-phê-sô cho đến lễ Ngũ Tuần,"},
	{9, "9 vì tại đó có một cái cửa lớn mở toang ra cho công việc tôi, lại có nhiều kẻ đối địch."},
	{10, "10 Nếu Ti-mô-thê đến thăm anh em, hãy giữ cho người khói sợ sệt gì trong anh em: vì người cũng làm việc cho Chúa như chính mình tôi vậy."},
	{11, "11 Nên chớ có ai khinh người, hãy đưa người đi về bình an, hầu cho người đến cùng tôi, vì tôi đương đợi người đồng đến với anh em."},
	{12, "12 Còn như anh em chúng ta là A-bô-lô tôi thường cố khuyên người đi với anh em đây mà đến cùng các anh em, nhưng hiện nay người chắc chưa khứng đi; người sẽ đi trong khi có dịp tiện."},
	{13, "13 Anh em hãy tỉnh thức, hãy vững vàng trong đức tin, hãy dốc chí trượng phu và mạnh mẽ."},
	{14, "14 Mọi điều anh em làm, hãy lấy lòng yêu thương mà làm."},
	{15, "15 Hỡi anh em, còn một lời dặn nữa: anh em biết rằng nhà Sê-pha-na là trái đầu mùa của xứ A-chai, và biết rằng nhà ấy hết lòng hầu việc các thánh đồ."},
	{16, "16 Vậy, hãy kính phục những người thể ấy, và kính phục cả mọi người cùng làm việc, cùng khó nhọc với chúng ta."},
	{17, "17 Sê-pha-na, Phốt-tu-na và A-chai-cơ đến đây, tôi lấy làm vui mừng lắm; các người ấy đã bù lại sự anh em thiếu thốn,"},
	{18, "18 vì các người ấy đã làm cho yên lặng tâm thần của tôi và của anh em. Hãy biết quí trọng những người dường ấy."},
	{19, "19 Các Hội thánh ở xứ A-si chào thăm anh em. A-qui-la và Bê-rít-sin gởi lời chào anh em trong Chúa, Hội thánh hiệp trong nhà hai người ấy cũng vậy."},
	{20, "20 Hết thảy anh em đây chào thăm các anh em. Hãy lấy cái hôn thánh mà chào nhau."},
	{21, "21 Tôi là Phao-lô, chính tay tôi viết chào thăm anh em."},
	{22, "22 Bằng có ai không kính mến Chúa, thì phải bị a-na-them! Ma-ra-na-tha."},
	{23, "23 Nguyền xin ơn của Ðức Chúa Jêsus Christ ở với anh em!"},
	{24, "24 Lòng yêu thương của tôi ở với hết thảy anh em trong Ðức Chúa Jêsus Christ."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
