#include <map>
#include <string>
class Bible53
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
	struct vi3	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Phao-lô, Sin-vanh và Ti-mô-thê gởi cho Hội thánh Tê-sa-lô-ni-ca, là Hội thánh ở trong Ðức Chúa Trời, Cha chúng ta, và trong Ðức Chúa Jêsus Christ, là Chúa:"},
	{2, "2 nguyền xin anh em được ân điển và sự bình an ban cho bởi Ðức Chúa Jêsus Christ!"},
	{3, "3 Hỡi anh em, chúng tôi phải vì anh em cảm tạ Ðức Chúa Trời luôn luôn; điều đó là phải lắm, vì đức tin anh em rất tấn tới, và lòng yêu thương của mỗi người trong hết thảy anh em đối với nhau càng ngày càng thêm."},
	{4, "4 Chúng tôi cũng vì anh em mà khoe mình cùng các Hội thánh của Ðức Chúa Trời, vì lòng nhịn nhục và đức tin anh em trong mọi sự bắt bớ khốn khó đương chịu."},
	{5, "5 Ðó đủ chứng cớ về sự đoán xét công bình của Ðức Chúa Trời, Ngài muốn khiến anh em nên xứng đáng cho nước Ngài, và vì nước đó mà anh em chịu khổ."},
	{6, "6 Vả, theo sự công bình Ðức Chúa Trời, thì ắt là Ngài lấy điều khổ báo cho những kẻ làm khổ anh em,"},
	{7, "7 và cho anh em, là kẻ chịu khổ, được nghỉ ngơi với chúng tôi, trong khi Ðức Chúa Jêsus từ trời hiện đến với các thiên sứ của quyền phép Ngài, giữa ngọn lửa hừng,"},
	{8, "8 báo thù những kẻ chẳng hề nhận biết Ðức Chúa Trời, và không vâng phục Tin Lành của Ðức Chúa Jêsus Christ chúng ta."},
	{9, "9 Họ sẽ bị hình phạt hư mất đời đời, xa cách mặt Chúa và sự vinh hiển của quyền phép Ngài,"},
	{10, "10 tức là khi Ngài sẽ đến trong ngày đó, để được sáng danh trong các thánh đồ, được khen ngợi trong mọi kẻ tin; vì anh em đã tin lời chúng tôi làm chứng trước mặt anh em."},
	{11, "11 Cho nên, chúng tôi vì anh em cầu nguyện không thôi, hầu cho Ðức Chúa Trời chúng ta khiến anh em xứng đáng với sự gọi của Ngài, và cho Ngài lấy quyền phép làm trọn trong anh em mọi ý định thương xót của lòng nhơn Ngài và công việc của đức tin;"},
	{12, "12 đến nỗi làm sáng danh Ðức Chúa Jêsus chúng ta trong anh em, và anh em trong Ngài, tùy theo ân điển của Ðức Chúa Trời chúng ta và của Ðức Chúa Jêsus Christ."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Luận về sự đến của Ðức Chúa Jêsus Christ chúng ta, và về sự chúng ta hội hiệp cùng Ngài,"},
	{2, "2 thì, hỡi anh em, xin chớ vội bối rối và kinh hoàng hoặc bởi thần cảm giả mạo, hoặc bởi lời nói hay là bởi bức thơ nào tựa như chúng tôi đã gởi mà nói rằng ngày Chúa gần đến."},
	{3, "3 Mặc ai dùng cách nào, cũng đừng để họ lừa dối mình. Vì phải có sự bỏ đạo đến trước, và có người tội ác, con của sự hư mất hiện ra,"},
	{4, "4 tức là kẻ đối địch, tôn mình lên trên mọi sự mà người ta xưng là Ðức Chúa Trời hoặc người ta thờ lạy, rất đỗi ngồi trong đền Ðức Chúa Trời, chính mình tự xưng là Ðức Chúa Trời."},
	{5, "5 Anh em há không nhớ khi tôi còn ở với anh em, thì đã nói về những sự đó sao?"},
	{6, "6 Hiện nay anh em biết rõ điều làm ngăn trở nó, hầu cho nó chỉ hiện ra đúng kỳ nó thôi."},
	{7, "7 Vì đã có sự mầu nhiệm của điều bội nghịch đương hành động rồi; song Ðấng còn ngăn trở cần phải cất đi."},
	{8, "8 Bấy giờ kẻ nghịch cùng luật pháp kia sẽ hiện ra, Ðức Chúa Jêsus sẽ dùng hơi miệng Ngài mà hủy diệt nó, và trừ bỏ nó bởi sự chói sáng của sự Ngài đến."},
	{9, "9 Kẻ đó sẽ lấy quyền của quỉ Sa-tan mà hiện đến, làm đủ mọi thứ phép lạ, dấu dị và việc kỳ dối giả;"},
	{10, "10 dùng mọi cách phỉnh dổ không công bình mà dổ những kẻ hư mất, vì chúng nó đã không nhận lãnh sự yêu thương của lẽ thật để được cứu rỗi."},
	{11, "11 Ấy vì thế mà Ðức Chúa Trời cho chúng nó mắc phải sự lầm lạc, là sự khiến chúng nó tin điều dối giả,"},
	{12, "12 hầu cho hết thảy những người không tin lẽ thật, song chuộng sự không công bình, điều bị phục dưới sự phán xét của Ngài."},
	{13, "13 Hỡi anh em yêu dấu của Chúa, còn như chúng tôi, phải vì anh em tạ ơn Ðức Chúa Trời không thôi, vì vừa lúc ban đầu, Ngài đã chọn anh em bởi sự nên thánh của Thánh Linh, và bởi tin lẽ thật, đặng ban sự cứu rỗi cho anh em."},
	{14, "14 Ấy cũng là vì đó mà Ngài đã dùng Tin Lành chúng tôi gọi anh em, đặng anh em hưởng được sự vinh hiển của Ðức Chúa Jêsus Christ chúng ta."},
	{15, "15 Hỡi anh em, vậy thì hãy đứng vững, hãy vâng giữ những điều dạy dỗ mà chúng tôi đã truyền cho anh em, hoặc bằng lời nói, hoặc bằng thơ từ của chúng tôi."},
	{16, "16 Nguyền xin chính Ðức Chúa Jêsus Christ chúng ta, và Ðức Chúa Trời Cha chúng ta, là Ðấng đã yêu thương chúng ta, và đã lấy ân điển mà ban cho chúng ta sự yên ủi đời đời và sự trông cậy tốt lành,"},
	{17, "17 hãy yên ủi lòng anh em, khiến anh em được bền vững trong mọi việc lành cùng mọi lời nói lành."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct vi3 poems[] = {
	{1, "1 Vả lại, hỡi anh em, hãy cầu nguyện cho chúng tôi, hầu cho đạo Chúa được đồn ra và được sáng danh, như thể ở trong anh em vậy,"},
	{2, "2 lại hầu cho chúng tôi được giải thoát khỏi kẻ vô lý và người ác; vì chẳng phải hết thảy đều có đức tin."},
	{3, "3 Chúa là thành tín, sẽ làm cho anh em bền vững và giữ cho khỏi Ác giả."},
	{4, "4 Về phần anh em, chúng tôi có lòng tin cậy trong Chúa rằng anh em đương làm và sẽ làm những việc chúng tôi dặn biểu."},
	{5, "5 Nguyền xin Chúa soi dẫn lòng anh em tới đến sự yêu mến Ðức Chúa Trời, và sự nhịn nhục của Ðấng Christ!"},
	{6, "6 Hỡi anh em, nhơn danh Ðức Chúa Jêsus Christ chúng ta, chúng tôi khuyên anh em phải lánh người anh em nào không biết tu đức hạnh mình, và không bước theo các điều dạy dỗ mà anh em đã nhận lãnh nơi chúng tôi."},
	{7, "7 Chính anh em biết điều mình phải làm để học đòi chúng tôi; vì chúng tôi không có ăn ở sái bậy giữa anh em,"},
	{8, "8 chưa từng ăn dưng của ai, nhưng đêm ngày làm lụng khó nhọc, để khỏi lụy đến một người nào trong anh em hết."},
	{9, "9 Chẳng phải chúng tôi không có quyền lợi được ăn dưng, nhưng muốn làm gương cho anh em, để anh em bắt chước."},
	{10, "10 Khi chúng tôi ở cùng anh em, cũng đã rao bảo cho anh em rằng: nếu ai không khứng làm việc, thì cũng không nên ăn nữa."},
	{11, "11 Vả, chúng tôi nghe trong anh em có kẻ ăn ở bậy bạ, chẳng hề làm lụng, trở chăm những sự vô ích thôi."},
	{12, "12 Chúng tôi nhơn danh Ðức Chúa Jêsus Christ, bảo và khuyên những kẻ đó phải yên lặng mà làm việc, hầu cho ăn bánh của mình làm ra."},
	{13, "13 Hỡi anh em, phần anh em chớ nên chán mệt làm sự lành."},
	{14, "14 Ví bằng có ai không tuân theo lời chúng tôi nói trong thơ nầy, thì hãy ghi lấy, chớ giao thông với họ, hầu cho họ nhơn đó biết xấu hổ."},
	{15, "15 Tuy vậy, chớ coi người đó như kẻ nghịch thù, nhưng hãy răn bảo người như anh em vậy."},
	{16, "16 Nguyền xin chính Chúa bình an ban sự bình an cho anh em trong mọi khi, đủ mọi cách! Lại xin Chúa ở cùng anh em hết thảy!"},
	{17, "17 Chính tay tôi, là Phao-lô, viết lời chào thăm nầy cho anh em: ấy đó là dấu ký của tôi trong mọi thơ từ của tôi, tôi viết như vậy."},
	{18, "18 Nguyền xin ân điển của Ðức Chúa Jêsus Christ chúng ta ở cùng anh em hết thảy!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};