#include <map>
#include <string>
class Bible55
{
	struct vi1	{ int val; const char *msg; };
	struct vi2	{ int val; const char *msg; };
	struct vi3	{ int val; const char *msg; };
	struct vi4	{ int val; const char *msg; };
public:
static void view1() {
struct vi1 poems[] = {
	{1, "1 Ta, Phao-lô, theo ý muốn Ðức Chúa Trời, làm sứ đồ của Ðức Chúa Jêsus Christ, đặng rao truyền lời hứa của sự sống trong Ðức Chúa Jêsus Christ,"},
	{2, "2 gởi cho Ti-mô-thê, là con rất yêu dấu của ta: nguyền con được ân điển, sự thương xót, sự bình an ban cho bởi Ðức Chúa Trời, là Cha, và bởi Ðức Chúa Jêsus Christ, Chúa chúng ta!"},
	{3, "3 Ta cảm tạ Ðức Chúa Trời mà ta hầu việc bằng lương tâm thanh sạch như tổ tiên ta đã làm, cả ngày lẫn đêm ta ghi nhớ con không thôi trong khi cầu nguyện."},
	{4, "4 Vì ta nhớ đến nước mắt con, muốn đến thăm con quá chừng, để được đầy lòng vui vẻ."},
	{5, "5 Ta cũng nhớ đến đức tin thành thật của con, là đức tin trước đã ở trong Lô-ít, bà nội con, và trong Ơ-nít, mẹ con, ta chắc rằng nay cũng ở trong con nữa."},
	{6, "6 Vậy nên ta khuyên con hãy nhen lại ơn của Ðức Chúa Trời ban cho, mà con đã nhận lãnh bởi sự đặt tay của ta."},
	{7, "7 Vì Ðức Chúa Trời chẳng ban cho chúng ta tâm thần nhút nhát, bèn là tâm thần mạnh mẽ, có tình thương yêu và giè giữ."},
	{8, "8 Vậy con chớ thẹn vì phải làm chứng cho Chúa chúng ta, cũng đứng lấy sự ta vì Ngài ở tù làm xấu hổ; nhưng hãy cậy quyền phép Ðức Chúa Trời mà chịu khổ với Tin Lành."},
	{9, "9 Ấy chính Chúa đã cứu chúng ta, đã gọi chúng ta bởi sự kêu gọi thánh, chẳng phải theo việc làm chúng ta, bèn theo ý riêng Ngài chỉ định, theo ân điển đã ban cho chúng ta trong Ðức Chúa Jêsus Christ từ trước muôn đời vô cùng,"},
	{10, "10 mà bây giờ mới bày ra bởi sự hiện ra của Ðức Chúa Jêsus Christ, Cứu Chúa chúng ta, Ngài đã hủy phá sự chết, dùng Tin Lành phô bày sự sống và sự không hề chết ra cho rõ ràng."},
	{11, "11 Ấy là vì Tin Lành đó mà ta đã được lập làm người giảng đạo, sứ đồ và giáo sư,"},
	{12, "12 ấy lại là cớ mà ta chịu khổ. Nhưng ta chẳng hổ thẹn vì biết ta đã tin Ðấng nào, chắc rằng Ðấng ấy có quyền phép giữ sự ta đã phó thác cho đến ngày đó."},
	{13, "13 Hãy lấy lòng tin và yêu trong Ðức Chúa Jêsus Christ mà giữ lấy mẫu mực của các sự dạy dỗ có ích, là sự con đã nhận lãnh nơi ta."},
	{14, "14 Hãy nhờ Ðức Thánh Linh ngự trong chúng ta mà giữ lấy điều phó thác tốt lành."},
	{15, "15 Con biết rằng mọi người ở xứ A-si đã lìa bỏ ta; trong số ấy có Phy-ghen và Hẹt-mô-ghen."},
	{16, "16 Cầu xin Chúa thương xót lấy nhà Ô-nê-si-phô-rơ, vì người đòi phen yên ủi ta, chẳng hề lấy sự ta bị xiềng xích làm xấu hổ."},
	{17, "17 Lại còn, khi người vừa đến thành Rô-ma, đã vội vả kiếm ta, và kiếm được."},
	{18, "18 Xin chính mình Chúa cho người tìm thấy sự thương xót của Chúa trong ngày đó! Con lại biết hơn kẻ khác, người ở Ê-phê-sô đã hầu việc ta mọi đàng."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct vi2 poems[] = {
	{1, "1 Vậy, hỡi con, hãy cậy ân điển trong Ðức Chúa Jêsus Christ mà làm cho mình mạnh mẽ."},
	{2, "2 Những điều con đã nghe nơi ta ở trước mặt nhiều người chứng, hãy giao phó cho mấy người trung thành, cũng có tài dạy dỗ kẻ khác."},
	{3, "3 Hãy cùng ta chịu khổ như một người lính giỏi của Ðức Chúa Jêsus Christ."},
	{4, "4 Khi một người đi ra trận, thì chẳng còn lấy việc đời lụy mình, làm vậy đặng đẹp lòng kẻ chiêu mộ mình."},
	{5, "5 Cùng một lẽ đó, người đấu sức trong diễn trường chỉ đấu nhau theo lệ luật thì mới được mão triều thiên."},
	{6, "6 Người cày ruộng đã khó nhọc thì phải trước nhứt được thâu hoa lợi."},
	{7, "7 Hãy hiểu rõ điều ta nói cho con, và chính Chúa sẽ ban sự khôn ngoan cho con trong mọi việc."},
	{8, "8 Hãy nhớ rằng Ðức Chúa Jêsus Christ, sanh ra bởi dòng vua Ða-vít, đã từ kẻ chết sống lại, theo như Tin Lành của ta,"},
	{9, "9 vì Tin Lành đó mà ta chịu khổ, rất đỗi bị trói như người phạm tội; nhưng đạo của Ðức Chúa Trời không hề bị trói đâu."},
	{10, "10 Vậy nên, ta vì cớ những người được chọn mà chịu hết mọi sự, hầu cho họ cũng được sự cứu trong Ðức Chúa Jêsus Christ, với sự vinh hiển đời đời."},
	{11, "11 Lời nầy chắc chắn lắm: Ví bằng chúng ta chết với Ngài, thì cũng sẽ sống với Ngài;"},
	{12, "12 lại nếu chúng ta chịu thử thách nổi, thì sẽ cùng Ngài đồng trị; nếu chúng ta chối Ngài, thì Ngài cũng sẽ chối chúng ta;"},
	{13, "13 nếu chúng ta không thành tín, song Ngài vẫn thành tín, vì Ngài không thể tự chối mình được."},
	{14, "14 Nầy là điều con hãy nhắc lại và răn bảo trước mặt Ðức Chúa Trời rằng, phải tránh sự cãi lẫy về lời nói, sự đó thật là vô dụng, chỉ hại cho kẻ nghe mà thôi."},
	{15, "15 Hãy chuyên tâm cho được đẹp lòng Ðức Chúa Trời như người làm công không chỗ trách được, lấy lòng ngay thẳng giảng dạy lời của lẽ thật."},
	{16, "16 Nhưng phải bỏ những lời hư không phàm tục; vì những kẻ giữ điều đó càng sai lạc luôn trong đường không tin kính,"},
	{17, "17 và lời nói của họ như chùm bao ăn lan. Hy-mê-nê và Phi-lết thật như thế,"},
	{18, "18 họ xây bỏ lẽ thật; nói rằng sự sống lại đã đến rồi, mà phá đổ đức tin của một vài người cách như vậy."},
	{19, "19 Tuy vậy, nền vững bền của Ðức Chúa Trời đã đặt vẫn còn nguyên, có mấy lời như ấn đóng rằng: Chúa biết kẻ thuộc về Ngài; lại rằng: Phàm người kêu cầu danh Chúa thì phải tránh khỏi sự gian ác."},
	{20, "20 Trong một nhà lớn, không những có bình vàng bình bạc mà thôi, cũng có cái bằng gỗ bằng đất nữa, thứ thì dùng việc sang, thứ thì dùng việc hèn."},
	{21, "21 Vậy, ai giữ mình cho khỏi những điều ô uế đó, thì sẽ như cái bình quí trọng, làm của thánh, có ích cho chủ mình và sẵn sàng cho mọi việc lành."},
	{22, "22 Cũng hãy tránh khỏi tình dục trai trẻ, mà tìm những điều công bình, đức tin, yêu thương, hòa thuận với kẻ lấy lòng tinh sạch kêu cầu Chúa."},
	{23, "23 Hãy cự những lời bàn luận điên dại và trái lẽ, vì biết rằng chỉ sanh ra điều tranh cạnh mà thôi."},
	{24, "24 Vả, tôi tớ của Chúa không nên ưa sự tranh cạnh; nhưng phải ở tử tế với mọi người, có tài dạy dỗ, nhịn nhục,"},
	{25, "25 dùng cách mềm mại mà sửa dạy những kẻ chống trả, mong rằng Ðức Chúa Trời ban cho họ sự ăn năn để nhìn biết lẽ thật,"},
	{26, "26 và họ tỉnh ngộ mà gỡ mình khỏi lưới ma quỉ, vì đã bị ma quỉ bắt lấy đặng làm theo ý nó."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct vi3 poems[] = {
	{1, "1 Hãy biết rằng trong ngày sau rốt, sẽ có những thời kỳ khó khăn."},
	{2, "2 Vì người ta đều tư kỷ, tham tiền, khoe khoang, xấc xược, hay nói xấu, nghịch cha mẹ, bó buộc, không tin kính,"},
	{3, "3 vô tình, khó hòa thuận, hay phao vu, không tiết độ, dữ tợn, thù người lành,"},
	{4, "4 lường thầy phản bạn, hay nóng giận, lên mình kiêu ngạo, ưa thích sự vui chơi hơn là yêu mến Ðức Chúa Trời,"},
	{5, "5 bề ngoài giữ điều nhơn đức, nhưng chối bỏ quyền phép của nhơn đức đó. Những kẻ thể ấy, con hãy lánh xa đi."},
	{6, "6 Trong bọn họ có kẻ lẻn vào các nhà, quyến dụ lòng những người đờn bà mang tội lỗi, bị bao nhiêu tình dục xui khiến,"},
	{7, "7 vẫn học luôn mà không hề thông biết lẽ thật được."},
	{8, "8 Xưa kia Gian-nét với Giam-be chống trả Môi-se thể nào, thì những kẻ nầy cũng chống trả lẽ thật thể ấy: lòng họ hoại bại, đức tin họ không thể chịu thử thách được."},
	{9, "9 Nhưng họ không làm thêm được nữa, vì sự điên dại của họ sẽ bày tỏ ra cho thiên hạ, cũng như của hai người kia."},
	{10, "10 Về phần con, con đã noi theo ta trong sự dạy dỗ, tánh hạnh, ý muốn, đức tin, nhịn nhục, yêu thương, bền đỗ của ta,"},
	{11, "11 trong những sự bắt bớ, và hoạn nạn đã xảy đến cho ta tại thành An-ti-ốt, Y-cô-ni và Lút-trơ. Những sự bắt bớ đó ta đều chịu cả, và Chúa đã cứu ta thoát khỏi luôn luôn."},
	{12, "12 Vả lại, hết thảy mọi người muốn sống cách nhân đức trong Ðức Chúa Jêsus Christ, thì sẽ bị bắt bớ."},
	{13, "13 Nhưng những người hung ác, kẻ giả mạo thì càng chìm đắm luôn trong điều dữ, làm lầm lạc kẻ khác mà cũng lầm lạc chính mình nữa."},
	{14, "14 Về phần con, hãy đứng vững trong những sự con đã đem lòng tin chắc mà học và nhận lấy, vì biết con đã học những điều đó với ai,"},
	{15, "15 và từ khi con còn thơ ấu đã biết Kinh Thánh vốn có thể khiến con khôn ngoan để được cứu bởi đức tin trong Ðức Chúa Jêsus Christ."},
	{16, "16 Cả Kinh Thánh đều là bởi Ðức Chúa Trời soi dẫn, có ích cho sự dạy dỗ, bẻ trách, sửa trị, dạy người trong sự công bình,"},
	{17, "17 hầu cho người thuộc về Ðức Chúa Trời được trọn vẹn và sắm sẵn để làm mọi việc lành."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct vi4 poems[] = {
	{1, "1 Ta ở trước mặt Ðức Chúa Trời và trước mặt Ðức Chúa Jêsus Christ là Ðấng sẽ đoán xét kẻ sống và kẻ chết, nhơn sự đến của Ngài và nước Ngài mà răn bảo con rằng:"},
	{2, "2 hãy giảng đạo, cố khuyên, bất luận gặp thời hay không gặp thời, hãy đem lòng rất nhịn nhục mà bẻ trách, nài khuyên, sửa trị, cứ dạy dỗ chẳng thôi."},
	{3, "3 Vì sẽ có một thời kia, người ta không chịu nghe đạo lành; những vì họ ham nghe những lời êm tai, theo tư dục mà nhóm họp các giáo sư xung quanh mình,"},
	{4, "4 bịt tai không nghe lẽ thật, mà xây hướng về chuyện huyễn."},
	{5, "5 Nhưng con, phải có tiết độ trong mọi sự, hãy chịu cực khổ, làm việc của người giảng Tin Lành, mọi phận sự về chức vụ con phải làm cho đầy đủ."},
	{6, "6 Về phần ta, ta đang bị đổ ra làm lễ quán, kỳ qua đời của ta gần rồi."},
	{7, "7 Ta đã đánh trận tốt lành, đã xong sự chạy, đã giữ được đức tin."},
	{8, "8 Hiện nay mão triều thiên của sự công bình đã để dành cho ta; Chúa là quan án công bình, sẽ ban mão ấy cho ta trong ngày đó, không những cho ta mà thôi, những cũng cho mọi kẻ yêu mến sự hiện đến của Ngài."},
	{9, "9 Hãy cố gắng đến cùng ta cho kíp;"},
	{10, "10 vì Ðê-ma đã lìa bỏ ta rồi, tại người ham hố đời nầy, và đã đi qua thành Tê-sa-lô-ni-ca. Cơ-rết-xen đi trong xứ Ga-li-lê, còn Tít thì đi xứ Ða-ma-ti rồi."},
	{11, "11 Chỉ có một mình Lu-ca ở với ta. Hãy đem Mác đến với con, vì người thật có ích cho ta về sự hầu việc lắm."},
	{12, "12 Ta đã sai Ti-chi-cơ sang thành Ê-phê-sô."},
	{13, "13 Khi con sẽ đến, hãy đem áo choàng mà ta để lại tại nhà Ca-bút, thành Trô-ách, cùng những sách vở nữa, nhứt là những sách bằng giấy da."},
	{14, "14 A-léc-xan-đơ; thợ đồng, đã làm hại ta nhiều lắm; tùy theo công việc hắn, Chúa sẽ báo ứng."},
	{15, "15 Con cũng phải coi chừng người đó, vì hắn hết sức chống trả lời chúng ta."},
	{16, "16 Khi ta binh vực mình lần thứ nhứt, chẳng có ai giúp đỡ; hết thảy đều lìa bỏ ta. Nguyền xin điều đó đừng đổ tội về họ!"},
	{17, "17 Nhưng Chúa đã giúp đỡ ta và thêm sức cho ta, hầu cho Tin Lành bởi ta được rao truyền đầy dẫy, để hết thảy dân ngoại đều nghe; ta lại đã được cứu khỏi hàm sư tử."},
	{18, "18 Chúa sẽ giải thoát ta khỏi mọi điều ác và cứu vớt ta vào trong nước trên trời của Ngài. Nguyền xin sự vinh hiển về nơi Ngài muôn đời không cùng! A-men."},
	{19, "19 Hãy chào thăm Bơ-rít-ca và A-qui-la, cùng người nhà Ô-nê-si-phô-rơ."},
	{20, "20 Ê-rát ở lại tại thành Cô-rinh-tô, còn Trô-phim đương đau ốm, ta để ở lại tại thành Mi-lê."},
	{21, "21 Con hãy cố sức đến trước mùa đông. Ơ-bu-lu, Bu-đen, Li-nút, Cơ-lau-đia cùng anh em thảy đều chào thăm con."},
	{22, "22 Nguyền xin Chúa ở cùng tâm thần con! Cầu xin ân điển ở cùng các anh em!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};